INSERT INTO `workout` (`ID`, `exercise_name`, `target`, `youtubeURL`, `Grips`, `Force`, `steps`, `Category`, `Difficulty`) VALUES
(0, 'Barbell Curl', ' Biceps', 'https://www.youtube.com/embed/PGW-SgbEfGU', 'Underhand', 'Pull', 'While holding the upper arms stationary, curl the weights forward while contracting the biceps as you breathe out., Continue the movement until your biceps are fully contracted and the bar is at shoulder level., Hold the contracted position for a second and squeeze the biceps hard., Slowly bring the weight back down to the starting position.', 'Barbell', 'Beginner'),
(1, 'Dumbbell Curl', 'Primary Biceps', 'https://www.youtube.com/embed/P1FqV-oFn5M', 'Underhand', 'Pull', 'Stand up straight with a dumbbell in each hand at arms length, Raise one dumbbell and twist your forearm until it is vertical and your palm faces the shoulder., Lower to original position and repeat with opposite arm', 'Dumbbells', 'Beginner'),
(2, 'Dumbbell Hammer Curl', 'Primary Biceps', 'https://www.youtube.com/embed/tvRlfjjH5NA', 'Neutral', 'Pull', 'Hold the dumbbells with a neutral grip (thumbs facing the ceiling)., Slowly lift the dumbbell up to chest height, Return to starting position and repeat.', 'Dumbbells', 'Beginner'),
(3, 'Kettlebell Goblet Curl', 'Primary Biceps', '', '', '', 'Stand up straight with a kettlebell in both hands in front of your pelvis., Raise the kettlebell bending your arms at the elbow until the kettlebell is level with your chest., Lower to the starting position and repeat.', 'Kettlebells', 'Beginner'),
(4, 'Kettlebell Concentration Curl', 'Primary Biceps', '', 'Underhand', 'Pull', 'Sitting on a chair with your legs apart, rest your arm against your thigh and hold the kettlebell with your arm extended towards the floor., Bending your arm at the elbow, lift the kettlebell until your palm faces your shoulder., Lower to the starting position and repeat.', 'Kettlebells', 'Intermediate'),
(5, 'Kettlebell Preacher Curl', 'Primary Biceps', '', 'Underhand', 'Pull', 'Stand behind a bench, with your chest leaning over the edge of the bench., With one arm resting on the bench, hold the kettlebell, bending the arm at the elbow., Lower your arm using the bench as a guide, keeping your forearm straight., Return to the starting position and repeat.', 'Kettlebells', 'Intermediate'),
(6, 'Kettlebell Single Arm Curl', 'Primary Biceps', '', '', '', 'Stand up straight with a kettlebell in one hand with your forearm facing out., Raise the kettlebell, bending your arm at the elbow and keeping your forearm vertical until your palm faces the shoulder., Lower to the starting position and repeat.', 'Kettlebells', 'Beginner'),
(7, 'Biceps Stretch Variation Five', 'Primary Biceps', '', 'None', '', 'Stand upright with your feet shoulder width apart., Raise your arms out to your sides, bringing them up to shoulder level., Rotate your palms so they are facing the ceiling., Then, change direction and rotate the arms as much as you can.', 'Stretches', 'Beginner'),
(8, 'Biceps Stretch Variation Four', 'Primary Biceps', '', 'None', '', 'Stand an arms width away from the wall., Raise your arms out behind you, and place them on the wall with your fingers pointing up., Bend your knees whilst keeping your hands in the same position.', 'Stretches', 'Beginner'),
(9, 'Biceps Stretch Variation Three', 'Primary Biceps', '', 'None', '', 'Bend your arm at the elbow, raising your hand to your shoulder., Using your other hand, stretch the arm down to its full extent.', 'Stretches', 'Beginner'),
(10, 'Biceps Stretch Variation Two', 'Primary Biceps', '', 'None', '', 'Stand facing the wall, an arms width away, Place your hand on the wall with your fingers pointing down., Lean slightly into the wall to feel the stretch in your bicep.', 'Stretches', 'Beginner'),
(11, 'Biceps Stretch Variation One', 'Primary Biceps', '', 'None', '', 'Stand one foot in front of the other with the wall to your right, an arms width away., Place your hand on the wall, fingers pointing away from you., Gently lean forward, keeping your hand stationary., Repeat with the other arm.', 'Stretches', 'Beginner'),
(12, 'Cable Twisting Curl', 'Primary Biceps', '', '', '', 'Use a handle attachment. The cable should be set all the way to the bottom of the machine., Face away from the cable machine. Stagger your stance so you have a better base of support., Start with a neutral grip. Flex at the elbow. While flexing twist your palm. When you hit the top of the movement your palm should be facing upward.', 'Cables', 'Advanced'),
(13, 'Cable Bayesian Curl', 'Primary Biceps', '', 'Underhand', 'Pull', 'Use a handle attachment. The cable should be set all the way to the bottom of the machine., Face away from the cable machine., Stagger your stance so you have a better base of support. Face your palm forward. Flex at the elbow and extend.', 'Cables', 'Beginner'),
(14, 'Cable Reverse Curl', 'Primary Biceps Secondary Forearms', '', '', '', 'Use a handle attachment. The cable should be set all the way to the bottom of the machine., Face away from the cable machine. Stagger your stance so you have a better base of support., Face your palm downwards. Flex at the elbow and extend.', 'Cables', 'Intermediate'),
(15, 'Cable Hammer Curl', 'Primary Biceps', 'https://www.youtube.com/embed/tvRlfjjH5NA', 'Neutral', 'Pull', 'Use a handle attachment. The cable should be set all the way to the bottom of the machine., Face away from the cable machine. Stagger your stance so you have a better base of support., Use a neutral grip thumb facing up pinky-facing down). Flex at the elbow and extend.', 'Cables', 'Beginner'),
(16, 'Band Bayesian Curl', 'Primary Biceps', '', '', '', 'Attach band to a low anchor point. Point your palms straight forward., Flex your elbow until you feel your biceps contract. Then fully extend elbows.', 'Band', 'Beginner'),
(17, 'Band Bayesian Hammer Curl', 'Primary Biceps Forearms', 'https://www.youtube.com/embed/tvRlfjjH5NA', '', '', 'Attach band to a low anchor point. Use a neutral grip., Flex your elbow until you feel your biceps contract. Then fully extend elbows.', 'Band', 'Intermediate'),
(18, 'Band Bayesian Reverse Curl', 'Primary Biceps Secondary Forearms', '', '', '', 'Attach band to a low anchor point. . Point your palms toward your thighs., Flex your elbow until you feel your biceps contract. Then fully extend elbows.', 'Band', 'Intermediate'),
(19, 'Dumbbell Reverse Curl', 'Primary Biceps Secondary Forearms', '', 'Overhand', 'Pull', 'Grab the dumbbells with a pronated (overhand) grip. You can do this exercise thumbless if its more comfortable on your wrists., Flex at the elbows until your biceps touch your forearms. Try not to let your elbows flair outward.', 'Dumbbells', 'Intermediate'),
(20, 'Barbell Reverse Curl', 'Primary Biceps Secondary Forearms', '', 'Overhand', 'Pull', 'Take a double overhand grip thats about shoulder width. Flex your elbows while keeping your elbows tucked in. Try not to let them flare out.\"', 'Barbell', 'Advanced'),
(21, 'Dumbbell Incline Hammer Curl', 'Primary Biceps', 'https://www.youtube.com/embed/tvRlfjjH5NA', 'Neutral', 'Pull', 'Hold the dumbbells with a neutral grip (thumbs facing the ceiling). Seated on an incline bench., Slowly lift the dumbbell up to chest height, Return to starting position and repeat.', 'Dumbbells', 'Beginner'),
(22, 'Dumbbell Incline Reverse Curl', 'Primary Biceps Secondary Forearms', '', 'Overhand', 'Pull', 'Lay on an incline bench., \"Grab the dumbbells with a pronated (overhand) grip. You can do this exercise thumbless if its more comfortable on your wrists.\"\"', 'Dumbbells', 'Beginner'),
(23, 'Dumbbell Incline Zottman Curl', 'Primary Biceps Secondary Forearms', '', 'Rotating', 'Pull', 'Lay on an incline bench with your palms facing forward., Flex at the elbows until your forearms touch your biceps., When you hit the top of the movement, rotate your palms and then lower the dumbbells slowly.', 'Dumbbells', 'Intermediate'),
(24, 'Dumbbell Single Arm Preacher Curl', 'Primary Biceps', '', 'Underhand', 'Pull', 'Start with a with a dumbbell in one hand and your elbow resting on the bench., From this starting position, curl the dumbbell up towards your shoulder, keeping your upper arm stationary on the bench.', 'Dumbbells', 'Beginner'),
(25, 'Dumbbell Single Arm Spider Curl', 'Primary Biceps', '', 'Underhand', 'Pull', 'Lay on an incline or preacher curl bench., Let your arms hang freely off the edge of the bench., Fully flex your elbow and fully extend your elbow on each rep.', 'Dumbbells', 'Beginner'),
(26, 'Dumbbell Spider Curl', 'Primary Biceps', '', 'Underhand', 'Pull', 'Start by placing your forearms on a flat bench and holding a dumbbell in each hand., From this starting position, curl the dumbbells up towards your shoulders, keeping your elbows close to your body and your core engaged., Slowly lower the dumbbells back down to the starting position, and repeat for the desired number of repetitions.', 'Dumbbells', 'Beginner'),
(27, 'Dumbbell Twisting Curl', 'Primary Biceps', '', 'Rotating', 'Pull', 'Start by standing with your feet shoulder-width apart and holding a dumbbell in each hand., Bring the dumbbells to your sides, with your palms facing inward and your elbows bent., From this starting position, curl the dumbbells up towards your shoulders while rotating your wrists so that your palms face your shoulders at the top of the movement.', 'Dumbbells', 'Beginner'),
(28, 'Plate Bicep Curl', 'Primary Biceps', '', 'Neutral', 'Pull', '', 'Plate', 'Beginner'),
(29, 'Plate Curl To Overhead Press', 'Primary Biceps Secondary Shoulders', '', 'Neutral', 'Push', 'Stand with feet shoulder-width apart and the plate in front of the torso, palms facing forward., Curl the plate up to the front of the shoulder while keeping the elbows close to the body., Rotate the palms to face forward and press the plate overhead, locking out the arms., Lower the plate back to the starting position, keeping control of it, and repeat for desired reps.', 'Plate', 'Intermediate'),
(30, 'Plate Curl To Overhead Press To Tricep Extension', 'Primary Biceps Triceps Secondary Shoulders', '', 'Neutral', '', 'Stand with feet shoulder-width apart and the plate in front of the torso, palms facing forward., Curl the plate up to the front of the shoulder while keeping the elbows close to the body., Rotate the palms to face forward and press the plate overhead, locking out the arms., Lower the plate back behind the head to the starting position of a tricep extension., Push the plate back up overhead, then lower it back to the starting position and repeat for desired reps.', 'Plate', 'Advanced'),
(31, 'Plate Squat Hold Curl', 'Primary Biceps Secondary Quads Glutes', '', '', '', 'Lower into a squat position, keeping the plate at chest height., Slowly curl the plate towards your shoulders, keeping your elbows close to your body., Hold for a moment at the top of the curl before lowering the plate back to chest height., Repeat the curls as you hold the squat position.', 'Plate', 'Intermediate'),
(32, 'Band High Curl', 'Primary Biceps', '', 'Underhand', 'Pull', 'Anchor a band to a high point., Start with your feet staggered and your elbows high., Flex your elbows while keeping your arms high. Curl the band to your forehead.', 'Band', 'Beginner'),
(33, 'Band High Hammer Curl', 'Primary Biceps', 'https://www.youtube.com/embed/tvRlfjjH5NA', 'Neutral', 'Pull', 'Start with the band anchored to something high with your feet staggered and a neutral grip., Keep your elbows high. Curl until your fists are at your forehead.', 'Band', 'Beginner'),
(34, 'Band High Reverse Curl', 'Primary Biceps Secondary Forearms', '', 'Overhand', 'Pull', 'Start with a band attached to a high point, your feet staggered, and an overhand grip., Flex your elbows pulling your forearms to your biceps. Your fists should land by your forehead.', 'Band', 'Intermediate'),
(35, 'Band Curl', 'Primary Biceps', '', 'Underhand', 'Pull', 'Stand with your feet shoulder-width apart, with the band under both feet., Grasp the handles of the band with both hands, keeping your arms straight and extended in front of you., Keep your back straight, abs engaged, and shoulders relaxed as you curl your hands towards your shoulders, bending at the elbows.', 'Band', 'Beginner'),
(36, 'Band Reverse Curl', 'Primary Biceps Secondary Forearms', '', 'Overhand', 'Pull', 'Start by standing on the center of the band with both feet, shoulder-width apart., Hold the band with both hands, palms facing down, arms extended straight in front of you., Slowly curl your hands towards your shoulders, pulling the band tight, then lower your arms back to the starting position, keeping tension on the band throughout the exercise.', 'Band', 'Intermediate'),
(37, 'Dumbbell Concentration Curl', 'Primary Biceps', '', 'Underhand', 'Pull', 'Sit on a flat bench with your feet flat on the floor and your back straight., Lean forward and place your elbow on the inside of your leg., Curl the dumbbell while keeping your elbow against your leg. Make sure to fully extend your elbow on each rep.', 'Dumbbells', 'Beginner'),
(38, 'TRX Curl', 'Primary Biceps', '', 'Underhand', 'Pull', 'Hold the TRX straps with an underhand grip and lean back and away from the anchor point., Keep your elbows high as you flex your elbows. Pull your fists toward your forehead.', 'TRX', 'Beginner'),
(39, 'TRX Hammer Curl', 'Primary Biceps', 'https://www.youtube.com/embed/tvRlfjjH5NA', '', 'Pull', 'Grab the TRX straps and lean away from the anchor point., Maintain a neutral grip. Flex your elbows and pull your fists to either sides of your temples.', 'TRX', 'Beginner'),
(40, 'TRX Reverse Curl', 'Primary Biceps Secondary Forearms', '', 'Overhand', 'Pull', 'Stand facing the TRX straps and grasp the handles with both hands, palms facing away from your body., Walk back until the straps are taut, keeping your arms straight., Slowly curl your hands towards your shoulders, keeping your elbows close to your body.', 'TRX', 'Intermediate'),
(41, 'Barbell Landmine Row', 'Primary Lats Mid back Secondary Biceps Tertiary Forearms', '', 'Overhand', 'Pull', 'Position a barbell in a landmine or corner of a room and stand facing it with your feet shoulder-width apart., Grasp the end of the barbell with a neutral grip, keeping your arms straight and your shoulders relaxed., Bend at the hips and knees to lower your torso until it is almost parallel to the ground., Pull the barbell towards your chest, squeezing your shoulder blades together as you row the weight up.', 'Barbell', 'Intermediate'),
(42, 'Chin Ups', 'Primary Lats Secondary Biceps Tertiary Forearms', 'https://www.youtube.com/embed/y6x0AIhgjsM', 'Underhand', 'Pull', 'Grab the bar shoulder width apart with a supinated grip (palms facing you), With your body hanging and arms fully extended, pull yourself up until your chin is past the bar., Slowly return to starting position. Repeat.', 'Bodyweight', 'Intermediate'),
(43, 'Band Kneeling Single Arm Pulldown', 'Primary Lats Secondary Biceps', '', '', '', 'Place the band at about eye level. Fall into a kneeling position., The knee that is on the ground should be the same side as the working arm. Stick your chest out., And then pull your elbow straight back. Flex your lats and then extend into the starting position.', 'Band', 'Beginner'),
(44, 'Barbell Long Landmine Row', 'Primary Lats Secondary Biceps Tertiary Forearms', '', 'Neutral', 'Pull', 'Position a barbell in a landmine or corner of a room and stand facing it with your feet shoulder-width apart., Grasp the end of the barbell with an overhand grip, keeping your arms straight and your shoulders relaxed., Bend at the hips and knees to lower your torso until it is almost parallel to the ground., Pull the barbell towards your chest, squeezing your shoulder blades together as you row the weight up.', 'Barbell', 'Advanced'),
(45, 'Barbell Meadows Row', 'Primary Lats Secondary Biceps Tertiary Forearms', '', 'Overhand', 'Pull', 'Position a barbell in a landmine or corner of a room and stand facing sideways with a staggered stance and your feet shoulder-width apart., Grasp the end of the barbell with an overhand grip, keeping your arms straight and your shoulders relaxed., Bend at the hips and knees to lower your torso until it is almost parallel to the ground., Pull the barbell towards your chest, squeezing your shoulder blades together as you row the weight up., *Named after the late John Meadows', 'Barbell', 'Intermediate'),
(46, 'Barbell Pronated Pendlay Row', 'Primary Lats Secondary Biceps Tertiary Lower back', '', 'Overhand', 'Pull', 'Start hinged forward with a flat back and the plates resting on the floor. Make sure your elbows are fully extended., Pull the bar to your sternum., Return the plates back to the floor and let the bar come to a full dead stop., Then initiate the next rep.', 'Barbell', 'Advanced'),
(47, 'Barbell Pronated Row', 'Primary Lats Secondary Biceps Tertiary Forearms', '', 'Overhand', 'Pull', 'Grab the barbell with an overhand grip. Hinge forward while keeping your back flat until your torso is about parallel with the ground., Let your arms hang freely., Pull the bar to your torso while tucking your elbows in slightly., Extend your elbows fully before initiating the next rep.', 'Barbell', 'Beginner'),
(48, 'Barbell Supinated Pendlay Row', 'Primary Lats Secondary Biceps Tertiary Lower back', '', 'Underhand', 'Pull', 'Begin by standing in front of a barbell with your feet about hip-width apart and your palms facing towards your body., Bend at the hips and knees to grab the barbell with a pronated (overhand) grip, positioning the barbell about an inch away from your shins., Keeping your back straight and your core engaged, use your back muscles to lift the barbell straight up towards your chest., As the barbell reaches your chest, rotate your wrists so that your palms are facing towards your body (supinated grip), and then lower the barbell back to the starting position.', 'Barbell', 'Advanced'),
(49, 'Barbell Supinated Row', 'Primary Lats Lower back Secondary Biceps', '', 'Underhand', 'Pull', 'Begin by standing in front of a barbell with your feet about hip-width apart., Bend at the hips and knees to grab the barbell with a supinated (underhand) grip, positioning the barbell about an inch away from your shins., Keeping your back straight and your core engaged, use your back muscles to lift the barbell straight up towards your chest., As the barbell reaches your chest, pause briefly, and then lower the barbell back to the starting position.', 'Barbell', 'Advanced'),
(50, 'Dumbbell Alternating Pendlay Row', 'Primary Lats Secondary Biceps', '', 'Neutral', 'Pull', 'The dumbbells should be set on the ground. Hinge forward while keeping a flat back., Pull one elbow back toward the ceiling and then return the dumbbell to the ground softly., Then pull with the other arm.', 'Dumbbells', 'Advanced'),
(51, 'Dumbbell Kneeling Single Arm Row', 'Primary Lats Secondary Biceps', '', 'Neutral', 'Pull', 'Place one hand and one knee on a bench. Make sure you keep a flat back., Pull the dumbbell toward the ceiling., Make sure to avoid any jerking or rotating.', 'Dumbbells', 'Beginner'),
(52, 'Dumbbell Laying Incline Row', 'Primary Lats Secondary Biceps', '', 'Neutral', 'Pull', 'Lay on an incline bench that is between 30-45 degrees high., Let your arms hang freely. Pull your elbows straight back toward the ceiling.', 'Dumbbells', 'Beginner'),
(53, 'Dumbbell Pendlay Row', 'Primary Lats Secondary Biceps', '', 'Neutral', 'Pull', 'The dumbbells should be placed on the floor., Hinge forward with a flat back and grab the dumbbells., Pull both dumbbells back and your elbows toward the ceiling., Return the dumbbells back to the floor softly on each rep.', 'Dumbbells', 'Intermediate'),
(54, 'Dumbbell Single Arm Row', 'Primary Lats Secondary Biceps', '', 'Neutral', 'Pull', 'Make sure to keep your elbow close to your body and your core engaged throughout the movement., Start by standing in front of a flat bench with your feet shoulder-width apart and holding a dumbbell in one hand., Place your other hand on the bench for support, and bend at the waist so that your chest is parallel to the ground., From this starting position, row the dumbbell up towards your chest, squeezing your shoulder blade together as you do so.', 'Dumbbells', 'Beginner'),
(55, 'Dumbbell Single Arm Row Knee', 'Primary Lats Secondary Biceps', '', 'Neutral', 'Pull', 'Start by placing your elbow on one knee., Hold a dumbbell in your other hand, with your palm facing inwards towards your body., From this starting position, row the dumbbell up towards your chest, keeping your elbow close to your body and your core engaged.', 'Dumbbells', 'Beginner'),
(56, 'Plate Pinch Grip Row', 'Primary Forearms Secondary Lats Tertiary Biceps', '', 'Pinch Grip', 'Pull', 'Make sure to keep your elbow close to your body and your core engaged throughout the movement., Bend forward at your hips while maintaining a flat back., Pull the plate toward your upper abdomen., Lower the weight in a controlled manner and repeat.', 'Plate', 'Intermediate'),
(57, 'Lawnmower Row', 'Primary Lats Secondary Biceps', '', 'Rotating', 'Pull', 'Stand with feet staggered and one hand on a bench/box. Hold a dumbbell in the opposing hand., Hinge forward at the hips, keeping the back flat, and eyes facing forward. The weight should hang directly below the shoulders., Start with an overhand grip, as you pull rotate the dumbbell. When your upper arm reaches your side your palm should be facing forward.', 'Dumbbells', 'Intermediate'),
(58, 'Dumbbell Knee Lawnmower Row', 'Primary Lats Secondary Biceps', '', 'Rotating', 'Pull', 'Stand with feet staggered and one hand on your knee. Hold a dumbbell in the opposing hand., Hinge forward at the hips, keeping the back flat, and eyes facing forward. The weight should hang directly below the shoulders., Start with an overhand grip, as you pull rotate the dumbbell. When your upper arm reaches your side your palm should be facing forward.', 'Dumbbells', 'Intermediate'),
(59, 'Dumbbell Supinated Row', 'Primary Lats Secondary Biceps', '', 'Underhand', 'Pull', 'Grab both dumbbells and hinge forward at the hips. Make sure you keep a flat back., \"The closer your torso is to parallel with the ground the longer the range of motion will be at your shoulder. The better the results youll get from the exercise.\"\"', 'Dumbbells', 'Beginner'),
(60, 'TRX Rotating Row', 'Primary Lats Secondary Biceps', '', 'Rotating', 'Pull', 'Grab the TRX straps and lean away from the anchor point until the strap is taut., Start with an overhand grip. Pull your elbows back and rotate your grip., By the time your elbows reach your side, your palms should be facing upwards.', 'TRX', 'Beginner'),
(61, 'Corpe Pose', 'Primary Biceps', '', '', 'Hold', 'Lie down on your back with your legs straight and arms at your sides., Close your eyes and take deep breaths in through your nose and out through your mouth., Allow your body to completely relax and let go of any tension., Hold for several minutes and then slowly come back up to a seated position.', 'Yoga', 'Beginner'),
(62, 'Crow Pose', 'Primary Biceps Secondary Shoulders', '', '', 'Hold', 'Begin in a squatting position with your feet together and your knees apart., Place your hands on the floor in front of you, shoulder-width apart. Keep your arms straight and engage your core., Slowly shift your weight forward and lift your feet off the ground, coming onto your tiptoes., Bend your elbows and lean your weight onto your hands, shifting your knees towards your upper arms., Gently lift one foot off the ground, and then the other, balancing on your hands. Hold the pose for a few breaths before releasing back to the ground.', 'Yoga', 'Advanced'),
(63, 'Downward Dog', 'Primary Biceps Secondary Hamstrings', '', '', 'Hold', 'Begin on your hands and knees with your hands shoulder-width apart and your knees hip-width apart., Spread your fingers wide and press down into your palms, making sure that your fingers are evenly spaced and facing forward., Tuck your toes under and lift your knees off the ground, straightening your legs as much as possible., Press your hips up and back, bringing your body into an inverted \"V\"\" shape.', 'Yoga', 'Beginner'),
(64, 'Downward Dog With Fingers Facing Feet', 'Primary Biceps Secondary Forearms Tertiary Hamstrings', '', '', 'Hold', 'Begin in the same starting position as the previous exercise, with your hands and knees on the ground., Spread your fingers wide and turn your hands so that your fingers are facing towards your feet., Tuck your toes under and lift your knees off the ground, straightening your legs as much as possible., Press your hips up and back, bringing your body into an inverted \"V\"\" shape.', 'Yoga', 'Beginner'),
(65, 'Scorpion Twist Pose', 'Primary Biceps Secondary Chest Tertiary Lower back', '', '', 'Hold', 'Begin in a downward-facing dog pose., Slowly lift one leg up towards the ceiling, keeping your hips level and your toes pointed., Bend your lifted leg and try to touch your toes to the back of your head., Once you have a firm grip with your toes, slowly begin to twist your torso towards the lifted leg., Keep your hands and feet firmly planted on the ground as you hold the pose for a few breaths. Release the pose and repeat on the other side.', 'Yoga', 'Beginner'),
(66, 'Seated Arm Stretch', 'Primary Biceps Secondary Forearms', '', '', 'Hold', 'Sit cross-legged on the floor., Extend your arms straight out in front of you, palms facing forward., Cross your right arm over your left arm and interlace your fingers., Pull your hands away from your body and hold for a few breaths, then release and repeat with the opposite arm on top.', 'Yoga', 'Beginner'),
(67, 'Barbell Bench Press', 'Primary Chest Secondary Triceps Tertiary Shoulders', 'https://www.youtube.com/embed/YwK4sbSrtso', 'Overhand', 'Push', 'Lay flat on the bench with your feet on the ground. With straight arms unrack the bar., Lower the bar to your mid chest, \"Raise the bar until youve locked your elbows.\"\"\"', 'Barbell', 'Intermediate'),
(68, 'Dips', 'Primary Chest Secondary Triceps Tertiary Shoulders', '', '', '', 'Hold your body with arms locked above the equipment, Lower your body slowly while leaning forward, flare out your elbows, Raise your body above the bars until your arms are locked.', 'Bodyweight', 'Intermediate'),
(69, 'Dumbbell Incline Bench Press', 'Primary Chest', 'https://www.youtube.com/embed/bH_T3ibPPzg', 'Overhand', 'Push', 'Raise the bench to a 30 - 45 degree angle, Lay on the bench and set your feet on the ground., Raise the dumbbells with straight arms then slowly lower them to about shoulder width., Raise them again until your arms are locked and at the starting position again.', 'Dumbbells', 'Beginner'),
(70, 'Dumbbell Incline Chest Flys', 'Primary Chest', '', 'Overhand', 'Push', 'Lay flat on the bench and place your feet on the ground., Begin the exercise with the dumbbells held together above your chest, elbows slightly bent., Simultaneously lower the weights to either side., Pause when the weights are parallel to the bench, then raise your arms to the starting position.', 'Dumbbells', 'Beginner'),
(71, 'Dumbbell Bench Press', 'Primary Chest', '', '', '', 'Lay flat on the bench with your feet on the ground. Raise the dumbbells until you have straight arms., Lower the dumbbells to your mid chest, \"Raise the dumbbells until youve locked your elbows.\"\"\"', 'Dumbbells', 'Beginner'),
(72, 'Barbell Incline Bench Press', 'Primary Chest Secondary Triceps Shoulders', '', 'Overhand', 'Push', 'Position the bench between 30 and 45 degrees., Lay flat on the bench with your feet on the ground. With straight arms unrack the bar., Lower the bar to your mid chest, \"Raise the bar (slowly and controlled) until youve locked your elbows.\"\"\"', 'Barbell', 'Intermediate'),
(73, 'Kettlebell Chest Press', 'Primary Chest', '', '', '', 'Laying on the floor with your knees bent and feet firmly on the floor, hold the kettlebell at your chest with both hands., Fully extend your arms, raising the kettlebell until your elbows are locked., Lower the kettlebell to the starting position and repeat.', 'Kettlebells', 'Beginner'),
(74, 'Kettlebell Single Arm Chest Fly', 'Primary Chest', '', '', '', 'Lie on the floor with your knees bent and feet firmly on the floor, with your arms to the side of your body., Holding a kettlebell in one hand, keeping your arm fully extended with a slight bend in the elbow and your forearms facing upwards, lift the kettlebell above your chest., Lower the kettlebell to the starting position and repeat.', 'Kettlebells', 'Beginner'),
(75, 'Kettlebell Single Arm Press', 'Primary Chest', '', '', '', 'Laying on the floor with your knees bent and feet firmly on the floor, leave one arm resting to the side of the body., Using the other arm, hold the kettlebell at arms length directly upwards of your shoulder., Lower your arm until your upper arm to elbow is resting on the floor., Return to starting position.', 'Kettlebells', 'Beginner'),
(76, 'Kettlebell Walkover Pushup', 'Primary Chest', '', '', '', 'Starting in a press up position with feet wide and one hand resting on the kettlebell, complete a full press up, lowering your chest towards the ground while keeping your body straight and returning to the starting position., Swap hands on the kettlebell, placing the hand originally on the kettlebell underneath your shoulder, complete another press up., Repeat.', 'Kettlebells', 'Intermediate'),
(77, 'Chest Stretch Variation Four', 'Primary Chest', '', 'None', '', 'Raise your arms to shoulder height, fully extended in front of you., Slowly bring your arms behind your back still at shoulder height., Pause for a few seconds and then return to starting position.', 'Stretches', 'Beginner'),
(78, 'Chest Stretch Variation Three', 'Primary Chest', '', 'None', '', 'Place your arms behind your back and clasp your hands together., Slowly extend your elbows until they are locked then lift them away from you., Pause in this position for a few seconds and then return them to starting position.', 'Stretches', 'Beginner'),
(79, 'Chest Stretch Variation Two', 'Primary Chest', '', 'None', '', 'Lay on your side with one hand just in front of you., With a slight bend in your arm, rotate your arm around your body as slowly as possible., Return to the starting position and then repeat on the other side.', 'Stretches', 'Beginner'),
(80, 'Chest Stretch Variation One', 'Primary Chest', '', 'None', '', 'Using a raised wall, place your arm at a 90 degree angle against it., With a staggered stance, lean into the wall extending your chest., Pause for a few seconds and then return to starting position and repeat on opposite side.', 'Stretches', 'Beginner'),
(81, 'Push Up', 'Primary Chest', 'https://www.youtube.com/embed/R4uGphXkSiQ', 'Neutral', 'Push', 'Place your hands firmly on the ground, directly under shoulders., Flatten your back so your entire body is straight and slowly lower your body, Draw shoulder blades back and down, keeping elbows tucked close to your body, Exhale as you push back to the starting position.', 'Bodyweight', 'Beginner'),
(82, 'Incline Push Up', 'Primary Chest', 'https://www.youtube.com/embed/R4uGphXkSiQ', '', '', 'Stand facing bench or sturdy elevated platform., Place hands on edge of bench or platform, slightly wider than shoulder width., Slowly lower your body until your chest almost touches the bench., Push body up until arms are extended.', 'Bodyweight', 'Beginner'),
(83, 'Decline Push Up', 'Primary Chest', 'https://www.youtube.com/embed/R4uGphXkSiQ', '', '', 'Use a bench to elevate your feet., Put your hands slightly wider than shoulder-width., Slowly lower your body until your chest almost touches the ground, Raise your body until you almost lock your elbows.', 'Bodyweight', 'Beginner'),
(84, 'Cable Pec Fly', 'Primary Chest', '', 'Neutral', 'Push', 'Use a handle attachment set all the way to the bottom of the machine., Bring both of the handles to your chest and make sure you are in the center of the cable crossover., Walk a few steps forward. Then press the weight forward. From there, your shoulders should horizontally abduct and adduct while your elbows stay in a fixed position.', 'Cables', 'Beginner'),
(85, 'Cable Chest Press', 'Primary Chest Secondary Triceps', '', 'Overhand', 'Push', 'Use a handle attachment. The cables should be set to shoulder height., Bring both of the handles to your chest and make sure you are in the center of the cable crossover., Walk a few steps forward. Then press the weight forward., From there, you should flex and extend at both the shoulders and elbows simultaneously.', 'Cables', 'Beginner'),
(86, 'Band Single Arm Pec Fly', 'Primary Chest', '', '', '', 'Attach the band to an anchor point that is about shoulder height. Take a few steps away until the band is taut., Extend your shoulder joint and bring your arm across your body until you feel a contraction in your pecs.', 'Band', 'Beginner'),
(87, 'Band Single Arm Chest Press', 'Primary Chest', '', '', '', 'Attach the band to an anchor point that is about shoulder height. Take a few steps away until the band is taut., Extend your shoulder joint and elbow across your body until your arm is fully extended.', 'Band', 'Beginner'),
(88, 'Band Pushup', 'Primary Chest Secondary Triceps', 'https://www.youtube.com/embed/R4uGphXkSiQ', '', '', 'Loop the band around your mid-back. The ends of the bands should be around your hands between your thumb and pointer finger., This exercise can be done from the knees or the toes depending on experience level., Begin by breaking at the elbows until your upper arm is by your side. Then extend at the elbows back into the top position.', 'Band', 'Beginner'),
(89, 'Barbell Floor Press', 'Primary Chest Secondary Triceps', '', '', 'Push', 'Un-rack the bar and break at the elbows and shoulders., Bring your elbows to the floor, rest for a second to remove all the momentum from the rep, then press to a lockout.', 'Barbell', 'Advanced'),
(90, 'Barbell Hooklying Bench Press', 'Primary Chest Secondary Triceps', '', 'Overhand', 'Push', 'Start in a bench press position with your feet planted on the bench and knees bent., Grasp the barbell with an overhand grip, positioning your hands slightly wider than shoulder-width apart., Lift the barbell off the rack and lower it to your chest, keeping your elbows close to your sides., Push the barbell back up to the starting position, fully extending your arms.', 'Barbell', 'Intermediate'),
(91, 'Barbell Larsen Bench Press', 'Primary Chest Secondary Triceps Tertiary Abdominals', '', 'Overhand', 'Push', 'Start in a bench press position with your legs elevated. Try to keep your knees with a slight bend in them., Unrack the bar then initiate the rep., Bring the bar to your torso and tap your ribcage before pressing back to the ceiling.', 'Barbell', 'Advanced'),
(92, 'Barbell Spoto Press', 'Primary Chest Secondary Triceps', '', 'Overhand', 'Push', 'Start on the bench with your shoulder blades pulled back., As you initiate the first rep, tuck your elbows slightly until the bar is a few inches off your chest., Pause just off your chest for 1-3 seconds. Then press explosively.', 'Barbell', 'Intermediate'),
(93, 'Dumbbell Bench Press', 'Primary Chest', '', '', '', 'Lay flat on the bench with your feet on the ground. Raise the dumbbells until you have straight arms., Lower the dumbbells to your mid chest, \"Raise the dumbbells until youve locked your elbows.\"\"\"', 'Dumbbells', 'Beginner'),
(94, 'Dumbbell Chest Fly', 'Primary Chest', '', 'Neutral', 'Push', 'Lay on a bench with two dumbbells extended in front of you., You should have a slight bend in your elbows and maintain that bend. Your shoulder joint should do all the movement., Bring the dumbbells out to your side. Stop the dumbbells before going behind your body., Bring the dumbbells back together at the top.', 'Dumbbells', 'Beginner'),
(95, 'Dumbbell Decline Alternating Single Arm Press', 'Primary Chest Secondary Triceps', '', 'Overhand', 'Push', 'Lay on a decline bench with both arms extended in front of you., Break at one of your arms first bringing that arm to your side while keeping the other arm extended., Press the dumbbell back to the ceiling. Fully extend that arm before initiating on the other side.', 'Dumbbells', 'Intermediate'),
(96, 'Dumbbell Decline Bench Press', 'Primary Chest Secondary Triceps', '', 'Overhand', 'Push', 'Lay on a decline bench with both arms extended in front of you., Break at the elbows and bring your upper arms to your side., Once your upper arms have reached your side, press the dumbbells back to the ceiling.', 'Dumbbells', 'Beginner'),
(97, 'Dumbbell Decline Chest Fly', 'Primary Chest', '', 'Neutral', 'Push', 'Lay flat on a decline bench press with the dumbbells pointed toward the ceiling., Maintain a slight bend in your elbows. Bring the dumbbells out to the side, stopping the dumbbells before they pass your torso., Bring the dumbbells together to finish the rep.', 'Dumbbells', 'Beginner'),
(98, 'Dumbbell Decline Guillotine Bench Press', 'Primary Chest Triceps', '', 'Overhand', 'Push', 'Lie on the decline bench, grasp a pair of dumbbells and hold them at shoulder level with your palms facing forward., Slowly lower the dumbbells to your chest, keeping your elbows flared out. As you lower the dumbbells, exhale deeply., Once your elbows reach your chest, pause for a moment and then press them back up to the starting position', 'Dumbbells', 'Advanced'),
(99, 'Dumbbell Decline Neutral Bench Press', 'Primary Chest Secondary Triceps', '', 'Neutral', 'Push', 'Lie on the decline bench, grasp a pair of dumbbells and hold them at shoulder level with your palms facing each other. Keep your elbows bent and close to your body., Slowly lower the dumbbells to your chest, keeping your elbows bent and close to your body., Once the dumbbells reach your chest, pause for a moment and then press them back up to the starting position., This variation is much easier on the shoulder joints.', 'Dumbbells', 'Beginner'),
(100, 'Dumbbell Decline Single Arm Bench Press', 'Primary Chest Secondary Triceps', '', 'Overhand', 'Push', 'Start by laying on a decline bench with two dumbbells., Position the dumbbell above your chest with your elbow bent and shoulder level., Slowly lower the dumbbell towards your chest, keeping your elbow close to your body and your core engaged while the other dumbbell remains pointed toward the ceiling., Once the dumbbell reaches your side, pause for a moment and then press the weight back up to the starting position., Repeat for the desired number of repetitions before switching to your other arm.', 'Dumbbells', 'Intermediate'),
(101, 'Dumbbell Decline Squeeze Press', 'Primary Chest Shoulders Secondary Triceps', '', 'Neutral', 'Push', 'Lay flat on a decline bench with two dumbbells pressed to one another., Break at the elbows and keep your elbows tucked as you bring the dumbbells to your chest., Continue to press the dumbbells into one another throughout the rep., Once the dumbbells reach your chest, press back toward the ceiling.', 'Dumbbells', 'Intermediate'),
(102, 'Dumbbell Guillotine Bench Press', 'Primary Chest Triceps', '', 'Overhand', 'Push', 'Lie on the flat bench, grasp a pair of dumbbells and hold them at shoulder level with your palms facing forward., Slowly lower the dumbbells to your chest, keeping your elbows flared out. As you lower the dumbbells, exhale deeply., Once your elbows reach your chest, pause for a moment and then press them back up to the starting position', 'Dumbbells', 'Advanced'),
(103, 'Dumbbell Guillotine Incline Bench Press', 'Primary Chest Secondary Triceps', '', 'Overhand', 'Push', 'Lie on the incline bench, grasp a pair of dumbbells and hold them at shoulder level with your palms facing forward., Slowly lower the dumbbells to your chest, keeping your elbows flared out. As you lower the dumbbells, exhale deeply., Once your elbows reach your chest, pause for a moment and then press them back up to the starting position', 'Dumbbells', 'Advanced'),
(104, 'Dumbbell Incline Bench Press', 'Primary Chest', 'https://www.youtube.com/embed/bH_T3ibPPzg', 'Overhand', 'Push', 'Raise the bench to a 30 - 45 degree angle, Lay on the bench and set your feet on the ground., Raise the dumbbells with straight arms then slowly lower them to about shoulder width., Raise them again until your arms are locked and at the starting position again.', 'Dumbbells', 'Beginner'),
(105, 'Dumbbell Incline Chest Fly', 'Primary Chest', '', 'Neutral', 'Push', 'Lay on an incline bench with two dumbbells extended in front of you., You should have a slight bend in your elbows and maintain that bend. Your shoulder joint should do all the movement., Bring the dumbbells out to your side. Stop the dumbbells before going behind your body., Bring the dumbbells back together at the top.', 'Dumbbells', 'Beginner'),
(106, 'Dumbbell Internally Rotated Chest Fly', 'Primary Chest', '', 'Overhand', 'Push', 'Lay on a bench with two dumbbells extended in front of you in an overhand grip., You should have a slight bend in your elbows and maintain that bend. Your shoulder joint should do all the movement., Bring the dumbbells out to your side. Stop the dumbbells before going behind your body., Bring the dumbbells back together at the top.', 'Dumbbells', 'Advanced'),
(107, 'Dumbbell Internally Rotated Incline Chest Fly', 'Primary Chest', '', 'Overhand', 'Push', 'Lay on an incline bench with two dumbbells extended in front of you., You should have a slight bend in your elbows and maintain that bend. Your shoulder joint should do all the movement. You should have an overhand grip., Bring the dumbbells out to your side. Stop the dumbbells before going behind your body., Bring the dumbbells back together at the top.', 'Dumbbells', 'Advanced'),
(108, 'Dumbbell Neutral Bench Press', 'Primary Chest Secondary Triceps', '', 'Neutral', 'Push', 'Lie on a flat bench, grasp a pair of dumbbells and hold them at shoulder level with your palms facing each other. Keep your elbows bent and close to your body., Slowly lower the dumbbells to your chest, keeping your elbows bent and close to your body., Once the dumbbells reach your chest, pause for a moment and then press them back up to the starting position., This variation is much easier on the shoulder joints.', 'Dumbbells', 'Beginner'),
(109, 'Dumbbell Neutral Incline Bench Press', 'Primary Chest Secondary Triceps', '', 'Neutral', 'Push', 'Lie on an incline bench, grasp a pair of dumbbells and hold them at shoulder level with your palms facing each other. Keep your elbows bent and close to your body., Slowly lower the dumbbells to your chest, keeping your elbows bent and close to your body., Once the dumbbells reach your chest, pause for a moment and then press them back up to the starting position., This variation is much easier on the shoulder joints.', 'Dumbbells', 'Beginner'),
(110, 'Dumbbell Squeeze Press', 'Primary Chest Secondary Triceps', '', 'Neutral', 'Push', 'Lay flat on a bench with two dumbbells pressed to one another., Break at the elbows and keep your elbows tucked as you bring the dumbbells to your chest., Continue to press the dumbbells into one another throughout the rep., Once the dumbbells reach your chest, press back toward the ceiling.', 'Dumbbells', 'Beginner'),
(111, 'Dumbbell Single Arm Press', 'Primary Chest Secondary Triceps', '', 'Overhand', 'Push', 'Start by laying on a bench with two dumbbells., Position the dumbbell above your chest with your elbow bent and shoulder level., Slowly lower the dumbbell towards your chest, keeping your elbow close to your body and your core engaged while the other dumbbell remains pointed toward the ceiling., Once the dumbbell reaches your side, pause for a moment and then press the weight back up to the starting position., Repeat for the desired number of repetitions before switching to your other arm.', 'Dumbbells', 'Intermediate'),
(112, 'Burpee', 'Primary Chest Abdominals Secondary Quads Tertiary Glutes', '', 'Overhand', 'Push', 'Start standing with your feet hip-width apart. Lower your body into a squat position, placing your hands on the ground in front of you., Quickly jump your feet back into a plank position, keeping your body in a straight line from your head to your toes., Perform a push-up, keeping your core tight and your elbows close to your body., Jump your feet back towards your hands, and then jump up into the air, reaching your arms overhead., Land softly and immediately lower your body back into the squat position to repeat the movement.', 'Bodyweight', 'Intermediate'),
(113, 'Band Chest Fly', 'Primary Chest', '', 'Neutral', 'Push', 'Secure two resistance bands to a sturdy anchor point (e.g. a door anchor, a post, etc.) at chest height., Stand facing away from the anchor point with feet shoulder-width apart and hold each end of the band in each hand., Start with arms fully extended and parallel to the floor., Keeping arms straight, slowly bring your arms together in front of your chest.', 'Band', 'Beginner'),
(114, 'Band Chest Press', 'Primary Chest', '', 'Overhand', 'Push', 'Stand in the middle of the two bands with feet shoulder-width apart. Step forward with one foot to create tension in the bands., Hold one band in each hand, palms facing forward.\\nStart the exercise with your arms extended in front of you, keeping a slight bend in your elbows., Lower your arms to the sides of your chest, maintaining control of the band., Push your arms back up to the starting position, keeping your core engaged and maintaining control of the band.', 'Band', 'Beginner'),
(115, 'Band Internally Rotated Chest Fly', 'Primary Chest', '', 'Overhand', 'Push', 'Start with both feet hip-width apart and step on the middle of the band with both feet. Hold the handles of the band in each hand and position the band above your chest, with your palms facing down., Keeping your elbows slightly bent, slowly raise your arms out to the sides until they are parallel with the floor. Make sure to internally rotate your shoulder joints by bringing your shoulder blades together and rotating your forearms inward towards your torso., Hold the position for a few seconds and then slowly lower your arms back to the starting position, keeping the internal rotation of your shoulder joints throughout the movement.', 'Band', 'Intermediate'),
(116, 'Plate Deficit Pushup', 'Primary Chest Secondary Triceps Tertiary Shoulders', '', 'Overhand', 'Push', 'Start in a pushup position with both hands on a plate. This will extend your range of motion., Break at the elbows and shoulders until your upper arm travels behind the body slightly., Then press back into the original position.', 'Plate', 'Advanced'),
(117, 'Plate Glute Bridge to Chest Press', 'Primary Chest Secondary Glutes', '', 'Neutral', 'Push', 'Lay flat on the ground with a plate on your chest., Push your hips up toward the ceiling and maintain that extended position., With your hips up, press the plate toward the ceiling.', 'Plate', 'Beginner'),
(118, 'Dumbbell Elevated Pushup', 'Primary Chest Secondary Triceps', '', 'Neutral', 'Push', 'Start by placing two dumbbells on the floor, about shoulder-width apart., Position yourself in a plank position with your hands on top of the dumbbells, keeping your arms straight., Keep your feet together, with your toes resting on the floor., Engage your core and lower your body towards the floor by bending your arms, until your chest nearly touches the dumbbells., Push your body back up to the starting position, straightening your arms and engaging your chest, triceps and shoulders.', 'Dumbbells', 'Intermediate'),
(119, 'Pushup to Renegade Row', 'Primary Chest Secondary Lats Tertiary Abdominals', '', 'Neutral', 'Push', 'Start in a high plank position with a pair of dumbbells positioned under your shoulders. Your hands should grip the dumbbells, palms facing each other., Keep your core engaged and your back straight as you row one dumbbell up towards your hip, keeping your elbow close to your body., Lower the dumbbell back down to the starting position and repeat on the other side.', 'Dumbbells', 'Intermediate'),
(120, 'TRX Pec Fly', 'Primary Chest', '', 'Neutral', 'Push', 'Start by adjusting the TRX straps to chest height. Stand facing the TRX with your feet shoulder-width apart., Grasp the TRX handles with both hands, keeping your arms extended and your shoulders away from your ears., Lean forward, keeping your arms extended and your core engaged. Your body should form a slight diagonal line from your ankles to your head., Slowly bring your arms towards each other, in front of your chest, while keeping your core engaged and your body in a stable position.', 'TRX', 'Intermediate'),
(121, 'TRX Pushup', 'Primary Chest Secondary Triceps Tertiary Shoulders', '', 'Overhand', 'Push', 'Start by setting up the TRX straps to a secure anchor point and adjust the straps so that they hang about waist height when you stand under them., Stand facing the TRX straps, grab the handles with both hands, and step forward so that your body is in a plank position with your hands directly beneath your shoulders., Lower your body down by bending your elbows until your chest nearly touches the handles. Keep your core tight and your body in a straight line., Push your body back up to the starting position, straightening your arms as you do so. Make sure to engage your chest, triceps, and core throughout the movement.', 'TRX', 'Beginner'),
(122, 'Dumbbell Alternating Single Arm Press', 'Primary Chest Shoulders Secondary Triceps', '', 'Overhand', 'Push', 'Start in a bench press position with two dumbbells., Break at one of your elbows first while keeping the other elbow extended., Your working arm should reach your side before pressing back to the ceiling., Then alternate to the other arm.', 'Dumbbells', 'Intermediate');
INSERT INTO `workout` (`ID`, `exercise_name`, `target`, `youtubeURL`, `Grips`, `Force`, `steps`, `Category`, `Difficulty`) VALUES
(123, 'Bow Pose', 'Primary Chest Secondary Abdominals Tertiary Quads', '', '', 'Hold', 'Lie on your stomach with your arms at your sides., Bend your knees and bring your heels towards your buttocks., Reach back with your hands and grab your ankles., Inhale and lift your chest off the ground, pulling your legs up and back with your hands., Keep your gaze forward and hold the pose for a few breaths before releasing back to the ground.', 'Yoga', 'Intermediate'),
(124, 'Yogi Arm Clock', 'Primary Chest Secondary Biceps Tertiary Shoulders', '', '', 'Hold', 'Stand facing a wall with your arms extended and your palms pressing into the wall at shoulder height., Slowly begin to walk your feet away from the wall, keeping your arms straight and your hands pressing into the wall., Walk until your arms are fully extended and your body is at a 45-degree angle to the ground., Repeat From High to Low Angles starting above the shoulder, and ending below the chest.', 'Yoga', 'Beginner'),
(125, 'Mountain', 'Primary Chest', '', '', 'Hold', 'Stand tall with your feet hip-width apart and your arms at your sides., Press your feet into the ground and engage your leg muscles., Lift your chest and roll your shoulders back and down., Take a deep breath in, and as you exhale, release any tension in your body and relax your muscles.', 'Yoga', 'Beginner'),
(126, 'Tree Pose', 'Primary Chest', '', '', 'Hold', 'Start in mountain pose, standing with your feet hip-width apart and arms at your sides., Shift your weight onto your left foot and bend your right knee, bringing the sole of your right foot to the inside of your left thigh., Bring your hands together in front of your heart, or raise them overhead., Hold the pose for a few breaths, then release and repeat on the other side.', 'Yoga', 'Beginner'),
(127, 'Warrior One', 'Primary Chest', '', '', 'Hold', 'Start in mountain pose, standing with your feet hip-width apart and arms at your sides., Step your left foot back about 3-4 feet and turn it slightly inward, while keeping your right foot facing forward., Inhale and raise your arms overhead, then exhale and bend your right knee, bringing it directly over your ankle., Hold the pose for a few breaths, then inhale and return to mountain pose. Repeat on the other side.', 'Yoga', 'Beginner'),
(128, 'Warrior Two', 'Primary Chest', '', '', 'Hold', 'Start in mountain pose, standing with your feet hip-width apart and arms at your sides., Step your left foot back about 3-4 feet and turn it slightly inward, while keeping your right foot facing forward., Inhale and raise your arms to shoulder height, parallel to the ground, with your palms facing down., Exhale and bend your right knee, bringing it directly over your ankle while keeping your left leg straight., Look over your right hand and hold the pose for a few breaths, then inhale and return to mountain pose. Repeat on the other side.', 'Yoga', 'Beginner'),
(129, 'Supported Matsyasana Pose', 'Primary Chest Secondary Mid back', '', '', 'Hold', 'Sit on the floor with your legs stretched out in front of you., Place a yoga block behind you, horizontally, at the base of your spine., Slowly recline over the block, supporting yourself with your forearms, until your back is resting on the block., Relax your arms at your sides, palms facing up, and breathe deeply.', 'Yoga', 'Beginner'),
(130, 'Barbell Squat', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/NcMEyM7qyuk', 'Overhand', 'Push', 'Stand with your feet shoulder-width apart. Maintain the natural arch in your back, squeezing your shoulder blades and raising your chest., Grip the bar across your shoulders and support it on your upper back. Unwrack the bar by straightening your legs, and take a step back., Bend your knees as you lower the weight without altering the form of your back until your hips are below your knees., Raise the bar back to starting position, lift with your legs and exhale at the top.', 'Barbell', 'Advanced'),
(131, 'Machine Leg Press', 'Primary Quads', '', 'None', 'Push', 'Place your legs on the platform with your feet at shoulder width., Release the weight and extend your legs fully, without locking your knees., Lower the weight until your legs are at a 90Â° angle (but DO NOT allow your butt and lower back to rise off of the pad. This will put your lower back in a rounded position, which is very dangerous.), Raise the weight back to starting position.', 'Machine', 'Intermediate'),
(132, 'Machine Leg Extension', 'Primary Quads', '', 'None', 'Push', 'Sit on the machine with your back against the cushion and adjust the machine you are using so that your knees are at a 90 degree angle at the starting position., Raise the weight by extending your knees outward, then lower your leg to the starting position. Both movements should be done in a slow, controlled motion.', 'Machine', 'Intermediate'),
(133, 'Dumbbell Goblet Squat', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Hold the weight tucked into your upper chest area, keeping your elbows in. Your feet should be slightly wider than shoulder width., Sink down into the squat, keeping your elbows inside the track of your knees., Push through your heels while keeping your chest up and return to starting position.', 'Dumbbells', 'Beginner'),
(134, 'Bulgarian Split Squat', 'Primary Quads', '', '', '', 'Stand with your back to a bench (or raised surface) and place one of your feet on the bench., Squat down until your front leg is about parallel to the floor., Go back to the starting position. After completing the desired amount of reps, switch legs and repeat.', 'Bodyweight', 'Beginner'),
(135, 'Kettlebell Step Up', 'Primary Quads', 'https://www.youtube.com/embed/c-Cc-LDrpL4', 'Neutral', 'Push', 'Standing up straight, using a bench as a step, raise one foot onto the bench and hold the kettlebell in the same arm as the straight leg., Stand and bring both feet onto the bench. Slowly lower your leg back down to the starting position., Repeat.', 'Kettlebells', 'Beginner'),
(136, 'Kettlebell Goblet Squat', 'Primary Quads', '', '', '', 'Hold the weight tucked into your upper chest area, keeping your elbows in. Your feet should be slightly wider than your shoulders., Sink down into the squat, keeping your elbows inside the track of your knees., Push through your heels while keeping your chest up and return to the starting position.', 'Kettlebells', 'Beginner'),
(137, 'Kettlebell Heels Up Goblet Squat', 'Primary Quads', '', '', '', 'Hold the weight tucked into your upper chest area, keeping your elbows in. Your feet should be slightly wider than your shoulders and the heels slightly raised on a surface., Sink down into the squat, keeping your elbows inside the track of your knees., Push through your heels while keeping your chest up and return to the starting position.', 'Kettlebells', 'Beginner'),
(138, 'Kettlebell Reverse Lunge', 'Primary Quads', '', '', '', 'Stand straight with your feet slightly apart and hold a kettlebell in one hand., Bring the same leg as the arm holding the kettlebell behind you, squat down until your back knee touches the ground, keeping your back straight., Return to the starting position and repeat.', 'Kettlebells', 'Beginner'),
(139, 'Kettlebell Forward Lunge', 'Primary Quads', '', '', '', 'Stand straight with your feet slightly apart and hold a kettlebell in one hand., Bring the same leg as the arm holding the kettlebell in front of you, squat down until your thigh is parallel to the ground, keeping your back straight., Return to the starting position and repeat.', 'Kettlebells', 'Beginner'),
(140, 'Kettlebell Lateral Lunge', 'Primary Quads', '', '', '', 'Stand straight with your feet shoulder width apart. Keep your back straight and hold the kettlebell in front of your pelvis., Take a large step to one side, holding the kettlebell low and lunge into the bent leg., Return to the starting position and repeat', 'Kettlebells', 'Beginner'),
(141, 'Quads Stretch Variation Four', 'Primary Quads', '', 'None', '', 'Lay flat on your belly. Extend one arm out in front of you, placing your palm on the floor. With your other arm, grab the top of your foot., Keeping your upper leg flat on the floor, pull your foot towards your buttocks to engage the stretch., Pause for a few seconds, and then repeat the stretch with your other leg.', 'Stretches', 'Beginner'),
(142, 'Quads Stretch Variation Three', 'Primary Quads', '', 'None', '', 'Lay flat on your belly, with both hands reach back and grab the tops of your feet., Keeping your upper legs flat on the ground, pull both of your feet towards your buttocks., Pause at the apex of the stretch, then return to starting position.', 'Stretches', 'Beginner'),
(143, 'Quads Stretch Variation Two', 'Primary Quads', '', 'None', '', 'Place one foot flat on the ground in front of you at a 90 degree angle. With your other leg, balance upon your knee, placing the tip of your foot against the wall behind you for balance., Place your hands on your knee in front of you, and lean forward so that your knee extends over your foot., Pause at the apex of the stretch, and slowly return to starting position. Repeat with your other leg.', 'Stretches', 'Beginner'),
(144, 'Quads Stretch Variation One', 'Primary Quads', '', 'None', '', 'Stand perpendicular to a wall, using one arm against the wall for balance. With your other arm, grab the top of your foot., Pull your leg upwards and back to engage your quads, pausing at the apex of the stretch for a few seconds., Return to starting position and repeat with your other leg.', 'Stretches', 'Beginner'),
(145, 'Side Lunges', 'Primary Quads Secondary Glutes', '', '', '', 'Take a slow, lateral step to the right., Extend the left knee, and drive your weight into your right side., Flex your knee and lower your body., Push back to the starting position.', 'Bodyweight', 'Beginner'),
(146, 'Forward Lunges', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/Y9zHge5sRj4', 'None', 'Push', 'Step forward with one leg., Lower your body until your rear knee nearly touches the ground., Ensure you remain upright, and your front knee stay above the front foot., Push off the floor with your front foot until you return to the starting position. Switch legs.', 'Bodyweight', 'Beginner'),
(147, 'Jump Squats', 'Primary Quads Secondary Glutes', '', '', '', 'Stand with your feet shoulder-width apart., Start by doing a regular squat, then engage your core and jump up explosively., When you land, lower your body back into the squat position.', 'Bodyweight', 'Beginner'),
(148, 'Bodyweight Squat', 'Primary Quads', '', 'None', 'Push', 'Stand with your feet shoulder width apart., flex your knees and hips and sit back into the squat while lowering your body, Continue down to full depth, Return to starting position.', 'Bodyweight', 'Beginner'),
(149, 'Barbell Curtsy Lunge', 'Primary Quads Secondary Glutes', '', 'Overhand', 'Push', 'Place the barbell on your back, Step your foot back and around while simultaneously bringing the weight down., Return to start and repeat on other leg.', 'Barbell', 'Intermediate'),
(150, 'Cable Goblet Squat', 'Primary Quads Secondary Glutes Tertiary Lower back', '', 'Neutral', '', 'Start with a straight bar or easy bar attachment ., Get your elbows directly underneath the bar, squat straight up and then walk back a few steps., Break at your knees and hips to initiate the squat. Make sure you maintain a flat back., Squat all the way back to a standing position.', 'Cables', 'Beginner'),
(151, 'Cable Split Squat', 'Primary Quads Secondary Glutes', '', 'Overhand', '', 'You can use any attachment on this exercise. Cable should be set all to the bottom., Grab the attachment and walk back a few steps. Take a split stance. One leg in front and one behind., Break at the knees and hips to initiate the split squat. Make sure your back knee touches the floor., Squat back up into a standing position.', 'Cables', 'Advanced'),
(152, 'Cable Standing Leg Extension', 'Primary Quads', '', 'None', '', 'Stand upright facing away from the cable crossover with the cable set to the height of your hips. Use an ankle/wrist attachment., Extend at the knee against the cable.', 'Cables', 'Advanced'),
(153, 'Band Spanish Squat', 'Primary Quads Secondary Glutes', '', '', '', '', 'Band', 'Advanced'),
(154, 'Band Leg Press', 'Primary Quads Secondary Glutes', '', '', '', 'Lay flat on the ground with your head by the anchor point of the band, which should be 6 to 12 inches above your head, Place the band around your midfoot and extend at the knees and hip.', 'Band', 'Advanced'),
(155, 'Band Squat', 'Primary Quads Secondary Glutes', '', '', '', 'Stand with a shoulder width grip. Both feet should be on the band with the band loops in each of your hand., Push your butt back and also break at the knees., \"Try to hit parallel depth (bottom of your leg should be parallel with the ground). If you cant tell your squat depth', 'Band', 'Beginner'),
(156, 'Dumbbell Goblet Bulgarian Split Squat', 'Primary Quads Secondary Glutes', '', '', '', 'Holding the dumbbell in a goblet squat place your foot on a bench or box., Break at the hips and knees. Try to go to the point where your hamstring is parallel with the ground. Or until your back knee taps the floor., Make sure to keep your back flat and your elbows directly under your fists.', 'Dumbbells', 'Advanced'),
(157, 'Barbell Bulgarian Split Squat', 'Primary Quads Secondary Glutes', '', '', '', 'Find a box or bench that is roughly knee height or a few inches lower. (The lower the box is the lower the mobility requirement)., You can either place your toes or the top of your foot on the box. Point your front foot forward or slightly turned outwards and break at the knees and hips simultaneously., Aim to get your front leg to hit parallel depth (hamstring parallel with the ground) or lower., The further forward your front foot is, the longer the Range of Motion is at your hips. The further back your front foot is, the longer the ROM at your knees.', 'Barbell', 'Advanced'),
(158, 'Barbell Front Squat Olympic', 'Primary Quads Secondary Glutes Tertiary Abdominals', '', '', 'Push', '\"Measure your hands by the markings on the bar to make sure youre even. Then throw your elbows underneath the bar.\"\"', 'Barbell', 'Advanced'),
(159, 'Barbell Front Squat Bodybuilding', 'Primary Quads Secondary Glutes Tertiary Abdominals', '', '', 'Push', '\"Measure your shoulders against the bar to make sure youre even. Throw your shoulders under the bar. The bar should be digging into your neck slightly.\"\"', 'Barbell', 'Advanced'),
(160, 'Barbell High Bar Squat', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/NcMEyM7qyuk', '', 'Push', 'Place the bar on top of your traps., Push your hips back and break at the knees. Push your elbows forward to keep yourself from getting pitched too far forward., Aim to get your hamstrings to parallel with the floor., Push your heels into the floor and squat back to a standing position.', 'Barbell', 'Beginner'),
(161, 'Barbell Low Bar Squat', 'Primary Quads Secondary Glutes Tertiary Lower back', 'https://www.youtube.com/embed/NcMEyM7qyuk', '', '', 'Pull your shoulder blades back really hard and place the bar on your posterior delts., Push your hips back and break at the knees to initiate the squat., Aim to get your hamstrings to parallel with the ground. Push your heels into the floor and squat back up to a standing position.', 'Barbell', 'Advanced'),
(162, 'Dumbbell Bulgarian Split Squat', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Find a box or bench that is roughly knee height or a few inches lower. (The lower the box is the lower the mobility requirement)., You can either place your toes or the top of your foot on the box. Point your front foot forward or slightly turned outwards and break at the knees and hips simultaneously., Aim to get your front leg to hit parallel depth (hamstring parallel with the ground) or lower., The further forward your front foot is, the longer the Range of Motion is at your hips. The further back your front foot is, the longer the ROM at your knees.', 'Dumbbells', 'Advanced'),
(163, 'Barbell Coan Deadlift', 'Primary Quads Secondary Glutes Tertiary Lower back', 'https://www.youtube.com/embed/C6aXGfIilEE', 'Overhand Mixed', '', 'Stand with your feet hip-width apart and place a barbell on the floor in front of you., Bend at the hips and knees to grasp the barbell with an overhand grip, positioning your hands just outside of your hips., Keeping your back straight and your core engaged, lift the barbell off the floor by extending your hips and knees., As you lift the barbell, pull your shoulders back and squeeze your glutes to fully extend your hips at the top of the movement.', 'Barbell', 'Intermediate'),
(164, 'Barbell Forward Lunge', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/Y9zHge5sRj4', 'Overhand', 'Push', 'Stand with your feet shoulder-width apart and hold a barbell at your shoulders with an overhand grip., Step forward with your right foot and bend both knees to lower your body until your right thigh is parallel to the ground., Push off your right foot to return to the starting position.', 'Barbell', 'Advanced'),
(165, 'Barbell Front Rack Step Up', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/c-Cc-LDrpL4', 'None', 'Push', 'Stand in front of a step or bench with your feet shoulder-width apart and hold a barbell at your shoulders with an overhand grip., Step up onto the bench with your right foot, pushing off your foot to raise your body up onto the bench., Bring your left foot up onto the bench and stand up straight, keeping the barbell at your shoulders., Step back down with your left foot, followed by your right foot, to return to the starting position.', 'Barbell', 'Advanced'),
(166, 'Barbell Heels Up Back Squat', 'Primary Quads Secondary Glutes', '', 'Overhand', 'Push', 'Stand with your feet shoulder-width apart and your heels on a plate or low step., Bend your knees and lower your body into a squat position., Push through your heels to return to the starting position.', 'Barbell', 'Advanced'),
(167, 'Barbell Heels Up Front Squat', 'Primary Quads Secondary Glutes', '', 'None', 'Push', 'Stand with your feet shoulder-width apart and feet on a plate. Place the bar in a front rack position., Bend your knees and lower your body into a squat position, keeping your heels raised off the ground., Push through your heels to return to the starting position.', 'Barbell', 'Advanced'),
(168, 'Barbell Lateral Lunge', 'Primary Quads Secondary Glutes', '', 'Overhand', 'Push', 'Stand with your feet shoulder-width apart and hold a barbell at your shoulders with an overhand grip., Step out to the side with your right foot and bend your right knee to lower your body until your right thigh is parallel to the ground., Push off your right foot to return to the starting position., Repeat on the opposite side by stepping out with your left foot.', 'Barbell', 'Advanced'),
(169, 'Barbell Pause Box Squat', 'Primary Quads Secondary Glutes Tertiary Lower back', '', 'Overhand', 'Push', 'Stand in front of a box or bench with your feet shoulder-width apart and a barbell resting on your upper back., Lower your body into a squat position, sitting back onto the box and pausing for a moment before standing back up., Push through your heels to return to the standing position.', 'Barbell', 'Advanced'),
(170, 'Barbell Pause Squat', 'Primary Quads Secondary Glutes Tertiary Lower back', '', 'Overhand', 'Push', 'Stand with your feet shoulder-width apart and a barbell resting on your upper back., Lower your body into a squat position, pausing for a moment at the bottom of the movement before standing back up., Push through your heels to return to the standing position.', 'Barbell', 'Intermediate'),
(171, 'Barbell Reverse Deadlift', 'Primary Quads Secondary Glutes Tertiary Lower back', '', 'Overhand Underhand', 'Push', 'Start by standing with your feet about hip-width apart, with your toes pointed slightly outward., Place a barbell on the floor behind you, with the weight plates on either side., Bend down and grab the barbell with an overhand grip, your hands slightly wider than shoulder-width apart. Keep your back straight and your shoulders down as you grip the bar., Stand up straight, lifting the barbell off the ground and pulling it toward your hips. Keep your elbows close to your body and your shoulders down as you do this.', 'Barbell', 'Advanced'),
(172, 'Barbell Reverse Lunge', 'Primary Quads Secondary Glutes', '', 'Overhand', 'Push', 'Start by standing with your feet about hip-width apart, with a barbell on your shoulders. Your feet should be slightly pointed outward, and your shoulders should be down and relaxed., Step backward with one foot, lowering your body until your front thigh is parallel to the ground and your back knee is hovering just above the ground., Push off your back foot, returning to the starting position.', 'Barbell', 'Intermediate'),
(173, 'Bodyweight Reverse Step Up', 'Primary Quads', '', 'None', 'Push', 'Start with a box or bench behind you and one of your feet on the box/bench., The balls of your feet should be planted on the box/bench., \"Drive through the ball of your foot extending your knees until youre upright and standing on the box.\"\"\"', 'Bodyweight', 'Advanced'),
(174, 'Barbell Side Step Up', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/c-Cc-LDrpL4', 'Overhand', 'Push', 'Start by standing with your feet about hip-width apart, with a barbell on your shoulders. Your feet should be slightly pointed outward, and your shoulders should be down and relaxed., Place a step or box beside you, and step up onto it with one foot. Your other foot should remain on the ground., Push off your grounded foot, raising your body up onto the step., Step back down with the same foot, returning to the starting position.', 'Barbell', 'Intermediate'),
(175, 'Barbell Split Squat', 'Primary Quads Secondary Glutes', '', 'Overhand', 'Push', 'Take a large step forward with one leg, and lower your body until your back knee nearly touches the ground. Your front knee should be bent at a 90-degree angle, and your back leg should be straight., Push through the heel of your front foot to raise your body back to the starting position.', 'Barbell', 'Intermediate'),
(176, 'Barbell Step Up Balance', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/c-Cc-LDrpL4', 'Overhand', 'Push', 'Begin by standing in front of a sturdy box or bench that is about knee-height, holding a barbell across your upper back and shoulders, using an overhand grip., Place your right foot on the box and push through the heel to lift your body up and onto the box, keeping your left foot off the ground., Step back down with your right foot, lowering your left foot to the ground.', 'Barbell', 'Intermediate'),
(177, 'Barbell Step Up', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/c-Cc-LDrpL4', 'Overhand', 'Push', 'Begin by standing in front of a sturdy box or bench that is about knee-height, holding a barbell across your upper back and shoulders, using an overhand grip., Place your right foot on the box and push through the heel to lift your body up and onto the box., Step back down with your right foot.', 'Barbell', 'Beginner'),
(178, 'Barbell Tap Pause Squat', 'Primary Quads Secondary Glutes', '', 'Overhand', 'Push', 'Begin by standing in front of a sturdy box or bench that is about knee-height, holding a barbell across your upper back and shoulders, using an overhand grip., Sit back and lower your body down until your glutes tap the top of the box, keeping your back straight and your core engaged., Push through your heels to extend your hips and stand up, bringing your body back to the starting position.', 'Barbell', 'Intermediate'),
(179, 'Barbell Zercher Squat', 'Primary Quads Secondary Glutes Tertiary Lower back', '', '', 'Push', 'Begin by standing with your feet about hip-width apart and holding a barbell in the crook of your arms, near your belly. Your palms should be facing towards your body., Sit back and lower your body down until your thighs are parallel to the ground, keeping your back straight and your core engaged., Push through your heels to extend your hips and stand back up to the starting position.', 'Barbell', 'Advanced'),
(180, 'Assisted Bulgarian Split Squat', 'Primary Quads Secondary Glutes', '', 'None', 'Push', 'Grab a stick of some sort or hold onto something stable (rack, bench)., Grab a box that is knee height or slightly lower., Hold the stick in your right arm and put your right leg on the box., Begin your squat by breaking at the knees. Get your knee close to the floor or all the way to the floor. Make sure to keep your chest tall.', 'Bodyweight', 'Beginner'),
(181, 'Bulgarian Split Squat', 'Primary Quads', '', '', '', 'Stand with your back to a bench (or raised surface) and place one of your feet on the bench., Squat down until your front leg is about parallel to the floor., Go back to the starting position. After completing the desired amount of reps, switch legs and repeat.', 'Bodyweight', 'Beginner'),
(182, 'Curtsy Lunge', 'Primary Quads Secondary Glutes', '', 'None', 'Push', 'Step your left foot diagonally behind your right leg, crossing it behind your body. Bend your knees and lower your hips until your left thigh is parallel to the floor and your right knee is hovering just above the ground., Push through your right foot to straighten your legs and return to the starting position., Repeat the movement with your other leg.', 'Bodyweight', 'Intermediate'),
(183, 'Forward Lunge', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/Y9zHge5sRj4', 'None', 'Push', 'Step forward with one leg and lower your body until your front thigh is parallel to the floor and your back knee almost touches the ground. Keep your chest up and your front foot flat on the floor., Push through your front foot to straighten your legs and return to the starting position., Repeat the movement with your other leg.', 'Bodyweight', 'Beginner'),
(184, 'Heels Up Squat', 'Primary Quads Secondary Glutes', '', 'None', 'Push', 'Place your heels on a plate or very low step., Keep your chest tall and initiate the squat by pushing your hips back and breaking at the knees., You should notice yourself able to squat lower than normal.', 'Bodyweight', 'Intermediate'),
(185, 'Lateral Lunge', 'Primary Quads Secondary Glutes', '', 'None', 'Push', 'Engage your core by drawing your belly button towards your spine. This will help to stabilize your body., Take a large step to the side with your right foot, planting it firmly on the ground., As you step, bend your right knee and lower your hips until your right thigh is parallel to the ground. Your left leg should remain straight., Push through your right heel to return to the starting position. Repeat on the opposite side by stepping out with your left foot.', 'Bodyweight', 'Intermediate'),
(186, 'Bodyweight Reverse Lunge', 'Primary Quads Hamstrings Secondary Glutes', '', 'None', 'Push', 'Push through your right heel to return to standing, bringing your left leg back to starting position., Repeat the movement on the other side, stepping backwards with your right leg and lowering your body until your right thigh is parallel to the ground and your left knee is bent at a 90-degree angle., Take a big step backwards with your left leg, lowering your body until your left thigh is parallel to the ground and your right knee is bent at a 90-degree angle.', 'Bodyweight', 'Beginner'),
(187, 'Split Squat', 'Primary Quads Secondary Glutes', '', 'None', 'Push', 'Take a split stance. Make sure to keep your chest tall., Bring your rear knee to the floor or close to it., Drive through your front heel and your rear foot back to the starting position.', 'Bodyweight', 'Beginner'),
(188, 'Dumbbell Bulgarian Split Squat', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Find a box or bench that is roughly knee height or a few inches lower. (The lower the box is the lower the mobility requirement)., You can either place your toes or the top of your foot on the box. Point your front foot forward or slightly turned outwards and break at the knees and hips simultaneously., Aim to get your front leg to hit parallel depth (hamstring parallel with the ground) or lower., The further forward your front foot is, the longer the Range of Motion is at your hips. The further back your front foot is, the longer the ROM at your knees.', 'Dumbbells', 'Advanced'),
(189, 'Dumbbell Forward Lunge', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/Y9zHge5sRj4', 'Neutral', 'Push', 'Stand with your feet hip-width apart and hold a dumbbell in each hand with your palms facing each other., Take a big step forward with your right foot, lowering your hips until your right thigh is parallel to the ground and your left knee is hovering just above the floor., Push through your right heel to straighten your right leg and return to the starting position.', 'Dumbbells', 'Intermediate'),
(190, 'Dumbbell Goblet Bulgarian Split Squat', 'Primary Quads Secondary Glutes', '', '', '', 'Holding the dumbbell in a goblet squat place your foot on a bench or box., Break at the hips and knees. Try to go to the point where your hamstring is parallel with the ground. Or until your back knee taps the floor., Make sure to keep your back flat and your elbows directly under your fists.', 'Dumbbells', 'Advanced'),
(191, 'Dumbbell Goblet Curtsy Lunge', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Stand with your feet hip-width apart and a dumbbell in a goblet position., Step your left foot behind and to the right of your right foot, crossing it behind your right leg., Bend your knees and lower your hips down and back, as if you were about to sit in a chair., Push through your right heel to straighten your right leg and return to the starting position.', 'Dumbbells', 'Advanced'),
(192, 'Dumbbell Goblet Forward Lunge', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/Y9zHge5sRj4', 'Neutral', 'Push', 'Hold a dumbbell in a goblet position., Step forward with one of your legs while bending at your back leg. Bring your back knee to the floor or close to the floor., Push through your front leg explosively to get back to your starting position.', 'Dumbbells', 'Intermediate'),
(193, 'Dumbbell Goblet Lateral Lunge', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Engage your core by drawing your belly button towards your spine. This will help to stabilize your body. Hold a dumbbell in a goblet position., Take a large step to the side with your right foot, planting it firmly on the ground., As you step, bend your right knee and lower your hips until your right thigh is parallel to the ground. Your left leg should remain straight., Push through your right heel to return to the starting position. Repeat on the opposite side by stepping out with your left foot.', 'Dumbbells', 'Advanced'),
(194, 'Dumbbell Goblet Reverse Lunge', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Hold a dumbbell in a goblet position., Take a big step backwards with your left leg, lowering your body until your left thigh is parallel to the ground and your right knee is bent at a 90-degree angle., Push through your right heel to return to standing, bringing your left leg back to starting position., Repeat the movement on the other side, stepping backwards with your right leg and lowering your body until your right thigh is parallel to the ground and your left knee is bent at a 90-degree angle.', 'Dumbbells', 'Beginner'),
(195, 'Dumbbell Goblet Split Squat', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Hold a dumbbell in a goblet position., Take a large step forward with one leg, and lower your body until your back knee nearly touches the ground. Your front knee should be bent at a 90-degree angle, and your back leg should be straight., Push through the heel of your front foot to raise your body back to the starting position.', 'Dumbbells', 'Intermediate'),
(196, 'Dumbbell Lateral Lunge Reach', 'Primary Quads Secondary Glutes Tertiary Lower back', '', 'Overhand Neutral', 'Push', 'Engage your core by drawing your belly button towards your spine. This will help to stabilize your body. Hold a dumbbell in a goblet position., Take a large step to the side with your right foot and reach toward the floor with your single dumbbell., As you step, bend your right knee and lower your hips until your right thigh is parallel to the ground. Your left leg should remain straight., Push through your right heel to return to the starting position. Repeat on the opposite side by stepping out with your left foot.', 'Dumbbells', 'Intermediate'),
(197, 'Dumbbell Reverse Lunge', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Hold two dumbbells by your side., Take a big step backwards with your left leg, lowering your body until your left thigh is parallel to the ground and your right knee is bent at a 90-degree angle., Push through your right heel to return to standing, bringing your left leg back to starting position., Repeat the movement on the other side, stepping backwards with your right leg and lowering your body until your right thigh is parallel to the ground and your left knee is bent at a 90-degree angle.', 'Dumbbells', 'Beginner'),
(198, 'Dumbbell Split Squat', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Hold a dumbbell in a goblet position., Take a large step forward with one leg, and lower your body until your back knee nearly touches the ground. Your front knee should be bent at a 90-degree angle, and your back leg should be straight., Push through the heel of your front foot to raise your body back to the starting position.', 'Dumbbells', 'Beginner'),
(199, 'Dumbbell Front Rack Pause Squat', 'Primary Quads Lower back Secondary Glutes', '', 'Neutral', 'Push', 'Place two dumbbells on your shoulders and make sure you force your elbows upward., Push your hips back and break at the knees., When you reach parallel (hamstrings parallel with the ground) pause for 1-3 seconds. Then push through your heels back to the starting position.', 'Dumbbells', 'Advanced'),
(200, 'Dumbbell Front Rack Squat', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Place two dumbbells on your shoulders and push your elbows upward., Push your hips back and break at the knees., Hit parallel depth and then drive through your heels back to the starting position.', 'Dumbbells', 'Intermediate'),
(201, 'Dumbbell Goblet Pulse Squat', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Hold a dumbbell in goblet position., Squat down to parallel depth and then halfway back up., Stay in this short range of motion for the duration of the set.', 'Dumbbells', 'Beginner'),
(202, 'Dumbbell Heels Up Goblet Squat', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Take a half kneeling stance. The dumbbell should start over your knee that is up., Rotate at the spine and point the dumbbell to the opposing side.', 'Dumbbells', 'Beginner'),
(203, 'Dumbbell Heels Up Narrow Goblet Squat', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Stand with your feet on a plate. Your feet should be very close to one another, but not touching., Begin your squat by pushing your hips back and breaking at the knees. Try to keep your chest tall while squatting.', 'Dumbbells', 'Advanced'),
(204, 'Dumbbell Leg Extension', 'Primary Quads', '', 'None', 'Push', 'Place a dumbbell between your feet. Lean back slightly and hold on to the bench., Extend your knees until you feel your quads contract hard.', 'Dumbbells', 'Beginner'),
(205, 'Dumbbell Offset Squat', 'Primary Quads Secondary Glutes Tertiary Obliques', '', 'Neutral', 'Push', 'Hold a dumbbell over one shoulder., Initiate the squat by pushing your hips back., Avoid doing any twisting. Try to keep your shoulders even.', 'Dumbbells', 'Intermediate'),
(206, 'Dumbbell Sumo Squat', 'Primary Quads Secondary Glutes Tertiary Lower back', '', 'Neutral', 'Push', 'Start by standing with your feet wider than shoulder-width apart and holding a dumbbell in each hand., Place the dumbbells on the ground between your feet., From this starting position, bend your knees and lower your body down into a squat., Make sure to keep your core engaged and your back straight throughout the movement.', 'Dumbbells', 'Beginner'),
(207, 'Dumbbell Thruster', 'Primary Quads Secondary Shoulders Tertiary Glutes', '', 'Neutral', 'Push', 'Start by standing with your feet shoulder-width apart and holding a dumbbell in each hand., Bring the dumbbells to your shoulders, with your palms facing inward and your elbows bent., From this starting position, dip your knees slightly and then quickly extend them as you press the dumbbells upwards., As you press the dumbbells upwards, also push your hips forward to help generate additional power., Slowly lower the dumbbells back down to the starting position, and repeat for the desired number of repetitions.', 'Dumbbells', 'Intermediate'),
(208, 'Plate Squat', 'Primary Quads', '', 'Neutral', 'Push', 'Stand with feet shoulder-width apart and hold a weight plate in front of your chest with both hands., Keep your back straight, lower your body as if you were sitting back into a chair., Go as low as you can while keeping your heels flat on the ground, then push through your heels to return to the starting position.', 'Plate', 'Intermediate'),
(209, 'Plate Squat Reach', 'Primary Quads Triceps Secondary Shoulders', '', '', 'Push', 'Stand holding a weight plate at chest height with both hands., Lower into a squat, extending the plate in front of you at chest height, As you stand back up, pull the plate back into your chest.', 'Plate', 'Advanced'),
(210, 'Plate Curtsy Lunge', 'Primary Quads Secondary Glutes', '', 'Neutral', '', 'Stand with feet shoulder-width apart and the plate in front of the torso., Step back and across the body with the right foot, bending both legs to lower into a lunge position., Push through the front foot to return to the starting position., Repeat on the other side by stepping back and across with the left foot., Continue alternating sides for desired reps.', 'Plate', 'Intermediate'),
(211, 'Plate Lateral Lunge', 'Primary Quads Secondary Glutes', '', 'Neutral', '', 'Stand with feet hip-width apart and the plate held in front of the torso., Take a step to the side with one foot, bending the knee and lowering the hips into a lunge., Push through the front foot to return to the starting position., Repeat on the other side by stepping to the other side with the other foot.', 'Plate', 'Beginner'),
(212, 'Plate Forward Lunge', 'Primary Quads Secondary Glutes', '', 'Neutral', '', 'Stand with feet hip-width apart and the plate held in front of the torso., Take a step forward with one foot, bending the front knee to lower the hips into a lunge., Push through the front foot to return to the starting position., Repeat on the other side by stepping forward with the other foot.', 'Plate', 'Beginner'),
(213, 'Plate Reverse Lunge Twist', 'Primary Quads Triceps Secondary Glutes Obliques', '', '', '', 'Step back with your right foot into a lunge position, both knees bent at 90 degrees., Twist your torso to the right, keeping the plate close to your chest., Push through your front foot to return to the starting position and repeat on the other side.', 'Plate', 'Intermediate'),
(214, 'Plate Reverse Lunge', 'Primary Quads', '', '', '', 'Hold the plate at chest height close to your body, Take a big step backwards with your left leg, lowering your body until your left thigh is parallel to the ground and your right knee is bent at a 90-degree angle., Push through your right heel to return to standing, bringing your left leg back to starting position., Repeat the movement on the other side, stepping backwards with your right leg and lowering your body until your right thigh is parallel to the ground and your left knee is bent at a 90-degree angle.', 'Plate', 'Beginner'),
(215, 'Band Leg Extension', 'Primary Quads', '', 'None', 'Push', 'Anchor a band to something stable and set up a bench., Run through the band underneath the bench and loop around your feet., Flex and extend your knees.', 'Band', 'Beginner'),
(216, 'Plate Overhead Squat', 'Primary Quads', '', '', '', '\"Hold a weight plate at arms length overhead', 'Plate', 'Intermediate'),
(217, 'Plate Deficit Lunge', 'Primary Quads Secondary Glutes', '', 'None', 'Push', 'Stand on a plate with both feet., Step back with one foot to the floor and let your back knee get close to or tap the floor., Push through your front leg until both feet are back on the plate.', 'Plate', 'Advanced'),
(218, 'Plate Overhead Walking Lunge', 'Primary Quads Secondary Glutes Tertiary Shoulders', '', 'Neutral', 'Push', 'Stand tall with a plate overhead., Step with one foot forward bringing your back knee to the floor. Gently tap your knee then drive through your front leg., Go back to a standing position and then step forward with the opposing legs.', 'Plate', 'Intermediate'),
(219, 'Plate Squat Hold', 'Primary Quads Glutes', '', 'Neutral', 'Hold', 'Start by standing with your feet shoulder-width apart, with a weight plate held in both hands at chest height., Lower your body by bending your knees and hips, keeping your back straight and your weight in your heels., Go down until your thighs are parallel to the floor, then pause for a moment., Hold the squat position for the desired amount of time, keeping the weight plate steady and your core engaged.', 'Plate', 'Beginner'),
(220, 'Plate Thruster', 'Primary Quads Secondary Shoulders Tertiary Glutes', '', 'Overhand', 'Push', 'Start with the plate at chest height. Begin a squat., Make sure to hit parallel depth. On your way back up, use the momentum you generate with your legs to help push the plate into an overhead position., Let the plate return to chest height before initiating the next squat.', 'Plate', 'Intermediate'),
(221, 'Dumbbell Overhead Squat', 'Primary Quads Secondary Glutes Tertiary Shoulders', '', 'Neutral', 'Push', 'Stand with your feet slightly wider than shoulder-width apart and hold two dumbbells overhead with both hands, using an overhand grip., Begin the squat by bending at the hips and knees, keeping your core tight and the dumbbells balanced overhead., Lower your body as far as you can while keeping the dumbbells overhead, then push back up to the starting position., Repeat for the desired number of repetitions, making sure to keep your back straight, chest lifted, and the dumbbells stable overhead throughout the entire movement.', 'Dumbbells', 'Advanced'),
(222, 'Single Arm Overhead Squat', 'Primary Quads Secondary Shoulders Tertiary Glutes', '', '', 'Push', 'Stand with your feet shoulder-width apart and a dumbbell in your right hand, extending it overhead., Inhale and begin to squat down, keeping the dumbbell overhead and your back straight., Go down as far as you can while still maintaining control, then exhale and return to standing position, keeping the dumbbell overhead. Repeat for desired reps and then switch to the other arm.', 'Dumbbells', 'Advanced'),
(223, 'TRX Curtsy Lunge', 'Primary Quads Secondary Glutes', '', 'Overhand Neutral', 'Push', 'Stand facing away from the anchor point, holding onto the TRX handles with both hands., Step your left foot behind your right, crossing it behind your right leg, and lower yourself into a curtsy lunge., Push through your right foot to return to the starting position and repeat the movement with the other leg.', 'TRX', 'Intermediate'),
(224, 'TRX Jumping Squat', 'Primary Quads Secondary Glutes', '', 'Overhand Neutral', 'Push', 'Stand facing the TRX straps and place your feet shoulder-width apart., Begin by lowering into a squat position by bending at the knees and hips, keeping your back straight and chest up., Explosively jump up off the ground, using your arms for momentum if desired., Land softly back in the squat position, making sure to absorb the impact with your legs.', 'TRX', 'Intermediate'),
(225, 'TRX Lateral Lunge', 'Primary Quads Secondary Glutes', '', 'Overhand Neutral', 'Push', 'Grasp the TRX handles with both hands and extend your arms straight in front of you., Step laterally to one side, keeping your arms extended and your core engaged., Lower your body into a lunge position, keeping your front knee bent at a 90-degree angle and your back leg straight., Push off your front foot to return to the starting position, keeping your arms extended and your core engaged.', 'TRX', 'Intermediate'),
(226, 'TRX Reverse Lunge', 'Primary Quads Secondary Glutes', '', 'Overhand Neutral', 'Push', 'Hold on to the TRX straps and step away until the strap is taut., Step backwards with one leg in to a lunge position and gently tap your knee to the floor., Lunge back to the starting position and repeat with the other leg.', 'TRX', 'Beginner'),
(227, 'TRX Split Squat', 'Primary Quads Secondary Glutes', '', 'Overhand Neutral', 'Push', 'Hold the two TRX straps and step away until the straps are taut. Take a split stance., Bring your back knee to the floor and gently tap the floor., Use your arms and the TRX to help assist you getting back into the starting position.', 'TRX', 'Beginner'),
(228, 'TRX Squat', 'Primary Quads Secondary Glutes', '', 'Overhand Neutral', 'Push', 'Hold the two TRX straps and lean away from the anchor point., Flex at the hips and knees in to a squat position., You can use your arms and the straps to assist you back into the starting position.', 'TRX', 'Beginner'),
(229, 'Dumbbell Glute Bridge', 'Primary Quads Hamstrings Glutes', '', '', '', 'Lay flat on the ground with both feet planted. Make sure you keep your heels glued to the floor., Place the dumbbell on your hips. Push your hips toward the ceiling until you feel your glutes contract., Then return to the floor.', 'Dumbbells', 'Beginner'),
(230, 'Barbell Sumo Deadlift', 'Primary Glutes Secondary Quads Tertiary Lower back', 'https://www.youtube.com/embed/C6aXGfIilEE', '', '', 'Stance width can vary with a sumo DL. Some prefer toes near the plates. Others prefer a more narrow stance to get more drive off the floor., Make sure your arms are inside your legs. The bar should be just over your mid-foot. Flatten your back and push your heels into the floor., Try to extend your knees and hips simultaneously.', 'Barbell', 'Intermediate'),
(231, 'Burpee', 'Primary Chest Abdominals Secondary Quads Tertiary Glutes', '', 'Overhand', 'Push', 'Start standing with your feet hip-width apart. Lower your body into a squat position, placing your hands on the ground in front of you., Quickly jump your feet back into a plank position, keeping your body in a straight line from your head to your toes., Perform a push-up, keeping your core tight and your elbows close to your body., Jump your feet back towards your hands, and then jump up into the air, reaching your arms overhead., Land softly and immediately lower your body back into the squat position to repeat the movement.', 'Bodyweight', 'Intermediate'),
(232, 'Plate Squat Hold Reach', 'Primary Triceps Shoulders Secondary Quads Glutes', '', '', '', 'Hold the plate with both hands at chest height., Keeping your core tight and your chest up, squat down as low as you can go while keeping your feet flat on the ground., Hold the squat position and reach the plate forward, keeping your arms straight and your back straight., Return the plate to your chest while you hold the squat position and repeat reaching the plate in front of you', 'Plate', 'Intermediate'),
(233, 'Plate Squat Hold Curl', 'Primary Biceps Secondary Quads Glutes', '', '', '', 'Lower into a squat position, keeping the plate at chest height., Slowly curl the plate towards your shoulders, keeping your elbows close to your body., Hold for a moment at the top of the curl before lowering the plate back to chest height., Repeat the curls as you hold the squat position.', 'Plate', 'Intermediate'),
(234, 'Plate Single Leg Deadlift', 'Primary Quads Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Engage your core and straighten your legs to lift the plate off the ground., Stand on one leg, keeping the other leg off the ground, while holding the plate in front of your body., Keep your back straight and maintain a neutral head position while looking straight ahead., Lower the plate back down to the ground while keeping your balance on one leg.', 'Plate', 'Advanced'),
(235, 'Crescent Moon Pose', 'Primary Quads', '', '', 'Hold', '\"Begin in Runners Lunge pose with your right foot forward.\"\"', 'Yoga', 'Beginner'),
(236, 'Crescent Moon Pose Quad Stretch', 'Primary Quads', '', '', 'Hold', 'Begin in Crescent Moon Pose with your right foot forward and a block nearby., Place the block at the highest height under your left hand., Reach your left arm behind you and take hold of your left foot or ankle., Gently pull your foot towards your buttocks while pressing your left hand into the block to deepen the stretch in your left quad. Hold for a few breaths before releasing and repeating on the other side.', 'Yoga', 'Intermediate'),
(237, 'Crescent Moon Pose Quad Stretch With Block', 'Primary Quads', '', '', 'Hold', 'Begin in Crescent Moon Pose with your right foot forward and a block nearby., Place the block at the highest height under your left hand., Reach your left arm behind you and take hold of your left foot or ankle., Gently pull your foot towards your buttocks while pressing your left hand into the block to deepen the stretch in your left quad. Hold for a few breaths before releasing and repeating on the other side.', 'Yoga', 'Beginner');
INSERT INTO `workout` (`ID`, `exercise_name`, `target`, `youtubeURL`, `Grips`, `Force`, `steps`, `Category`, `Difficulty`) VALUES
(238, 'Lord Of The Dance', 'Primary Quads', '', '', 'Hold', 'Begin by standing tall with your feet hip-width apart., Shift your weight onto your left foot and bring your right heel towards your glute., Reach your right arm back and grab onto the inside of your right foot or ankle., Inhale and lift your right foot away from your glute while extending your left arm forward. Hold for 5-10 breaths before switching sides.', 'Yoga', 'Intermediate'),
(239, 'Monkey Pose', 'Primary Quads Secondary Hamstrings', '', '', 'Hold', 'Begin in a low lunge with your right foot forward and your left knee on the ground., Slowly slide your left knee back while extending your right leg forward, keeping both legs straight., Lower your body down to the ground, using your hands for support if needed., Hold the pose for a few breaths, then switch sides and repeat.', 'Yoga', 'Advanced'),
(240, 'Monkey Pose With Blocks', 'Primary Quads Secondary Hamstrings', '', '', 'Hold', 'Begin in tabletop position, with hands and knees on the ground., Place yoga blocks shoulder-width apart under your hands., Shift your hips back towards your heels, while keeping your arms straight., Hold the pose for several deep breaths, feeling a stretch in your shoulders and upper back. Release the pose and return to tabletop position.', 'Yoga', 'Intermediate'),
(241, 'Runners Lunge', 'Primary Quads', '', '', 'Hold', 'Begin in Downward Facing Dog pose (Adho Mukha Svanasana)., Step your right foot forward between your hands., Place your left knee on the ground behind you and slide it back until you feel a stretch in your left hip flexor., Keep your hands on the ground or lift them to the sky and hold for a few breaths before repeating on the other side.', 'Yoga', 'Beginner'),
(242, 'Chair Pose', 'Primary Quads Secondary Shoulders', '', '', 'Hold', 'Stand straight with your feet hip-width apart and arms at your sides., Inhale and raise your arms straight up over your head., Exhale and bend your knees, lowering your hips as if you were sitting in a chair., Hold the pose for a few breaths, then inhale and stand up straight.', 'Yoga', 'Beginner'),
(243, 'Ball Headlock Squat', '', '', '', '', '', 'MedicineBall', ''),
(244, 'Dumbbell Seated Shrug', 'Primary Traps', 'https://www.youtube.com/embed/sgcOZ3wcWmI', 'Neutral', 'Pull', 'Sit on a bench with dumbbells in both hands, palms facing your body, back straight., Elevate your shoulders and hold the contracted position at the apex of the motion., Slowly lower your shoulders back to starting position.', 'Dumbbells', 'Beginner'),
(245, 'Smith Machine Standing Shrugs', 'Primary Traps', '', 'Overhand', 'Pull', 'Place the bar on a lower rung so that when gripped your arms are fully extended and your back is straight., Grip at shoulder width, raise the bar with your shoulders and pause at the contracted position., Slowly lower the bar back to starting position.', 'Machine', 'Beginner'),
(246, 'Kettlebell Shrug', 'Primary Traps', '', '', '', 'Standing up straight with feet shoulder width apart., Holding the kettlebell in both hands in front of your pelvis, engage your shoulder blades, as if you are trying to touch them together., Release the shrug and repeat', 'Kettlebells', 'Beginner'),
(247, 'Kettlebell Upright Row', 'Primary Traps', 'https://www.youtube.com/embed/v7nsz05s9oM', '', '', 'Stand with your feet shoulder width apart holding the kettlebell with both hands in front of your thighs., Bend forward at the hips bringing the kettlebell to the floor while you slightly bend your knees, keeping your back straight., Lift the kettlebell upwards towards your chest and lower - repeat.', 'Kettlebells', 'Beginner'),
(248, 'Kettlebell Silverback Shrug', 'Primary Traps Secondary Mid back', '', '', '', 'Stand with your feet shoulder width apart holding the kettlebell with both hands in front of your thighs., Bend forward at the hips bringing the kettlebell to the floor while you slightly bend your knees, keeping your back straight., Holding the kettlebell in both hands engage your shoulder blades, as if you are trying to touch them together. Release the shrug.', 'Kettlebells', 'Intermediate'),
(249, 'Traps Stretch Variation Three', 'Primary Traps', '', 'None', '', 'Stand upright with your feet shoulder width apart., Nod your head forward, bringing your chin to your chest., You will feel the stretch across your neck.', 'Stretches', 'Beginner'),
(250, 'Traps Stretch Variation Two', 'Primary Traps', '', 'None', '', 'Keeping your chest facing forward, turn your head 90 degrees to the left. Then return to centre point., Stand upright with your feet shoulder width apart., Repeat, turning your head in the other direction.', 'Stretches', 'Beginner'),
(251, 'Traps Stretch Variation One', 'Primary Traps', '', 'None', '', 'Stand upright with your feet shoulder width apart., Place your left hand on your head and gently pull your head down towards your left shoulder. Then return to centre point., Repeat, using your right hand pulling towards your right shoulder.', 'Stretches', 'Beginner'),
(252, 'Elevated Pike Shoulder Shrug', 'Primary Traps Shoulders', '', '', '', 'Use a bench or an object like a chair to elevate your feet., Slowly lower your body (scapula) while keeping your elbows locked (this is key), Slowly raise your body back to the start position.', 'Bodyweight', 'Beginner'),
(253, 'Barbell Silverback Shrug', 'Primary Traps', '', 'Overhand Mixed', 'Pull', 'Stand with your feet shoulder width apart holding the Barbell with both hands in front just past shoulder width., Bend forward at the hips with a slight bend in your knees, keeping your back straight., Engage your shoulder blades, as if you are trying to touch them together., Release the shrug.', 'Barbell', 'Intermediate'),
(254, 'Cable 30 Degree Shrug', 'Primary Traps', '', 'Neutral', 'Pull', 'Use a handle attachment on both sides of crossover. With cable attachment set all the way to the bottom., Grab both handles and make sure you are centered in the machine. Let your arms hang freely., Pull your shoulder blades up and in towards your ears. Let your shoulder blades depress back to the starting position.', 'Cables', 'Beginner'),
(255, 'Band Shrug', 'Primary Traps', '', '', '', 'Place the band under your feet. Stand upright., Elevate your shoulder blades and then depress back to the starting position.', 'Band', 'Beginner'),
(256, 'Dumbbell Shrug', 'Primary Traps', '', 'Neutral', 'Pull', 'Stand tall with two dumbbells. Pull your shoulder blades up. Give a one second squeeze at the top.', 'Dumbbells', 'Beginner'),
(257, 'Barbell Shrug', 'Primary Traps', '', 'Overhand', 'Pull', 'Start by standing with your feet about hip-width apart, with a barbell in front of you., Bend down and grab the barbell with an overhand grip, your hands slightly wider than shoulder-width apart. Keep your back straight and your shoulders down as you grip the bar., Stand up straight, lifting the barbell off the ground and pulling it toward your hips. Keep your elbows close to your body and your shoulders down as you do this., Once the barbell is at your hips, raise your shoulders as high as you can, holding the position for a moment.', 'Barbell', 'Beginner'),
(258, 'Dumbbell Laying 30 Degree Shrug', 'Primary Traps', '', 'Overhand Neutral', 'Pull', 'Lay sideways on a bench letting your arm hang freely., Pull your shoulder blade and joint toward the ceiling.', 'Dumbbells', 'Intermediate'),
(259, 'Plate Overhead Carry', 'Primary Traps Abdominals Secondary Shoulders', '', 'Neutral', 'Hold', 'Start by standing upright with your feet shoulder-width apart. Hold a single weight plate overhead with both hands, keeping your arms extended straight above your head., Begin walking forward, taking slow and steady steps while maintaining a straight line. Your focus should be on maintaining balance and control of the weight plate overhead., As you walk, continue to keep your arms extended and the weight plate overhead. Try to maintain good posture and avoid leaning forward or backward, as this could result in losing control of the weight plate., Continue walking for a set distance or for a certain amount of time, keeping your focus on proper form and technique. When finished, return to the starting position and repeat as desired.', 'Plate', 'Beginner'),
(260, 'Plate Overhead Shrug', 'Primary Traps', '', 'Neutral', 'Push', 'Stand tall with a weight plate overhead., Elevated and depress your scapula., Avoid leaning forward or backwards to keep pressure off of your lumbar spine.', 'Plate', 'Advanced'),
(261, 'Kettlebell Incline Shrug', 'Primary Mid back Secondary Traps', '', 'Overhand', 'Pull', 'Leaning across the back section of the bench, with feet firmly on the floor and with arms hanging to the sides., Holding the kettlebell in both hands engage your shoulder blades, as if you are trying to touch them together. Release the shrug.', 'Kettlebells', 'Intermediate'),
(262, 'Elevated Pike Press', 'Primary Shoulders Mid back Secondary Traps Tertiary Triceps', '', 'Overhand', 'Push', 'Use a bench or an object to elevate your feet., Lower your head towards the floor by bending your elbows, Push through your hands and return to the starting pike position., Repeat', 'Bodyweight', 'Intermediate'),
(263, 'Cable Upright Row', 'Primary Shoulders Secondary Traps', 'https://www.youtube.com/embed/v7nsz05s9oM', 'Underhand', '', 'Use a handle attachment with the cable set to the bottom of the machine., Pull your elbows straight up toward the ceiling until the attachment is just below your chin then extend until your elbows are fully extended.', 'Cables', 'Advanced'),
(264, 'Cable Silverback Shrug', 'Primary Mid back Secondary Traps', '', 'Overhand', '', 'Use a bar attachment. Cable should be set all the way to the bottom., Stand up with the attachment in hand, walk a couple of steps back make sure you are standing upright and not leaning backwards., Retract your shoulder blades and then protract to return to the starting position.', 'Cables', 'Beginner'),
(265, 'Band Horizontal Shrug', 'Primary Traps Shoulders Mid back', '', '', '', 'Anchor the band at shoulder height. Take a few steps away until the band is taut., Retract your shoulder blades straight back in schools. Then protract to the starting position.', 'Band', 'Advanced'),
(266, 'Band Single Arm Overhead Press', 'Primary Shoulders Secondary Triceps Tertiary Traps', '', '', '', 'Loop the band under one of your feet. The other end of the bed should be in your hand between your thumb and pointer finger., Press the band straight up towards the ceiling. Until your elbow is fully extended.', 'Band', 'Intermediate'),
(267, 'Barbell Upright Row', 'Primary Shoulders Secondary Traps', 'https://www.youtube.com/embed/v7nsz05s9oM', 'Overhand', 'Pull', 'Take a double overhand roughly shoulder width grip., Pull your elbows straight up the ceiling. Aim to get the bar to chin level or slightly higher.', 'Barbell', 'Advanced'),
(268, 'Barbell Z Press', 'Primary Shoulders Secondary Traps Tertiary Triceps', '', 'Overhand', 'Push', 'Begin by sitting on the ground with your legs extended and a barbell in your hands, with your palms facing forward and your elbows bent., Keeping your core engaged and your back straight, lift the barbell up from your chest and press it straight up above your head., Lower the barbell back down to your chest.', 'Barbell', 'Advanced'),
(269, 'Dumbbell Laying Silverback Shrug', 'Primary Mid back Secondary Traps', '', 'Overhand Neutral', 'Pull', 'Lay on an incline bench with your arms hanging freely., Pull your shoulder blades back toward the ceiling.', 'Dumbbells', 'Intermediate'),
(270, 'Dumbbell Silverback Shrug', 'Primary Mid back', '', '', '', 'Hinge forward slightly at the hips. Make sure to keep a flat back. Let your arms hang in front of you., Let your scapula protract, and then retract them. Imagine you have a pencil in your mid-back and squeeze it between your shoulder blades.', 'Dumbbells', 'Intermediate'),
(271, 'TRX Y Raise', 'Primary Shoulders Secondary Traps Tertiary Mid back', '', 'Overhand', 'Pull', 'Hold the TRX straps and lean away from the anchor point until the straps are taut., Keep both elbows mostly extended and raise both arms out and upwards., You should make a Y shape with your arms during the back portion of the exercise.', 'TRX', 'Beginner'),
(272, 'Criss Cross Bow Tie Pose', 'Primary Traps Secondary Shoulders Tertiary Mid back', '', '', 'Hold', 'Stand with your feet hip-width apart and your arms at your sides., Cross your right arm over your left arm, bringing your palms together in front of your chest., Lower your arms down so that your palms face down towards the ground, and your right elbow is resting on top of your left elbow., Hold for a few breaths, then switch sides.', 'Yoga', 'Beginner'),
(273, 'Eagle Arms Chin Into Chest', 'Primary Traps Secondary Mid back Tertiary Shoulders', '', '', 'Hold', 'Sit cross-legged on the floor and bring your arms out to the sides at shoulder height., Cross your right arm over your left, bending both elbows so that your forearms are parallel to the floor., Lift your elbows towards the ceiling and bring your hands together, then drop your chin towards your chest., Hold for a few breaths, then release your arms and repeat on the other side.', 'Yoga', 'Beginner'),
(274, 'Easy Pose Neck Stretch', 'Primary Traps', '', '', 'Hold', 'Begin by sitting cross-legged on the floor., Slowly drop your chin towards your chest, feeling a stretch in the back of your neck., Inhale and lift your head up, then exhale and drop your right ear towards your right shoulder., Hold for a few breaths, then switch sides and repeat the stretch.', 'Yoga', 'Beginner'),
(275, 'Half Neck Rolls', 'Primary Traps', '', '', 'Hold', 'Sit cross-legged on the floor and interlace your fingers behind your head., Drop your chin to your chest and roll your head to the right, bringing your right ear towards your right shoulder., Slowly roll your head back to center, then over to the left so that your left ear is close to your left shoulder., Repeat the movement from side to side, keeping your shoulders relaxed and breathing deeply.', 'Yoga', 'Beginner'),
(276, 'Thread The Needle Hip Over Knees', 'Primary Traps Secondary Obliques', '', '', 'Hold', 'Start on your hands and knees in a tabletop position., Slide your right knee up between your hands and place your right foot close to your left wrist., Take your left leg back and lower it down to the ground, keeping your toes tucked., Twist your torso to the right and reach your right arm underneath your left arm, threading the needle. Hold for a few breaths, then switch sides.', 'Yoga', 'Intermediate'),
(277, 'Dips narrow elbows', 'Primary Triceps', '', '', '', 'Hold your body at arms length above the equipment, Inhale and slowly lower yourself downward. Keep your elbows close to your body and your torso upright., Exhale, and using your triceps raise your body back to the starting position.', 'Bodyweight', 'Advanced'),
(278, 'Dumbbell Seated Overhead Tricep Extension', 'Primary Triceps', '', '', '', 'Sit on the bench and hold a dumbbell with both hands. Raise the dumbbell overhead at arms length, holding the weight up with the palms of your hands., Keep your elbows in while you lower the weight behind your head, your upper arms stationary., Raise the weight back to starting position.', 'Dumbbells', 'Intermediate'),
(279, 'Machine Cable V Bar Push Downs', 'Primary Triceps', '', '', '', 'Grip the pulley bar with palms facing down at shoulder width., Stand straight with a small forward incline. Keep your upper arms close to your body, and slowly bring the pulley bar down until your arms are fully extended., Pause when you are at the contracted position of the motion, then slowly raise the pulley bar back to the starting point.', 'Machine', 'Beginner'),
(280, 'Bench Dips', 'Primary Triceps', '', 'Overhand', 'Push', 'Grip the edge of the bench with your hands, Keep your feet together and legs straight., Lower your body straight down., Slowly press back up to the starting point., TIP: Make this harder by raising your feet off the floor and adding weight.', 'Bodyweight', 'Beginner'),
(281, 'Barbell Laying Triceps Extensions', 'Primary Triceps', '', '', '', 'Lay on a flat bench while holding a barbell with a shoulder-width grip., Fully extend your elbows until the barbell is directly over your chest., Begin to flex your elbows and allow the barbell to nearly touch your forehead., Extend your elbows back to the starting position and repeat.', 'Barbell', 'Intermediate'),
(282, 'Kettlebell Skull Crusher', 'Primary Triceps', '', '', '', 'Lie on your back with knees in the air and soles of your feet flat on the floor., Hold the kettlebell in both hands directly above your chest., Keeping your upper arms in place, bend your elbows to slowly bring the kettlebell in front of your forehead., Return to the starting position and repeat.', 'Kettlebells', 'Advanced'),
(283, 'Kettlebell Standing Tricep Extension', 'Primary Triceps', '', '', '', 'Stand with legs shoulder width apart., Hold the kettlebell in both hands behind your head., Straighten arms bringing kettlebell above your head., Return to the starting position and repeat.', 'Kettlebells', 'Beginner'),
(284, 'Kettlebell Decline Skull Crusher', 'Primary Triceps', '', 'Neutral', 'Push', 'Lie on the floor with your upper back and soles of feet on the floor, knees bent, and pelvis and lower back suspended in the air., Hold the kettlebell in both hands directly above your chest., Keeping your upper arms in place, bend your elbows to bring the kettlebell to the floor above your head., Return the kettlebell to the starting position and repeat.', 'Kettlebells', 'Advanced'),
(285, 'Kettlebell Tate Press', 'Primary Triceps', '', '', '', 'Lie flat on your back. Keeping your arm straight, hold the kettlebell above your chest with your palm facing away from your body., Bend your arm, touching the kettlebell to the top of the shoulder opposite to the arm being worked., Return to the starting position and repeat.', 'Kettlebells', 'Intermediate'),
(286, 'Triceps Stretch Variation Three', 'Primary Triceps', '', 'None', '', 'Bend your left arm so your hand is at your shoulder, and hold your elbow with the opposite hand., Gently push on your elbow so it is raised by your head., Hold the stretch for a few seconds then repeat with the other arm.', 'Stretches', 'Beginner'),
(287, 'Triceps Stretch Variation Two', 'Primary Triceps', '', 'None', '', 'Hold a band so it is in line with your spine. Hold the top with your right hand and the bottom with your left., Keeping the top still, pull down on the band., Hold the stretch for a few seconds then repeat with the other arm.', 'Stretches', 'Beginner'),
(288, 'Triceps Stretch Variation One', 'Primary Triceps', '', 'None', '', 'Use the opposite hand to gently apply pressure to the elbow., Hold the stretch for a few seconds then repeat with the other arm., Raise your left arm above your head, and bend at the elbow so your hand is resting at the top of your back.', 'Stretches', 'Beginner'),
(289, 'Diamond Push Ups', 'Primary Triceps', '', 'Neutral', 'Push', 'Position your index fingers and thumbs so they are touching, forming a diamond shape, Use a standard push up position, Lower your chest towards your hands, keep your elbows close to your body, Stop just before your chest touches the floor, then push back up to the starting position.', 'Bodyweight', 'Beginner'),
(290, 'Barbell Close Grip Bench Press', 'Primary Triceps Secondary Chest', '', 'Overhand', 'Push', 'Lay flat on the bench with your feet on the ground. With a narrow grip on the bar, straighten your arms, Lower the bar to your lower-mid chest, \"Slowly raise the bar until youve locked your elbows.\"\"\"', 'Barbell', 'Intermediate'),
(291, 'Cable Push Down', 'Primary Triceps', '', 'Neutral', 'Push', 'You can use any attachment for this. The cable should be set all the way at the top of the machine., Make sure to keep your upper arm glued at your side. Extend your elbows until you feel your triceps contract.', 'Cables', 'Beginner'),
(292, 'Cable Cross Pushdown', 'Primary Triceps', '', 'Neutral', '', 'Either use two handles or no attachment at all. Both sides of the cable crossover should be set to the top., Stand in the center of the machine holding each Cable in the opposing hand. Your upper arm should be at a 45 degree angle with your torso., Extend your elbows until you feel a contraction and then return to the starting position', 'Cables', 'Advanced'),
(293, 'Cable Skullcrusher', 'Primary Triceps', '', 'Neutral', '', 'You can use a straight bar, rope, easy bar, or two handles. The cable should be set all the way to the top of the machine., Face away from the machine with your elbows up and the attachment just behind your head., Extend at the elbows until you feel a contraction in your triceps and then flex at the elbow back to the starting position.', 'Cables', 'Intermediate'),
(294, 'Cable Overhead Tricep Extension', 'Primary Triceps', '', 'Neutral', 'Push', 'You can use any attachment. The cable should be all the way to the bottom of the machine. Face away from the machine., Point your elbows straight up toward the ceiling., From there, extend your elbows until your fists are pointed straight toward the ceiling.', 'Cables', 'Intermediate'),
(295, 'Band Overhead Tricep Extension', 'Primary Triceps', '', '', '', 'Stand with one or both feet on band. Go up and over your head with the band to the starting position., Flex and extend your elbows.', 'Band', 'Advanced'),
(296, 'Band Pushdown', 'Primary Triceps', '', '', '', 'Place the band just above eye level. Take a few steps away until the band is taut., Push your hips back slightly. Extend and flex at the elbows.', 'Band', 'Beginner'),
(297, 'Band Skullcrusher', 'Primary Triceps', '', '', '', '', 'Band', 'Advanced'),
(298, 'Dumbbell Skullcrusher', 'Primary Triceps', '', 'Neutral', 'Push', 'Lay flat on the floor or a bench with your fists extended to the ceiling and a neutral grip., Break at the elbows until your fists are by your temples. Then extend your elbows and flex your triceps at the top.', 'Dumbbells', 'Intermediate'),
(299, 'Barbell Skullcrusher', 'Primary Triceps', '', '', '', '\"Take a shoulder width grip. Break at the elbows. Try to keep your elbows tucked in. Dont let them flare out.\"\"', 'Barbell', 'Intermediate'),
(300, 'Barbell Overhead Tricep Extension', 'Primary Triceps', '', '', '', 'Be careful getting the bar into place overhead. Push your elbows forward while keeping your elbows tucked in., Look for what feels like a stretch in your triceps. Extend your elbows until your fists are pointed at the ceiling.', 'Barbell', 'Advanced'),
(301, 'Dumbbell Decline Skullcrusher', 'Primary Triceps', '', 'Neutral', 'Push', 'Start by lying on a decline bench with a dumbbell in each hand., Hold the dumbbells above your chest with your arms extended and your palms facing each other. This is the starting position., Slowly lower the dumbbells towards your forehead by bending your elbows. Keep your upper arms stationary and only move your forearms., Once the dumbbells are close to your forehead, reverse the motion by extending your elbows and returning to the starting position.', 'Dumbbells', 'Beginner'),
(302, 'Dumbbell Rolling Tricep Extension', 'Primary Triceps', '', 'Neutral', 'Push', 'Lay flat on a bench with two dumbbells pointed toward the ceiling., Break at the elbows until your forearms touch your biceps, then flex at the shoulder joint bringing your upper arms to the sides of your head., Bring your elbows back up until they are pointed at the ceiling, then extend your elbows.', 'Dumbbells', 'Advanced'),
(303, 'Dumbbell Overhead Tricep Extension', 'Primary Triceps', '', 'Underhand Neutral', 'Push', '', 'Dumbbells', 'Beginner'),
(304, 'Dumbbell Tricep Kickback', 'Primary Triceps', '', 'Neutral', 'Push', 'Start by standing with your feet shoulder-width apart and holding a dumbbell in one hand., Bend at the waist and place your opposite hand on your knee for support., From this starting position, extend your arm backwards so that the dumbbell is behind your body., Make sure to keep your elbow close to your body and your core engaged throughout the movement.', 'Dumbbells', 'Beginner'),
(305, 'Plate Curl To Overhead Press To Tricep Extension', 'Primary Biceps Triceps Secondary Shoulders', '', 'Neutral', '', 'Stand with feet shoulder-width apart and the plate in front of the torso, palms facing forward., Curl the plate up to the front of the shoulder while keeping the elbows close to the body., Rotate the palms to face forward and press the plate overhead, locking out the arms., Lower the plate back behind the head to the starting position of a tricep extension., Push the plate back up overhead, then lower it back to the starting position and repeat for desired reps.', 'Plate', 'Advanced'),
(306, 'Plate Overhead Tricep Extension', 'Primary Triceps', '', '', '', 'Stand or sit with a weight plate in your hands, arms extended straight above your head, Lower the weight plate behind your head by bending at the elbows., Keep your elbows close to your head and shoulders relaxed., Push the weight plate back up to starting position, extending your arms fully.', 'Plate', 'Beginner'),
(307, 'Band Rapunzel Pushdown', 'Primary Triceps', '', 'Neutral', 'Push', 'Anchor the band to a high point and face away. Hold the band with both hands, placing your hands shoulder-width apart and keeping your elbows close to your body., Slowly extend your arms downward, keeping your elbows close to your body. Push the band down as far as you can, feeling the contraction in your triceps.', 'Band', 'Beginner'),
(308, 'Plate Overhead Press To Tricep Extension', 'Primary Triceps Secondary Shoulders', '', '', '', 'Brace your core and press the weight plate overhead until your arms are fully extended. Keep your shoulders down and back., While your arms are fully extended, lower the weight behind your head to perform the extension, After completing the extension, lower the weight to your chest, and perform the overhead press. Repeat', 'Plate', ''),
(309, 'Band Single Arm Pushdown', 'Primary Triceps', '', 'Neutral', 'Push', 'Stand with your feet shoulder-width apart and hold the band with your right hand, with your arm at your side., Flex and extend your elbow.', 'Band', 'Beginner'),
(310, 'Single Arm Rapunzel Pushdown', 'Primary Triceps', '', 'Neutral', 'Push', 'Stand with a band anchored to a high point. Face away from the band. Hold the loop in one hand., \"Keep your elbow tucked in to your side. Extend your elbow. When you extend', 'Band', 'Beginner'),
(311, 'Band Single Arm Skullcrusher', 'Primary Triceps', '', 'Neutral', 'Push', 'Stand facing away from a band which is anchored to a high point., Keep your elbow pointed forward, and extend and flex your elbow.', 'Band', 'Intermediate'),
(312, 'Dumbbell Single Arm Overhead Tricep Extension', 'Primary Triceps', '', 'Neutral', 'Push', 'Stand tall with a single dumbbell extended overhead., Flex at the elbow letting the dumbbell travel behind your body., Try not to let your elbow flare outward.', 'Dumbbells', 'Beginner'),
(313, 'Dumbbell Tate Press', 'Primary Triceps', '', '', 'Push', 'Lay flat on a bench with two dumbbells extended toward the ceiling., Maintain an overhand grip. Flex at the elbows., The dumbbells will land on your upper chest/clavicle., Extend your elbows bringing the dumbbells back into their starting position., *Named after Dave Tate*', 'Dumbbells', 'Intermediate'),
(314, 'TRX Skullcrusher', 'Primary Triceps', '', 'Overhand', 'Push', 'Hold the two TRX straps and lean forward. The straps should be over each shoulder., Keep your elbows tucked in and then flex and extend at the elbows.', 'TRX', 'Beginner'),
(315, 'Dips', 'Primary Chest Secondary Triceps Tertiary Shoulders', '', '', '', 'Hold your body with arms locked above the equipment, Lower your body slowly while leaning forward, flare out your elbows, Raise your body above the bars until your arms are locked.', 'Bodyweight', 'Intermediate'),
(316, 'Band Pushup', 'Primary Chest Secondary Triceps', 'https://www.youtube.com/embed/R4uGphXkSiQ', '', '', 'Loop the band around your mid-back. The ends of the bands should be around your hands between your thumb and pointer finger., This exercise can be done from the knees or the toes depending on experience level., Begin by breaking at the elbows until your upper arm is by your side. Then extend at the elbows back into the top position.', 'Band', 'Beginner'),
(317, 'Barbell Floor Press', 'Primary Chest Secondary Triceps', '', '', 'Push', 'Un-rack the bar and break at the elbows and shoulders., Bring your elbows to the floor, rest for a second to remove all the momentum from the rep, then press to a lockout.', 'Barbell', 'Advanced'),
(318, 'Barbell Hooklying Bench Press', 'Primary Chest Secondary Triceps', '', 'Overhand', 'Push', 'Start in a bench press position with your feet planted on the bench and knees bent., Grasp the barbell with an overhand grip, positioning your hands slightly wider than shoulder-width apart., Lift the barbell off the rack and lower it to your chest, keeping your elbows close to your sides., Push the barbell back up to the starting position, fully extending your arms.', 'Barbell', 'Intermediate'),
(319, 'Barbell Larsen Bench Press', 'Primary Chest Secondary Triceps Tertiary Abdominals', '', 'Overhand', 'Push', 'Start in a bench press position with your legs elevated. Try to keep your knees with a slight bend in them., Unrack the bar then initiate the rep., Bring the bar to your torso and tap your ribcage before pressing back to the ceiling.', 'Barbell', 'Advanced'),
(320, 'Barbell Spoto Press', 'Primary Chest Secondary Triceps', '', 'Overhand', 'Push', 'Start on the bench with your shoulder blades pulled back., As you initiate the first rep, tuck your elbows slightly until the bar is a few inches off your chest., Pause just off your chest for 1-3 seconds. Then press explosively.', 'Barbell', 'Intermediate'),
(321, 'Barbell Z Press', 'Primary Shoulders Secondary Traps Tertiary Triceps', '', 'Overhand', 'Push', 'Begin by sitting on the ground with your legs extended and a barbell in your hands, with your palms facing forward and your elbows bent., Keeping your core engaged and your back straight, lift the barbell up from your chest and press it straight up above your head., Lower the barbell back down to your chest.', 'Barbell', 'Advanced'),
(322, 'Dumbbell Alternating Single Arm Press', 'Primary Chest Shoulders Secondary Triceps', '', 'Overhand', 'Push', 'Start in a bench press position with two dumbbells., Break at one of your elbows first while keeping the other elbow extended., Your working arm should reach your side before pressing back to the ceiling., Then alternate to the other arm.', 'Dumbbells', 'Intermediate'),
(323, 'Dumbbell Bench Press', 'Primary Chest', '', '', '', 'Lay flat on the bench with your feet on the ground. Raise the dumbbells until you have straight arms., Lower the dumbbells to your mid chest, \"Raise the dumbbells until youve locked your elbows.\"\"\"', 'Dumbbells', 'Beginner'),
(324, 'Dumbbell Decline Alternating Single Arm Press', 'Primary Chest Secondary Triceps', '', 'Overhand', 'Push', 'Lay on a decline bench with both arms extended in front of you., Break at one of your arms first bringing that arm to your side while keeping the other arm extended., Press the dumbbell back to the ceiling. Fully extend that arm before initiating on the other side.', 'Dumbbells', 'Intermediate'),
(325, 'Dumbbell Decline Bench Press', 'Primary Chest Secondary Triceps', '', 'Overhand', 'Push', 'Lay on a decline bench with both arms extended in front of you., Break at the elbows and bring your upper arms to your side., Once your upper arms have reached your side, press the dumbbells back to the ceiling.', 'Dumbbells', 'Beginner'),
(326, 'Dumbbell Decline Guillotine Bench Press', 'Primary Chest Triceps', '', 'Overhand', 'Push', 'Lie on the decline bench, grasp a pair of dumbbells and hold them at shoulder level with your palms facing forward., Slowly lower the dumbbells to your chest, keeping your elbows flared out. As you lower the dumbbells, exhale deeply., Once your elbows reach your chest, pause for a moment and then press them back up to the starting position', 'Dumbbells', 'Advanced'),
(327, 'Dumbbell Decline Neutral Bench Press', 'Primary Chest Secondary Triceps', '', 'Neutral', 'Push', 'Lie on the decline bench, grasp a pair of dumbbells and hold them at shoulder level with your palms facing each other. Keep your elbows bent and close to your body., Slowly lower the dumbbells to your chest, keeping your elbows bent and close to your body., Once the dumbbells reach your chest, pause for a moment and then press them back up to the starting position., This variation is much easier on the shoulder joints.', 'Dumbbells', 'Beginner'),
(328, 'Dumbbell Decline Single Arm Bench Press', 'Primary Chest Secondary Triceps', '', 'Overhand', 'Push', 'Start by laying on a decline bench with two dumbbells., Position the dumbbell above your chest with your elbow bent and shoulder level., Slowly lower the dumbbell towards your chest, keeping your elbow close to your body and your core engaged while the other dumbbell remains pointed toward the ceiling., Once the dumbbell reaches your side, pause for a moment and then press the weight back up to the starting position., Repeat for the desired number of repetitions before switching to your other arm.', 'Dumbbells', 'Intermediate'),
(329, 'Dumbbell Decline Squeeze Press', 'Primary Chest Shoulders Secondary Triceps', '', 'Neutral', 'Push', 'Lay flat on a decline bench with two dumbbells pressed to one another., Break at the elbows and keep your elbows tucked as you bring the dumbbells to your chest., Continue to press the dumbbells into one another throughout the rep., Once the dumbbells reach your chest, press back toward the ceiling.', 'Dumbbells', 'Intermediate'),
(330, 'Dumbbell Guillotine Bench Press', 'Primary Chest Triceps', '', 'Overhand', 'Push', 'Lie on the flat bench, grasp a pair of dumbbells and hold them at shoulder level with your palms facing forward., Slowly lower the dumbbells to your chest, keeping your elbows flared out. As you lower the dumbbells, exhale deeply., Once your elbows reach your chest, pause for a moment and then press them back up to the starting position', 'Dumbbells', 'Advanced'),
(331, 'Dumbbell Guillotine Incline Bench Press', 'Primary Chest Secondary Triceps', '', 'Overhand', 'Push', 'Lie on the incline bench, grasp a pair of dumbbells and hold them at shoulder level with your palms facing forward., Slowly lower the dumbbells to your chest, keeping your elbows flared out. As you lower the dumbbells, exhale deeply., Once your elbows reach your chest, pause for a moment and then press them back up to the starting position', 'Dumbbells', 'Advanced'),
(332, 'Dumbbell Incline Bench Press', 'Primary Chest', 'https://www.youtube.com/embed/bH_T3ibPPzg', 'Overhand', 'Push', 'Raise the bench to a 30 - 45 degree angle, Lay on the bench and set your feet on the ground., Raise the dumbbells with straight arms then slowly lower them to about shoulder width., Raise them again until your arms are locked and at the starting position again.', 'Dumbbells', 'Beginner'),
(333, 'Dumbbell Neutral Bench Press', 'Primary Chest Secondary Triceps', '', 'Neutral', 'Push', 'Lie on a flat bench, grasp a pair of dumbbells and hold them at shoulder level with your palms facing each other. Keep your elbows bent and close to your body., Slowly lower the dumbbells to your chest, keeping your elbows bent and close to your body., Once the dumbbells reach your chest, pause for a moment and then press them back up to the starting position., This variation is much easier on the shoulder joints.', 'Dumbbells', 'Beginner'),
(334, 'Dumbbell Neutral Incline Bench Press', 'Primary Chest Secondary Triceps', '', 'Neutral', 'Push', 'Lie on an incline bench, grasp a pair of dumbbells and hold them at shoulder level with your palms facing each other. Keep your elbows bent and close to your body., Slowly lower the dumbbells to your chest, keeping your elbows bent and close to your body., Once the dumbbells reach your chest, pause for a moment and then press them back up to the starting position., This variation is much easier on the shoulder joints.', 'Dumbbells', 'Beginner'),
(335, 'Dumbbell Neutral Seated Overhead Press', 'Primary Shoulders Secondary Triceps', 'https://www.youtube.com/embed/C0We_bEyxlM', 'Neutral', 'Push', 'Sit on an upright bench , grasp a pair of dumbbells and hold them at shoulder level with your palms facing each other. Keep your elbows bent and close to your body., Slowly lower the dumbbells to your shoulder joint, keeping your elbows bent and close to your body., Once the dumbbells reach your chest, pause for a moment and then press them back up to the starting position., This variation is much easier on the shoulder joints.', 'Dumbbells', 'Beginner'),
(336, 'Dumbbell Seated Arnold Press', 'Primary Shoulders Secondary Triceps', 'https://www.youtube.com/embed/C0We_bEyxlM', 'Rotating', 'Push', 'Begin seated on an upright bench with your palms facing you., Begin pressing toward the ceiling. As you press, rotate your palms., As you extend your elbows fully your palms should be facing forward.', 'Dumbbells', 'Intermediate'),
(337, 'Dumbbell Squeeze Press', 'Primary Chest Secondary Triceps', '', 'Neutral', 'Push', 'Lay flat on a bench with two dumbbells pressed to one another., Break at the elbows and keep your elbows tucked as you bring the dumbbells to your chest., Continue to press the dumbbells into one another throughout the rep., Once the dumbbells reach your chest, press back toward the ceiling.', 'Dumbbells', 'Beginner'),
(338, 'Dumbbell Single Arm Press', 'Primary Chest Secondary Triceps', '', 'Overhand', 'Push', 'Start by laying on a bench with two dumbbells., Position the dumbbell above your chest with your elbow bent and shoulder level., Slowly lower the dumbbell towards your chest, keeping your elbow close to your body and your core engaged while the other dumbbell remains pointed toward the ceiling., Once the dumbbell reaches your side, pause for a moment and then press the weight back up to the starting position., Repeat for the desired number of repetitions before switching to your other arm.', 'Dumbbells', 'Intermediate'),
(339, 'Dumbbell Alternating Arnold Press', 'Primary Shoulders Secondary Triceps', '', 'Rotating', 'Push', 'Start in a standing position', 'Dumbbells', 'Advanced'),
(340, 'Dumbbell Alternating Overhead Press', 'Primary Shoulders Secondary Triceps', '', 'Overhand', 'Push', 'Start with two dumbbells just above your shoulders., Press one dumbbell to the ceiling while keeping the other in place., Let the first dumbbell return to its starting position before pressing the other dumbbell.', 'Dumbbells', 'Intermediate'),
(341, 'Dumbbell Neutral Alternating Overhead Press', 'Primary Shoulders Secondary Triceps', '', 'Neutral', 'Push', 'Start with two dumbbells near your shoulders with a neutral grip., Press one of the dumbbells toward the ceiling while keeping the other in position., Let the first dumbbell return to its starting position before pressing the other.', 'Dumbbells', 'Intermediate'),
(342, 'Dumbbell Neutral Overhead Press', 'Primary Shoulders Secondary Triceps', '', 'Neutral', 'Push', 'Start with both dumbbells near your shoulders in a neutral grip., Press the dumbbells toward the ceiling., \"Make sure you dont arch your back as you fatigue.\"\"\"', 'Dumbbells', 'Beginner'),
(343, 'Dumbbell Overhead Press', 'Primary Shoulders Secondary Triceps', '', 'Overhand', 'Push', 'Start by standing with your feet shoulder-width apart and holding a dumbbell in each hand., Bring the dumbbells to your shoulders, with your palms facing forward and your elbows bent., From this starting position, extend your arms upwards so that the dumbbells are overhead., Make sure to keep your core engaged and your back straight throughout the movement.', 'Dumbbells', 'Beginner'),
(344, 'Dumbbell Push Press', 'Primary Shoulders Secondary Triceps', '', 'Overhand Neutral', 'Push', 'Start by standing with your feet shoulder-width apart and holding a dumbbell in each hand., From this starting position, dip your knees slightly and then quickly extend them as you press the dumbbells upwards., Make sure to keep your core engaged and your back straight throughout the movement., Slowly lower the dumbbells back down to the starting position, and repeat for the desired number of repetitions.', 'Dumbbells', 'Intermediate'),
(345, 'Dumbbell Single Arm Arnold Press', 'Primary Shoulders Secondary Triceps', '', 'Rotating', 'Push', 'Start by standing with your feet shoulder-width apart and holding a dumbbell in one hand., Bring the dumbbell to your shoulder, with your palm facing you and your elbow bent., From this starting position, extend your arm upwards so that the dumbbell is directly above your head., As you extend your arm, rotate your palm to face away from your body.', 'Dumbbells', 'Intermediate'),
(346, 'Dumbbell Single Arm Neutral Overhead Press', 'Primary Shoulders Secondary Triceps', '', 'Neutral', 'Push', 'Start by standing with your feet shoulder-width apart and holding a dumbbell in one hand., Bring the dumbbell to your shoulder, with your palm facing your body and your elbow bent., From this starting position, press the dumbbell upwards so that it is directly above your shoulder.', 'Dumbbells', 'Beginner'),
(347, 'Dumbbell Single Arm Overhead Press', 'Primary Shoulders Secondary Triceps', '', 'Overhand', 'Push', 'Start by standing with your feet shoulder-width apart and holding a dumbbell in one hand., Bring the dumbbell to your shoulder, with your palm facing forward and your elbow bent., From this starting position, extend your arm upwards so that the dumbbell is overhead., Make sure to keep your core engaged and your back straight throughout the movement.', 'Dumbbells', 'Beginner'),
(348, 'Plate Squat Reach', 'Primary Quads Triceps Secondary Shoulders', '', '', 'Push', 'Stand holding a weight plate at chest height with both hands., Lower into a squat, extending the plate in front of you at chest height, As you stand back up, pull the plate back into your chest.', 'Plate', 'Advanced'),
(349, 'Plate Squat Hold Reach', 'Primary Triceps Shoulders Secondary Quads Glutes', '', '', '', 'Hold the plate with both hands at chest height., Keeping your core tight and your chest up, squat down as low as you can go while keeping your feet flat on the ground., Hold the squat position and reach the plate forward, keeping your arms straight and your back straight., Return the plate to your chest while you hold the squat position and repeat reaching the plate in front of you', 'Plate', 'Intermediate'),
(350, 'Plate Reverse Lunge Twist', 'Primary Quads Triceps Secondary Glutes Obliques', '', '', '', 'Step back with your right foot into a lunge position, both knees bent at 90 degrees., Twist your torso to the right, keeping the plate close to your chest., Push through your front foot to return to the starting position and repeat on the other side.', 'Plate', 'Intermediate'),
(351, 'Plate Deficit Pushup', 'Primary Chest Secondary Triceps Tertiary Shoulders', '', 'Overhand', 'Push', 'Start in a pushup position with both hands on a plate. This will extend your range of motion., Break at the elbows and shoulders until your upper arm travels behind the body slightly., Then press back into the original position.', 'Plate', 'Advanced'),
(352, 'Dumbbell Elevated Pushup', 'Primary Chest Secondary Triceps', '', 'Neutral', 'Push', 'Start by placing two dumbbells on the floor, about shoulder-width apart., Position yourself in a plank position with your hands on top of the dumbbells, keeping your arms straight., Keep your feet together, with your toes resting on the floor., Engage your core and lower your body towards the floor by bending your arms, until your chest nearly touches the dumbbells., Push your body back up to the starting position, straightening your arms and engaging your chest, triceps and shoulders.', 'Dumbbells', 'Intermediate'),
(353, 'TRX Pushup', 'Primary Chest Secondary Triceps Tertiary Shoulders', '', 'Overhand', 'Push', 'Start by setting up the TRX straps to a secure anchor point and adjust the straps so that they hang about waist height when you stand under them., Stand facing the TRX straps, grab the handles with both hands, and step forward so that your body is in a plank position with your hands directly beneath your shoulders., Lower your body down by bending your elbows until your chest nearly touches the handles. Keep your core tight and your body in a straight line., Push your body back up to the starting position, straightening your arms as you do so. Make sure to engage your chest, triceps, and core throughout the movement.', 'TRX', 'Beginner'),
(354, 'Child Pose Elbows On Block', 'Primary Triceps Secondary Lats', '', '', 'Hold', 'Place two blocks on the floor, shoulder-width apart., Kneel in front of the blocks, and lower your torso down so that your elbows are resting on the blocks, and your forehead is resting on the floor., Relax your arms, shoulders, and neck, and breathe deeply., Hold for a few breaths, then release.', 'Yoga', 'Beginner'),
(355, 'One Arm Cow Face Pose', 'Primary Triceps Secondary Lats', '', '', 'Hold', 'Sit on the floor with your legs extended in front of you., Bend your left knee and place your left foot on the floor, close to your right hip., Cross your right leg over your left leg, stacking your knees on top of each other., Bring your left arm up overhead, and bend your elbow so that your left hand reaches towards your upper back. Reach your right hand behind your back and try to clasp your left hand. Hold', 'Yoga', 'Beginner'),
(356, 'Pyramid Prayer', 'Primary Triceps Secondary Lats', '', '', 'Hold', 'Stand with your feet hip-width apart and place your hands on a table, chair, or any elevated surface in front of you., Walk your feet back until your torso is parallel to the ground, and your elbows are resting on the surface., Relax your head and neck, and breathe deeply., Hold for a few breaths, then release.', 'Yoga', 'Beginner'),
(357, 'Dumbbell Lateral Raise', 'Primary Shoulders', '', '', '', 'Stand up straight with dumbbells at either side, palms facing your hips., Raise your arms on either side with a slight bend in your elbow until they are parallel with the floor. Pause at the top of the motion., Slowly return your arms down to starting position.', 'Dumbbells', 'Beginner'),
(358, 'Dumbbell Rear Delt Fly', 'Primary Shoulders', '', '', '', 'With dumbbells in either hand, bend your knees with your feet slightly bowed out. Arch your back above your knees, and start with the weights touching in front of your chest., With bent elbows, raise your arms up to shoulder level, pausing at the at the end of the motion., Slowly lower your arms back to starting position.', 'Dumbbells', 'Intermediate'),
(359, 'Machine Face Pulls', 'Primary Shoulders', '', 'Overhand', 'Pull', 'Facing the pulley, pull the weight towards you while keeping your arms parallel to the ground., Pull your hands back to both sides of your head and hold the position., Slowly return weight to starting position. Repeat.', 'Machine', 'Intermediate'),
(360, 'Dumbbell Seated Overhead Press', 'Primary Shoulders', 'https://www.youtube.com/embed/C0We_bEyxlM', 'Overhand', 'Push', 'Sit on a bench with back support. Raise the dumbbells to shoulder height with your palms forward., Raise the dumbbells upwards and pause at the contracted position., Lower the weights back to starting position.', 'Dumbbells', 'Beginner'),
(361, 'Dumbbell Front Raise', 'Primary Shoulders', '', 'Overhand', 'Push', 'Grab two dumbbells while standing upright with the dumbbells at your side., Raise the two dumbbells with your elbows being fully extended until the dumbbells are eye level., Lower the weights in a controlled manner to the starting position and repeat.', 'Dumbbells', 'Beginner'),
(362, 'Dumbbell Seated Rear Delt Fly', 'Primary Shoulders', '', '', '', 'With dumbbells in either hand, bend your knees with your feet slightly bowed out. Arch your back above your knees, and start with the weights touching in front of your chest., With bent elbows, raise your arms up to shoulder level, pausing at the at the end of the motion., Slowly lower your arms back to starting position.', 'Dumbbells', 'Intermediate'),
(363, 'Kettlebell Rear Delt Row', 'Primary Shoulders', '', '', '', 'Stand with your feet shoulder width apart, holding the kettlebell with both hands in front of your thighs., Bend forward at the hips bringing the kettlebell to the floor while you slightly bend your knees, keeping your back straight., Lift the kettlebell upwards towards your upper chest and then lower the kettlebell- repeat.', 'Kettlebells', 'Beginner'),
(364, 'Kettlebell Front Raise', 'Primary Shoulders', '', '', '', 'Stand up straight with feet shoulder width apart, holding the kettlebell in front of your pelvis., Keeping arms straight, raise the kettlebell slightly higher than head level., Return to the starting position and repeat.', 'Kettlebells', 'Beginner'),
(365, 'Kettlebell Single Arm Rear Delt Fly', 'Primary Shoulders', '', '', '', 'Stand with your feet shoulder width apart holding the kettlebell with both hands in front of your thighs., Bend forward at the hips bringing the kettlebell to the floor while you slightly bend your knees, keeping your back straight., Bring one arm behind you and rest this arm on your back. With the other arm, hold the kettlebell at arms length close to the floor., Swing the kettlebell out and up towards the side of your body and then return to centre. Repeat.', 'Kettlebells', 'Intermediate');
INSERT INTO `workout` (`ID`, `exercise_name`, `target`, `youtubeURL`, `Grips`, `Force`, `steps`, `Category`, `Difficulty`) VALUES
(366, 'Kettlebell Long Lever Lateral Raise', 'Primary Shoulders', '', '', '', 'Stand with feet shoulder width apart holding a kettlebell with one hand at your side., Keeping your arm straight, swing the kettlebell up and out, away from your body to around head height, holding on to the kettlebell firmly so it does not change position in your hand., Return to the starting position and repeat.', 'Kettlebells', 'Intermediate'),
(367, 'Kettlebell Single Arm Lateral Raise', 'Primary Shoulders', '', '', '', 'Stand with feet shoulder width apart holding a kettlebell with one hand at your side., Keeping your arm straight, swing the kettlebell up and out, away from your body to around head height., Return to the starting position and repeat.', 'Kettlebells', 'Beginner'),
(368, 'Shoulders Stretch Variation Four', 'Primary Shoulders', '', 'None', '', 'Stand with you feet shoulder width apart. Place one arm across the front of your chest at shoulder height, press the forearm of your other arm above the elbow, hooking the stretching arm with your hand., Press the arm until it is straight and rotate your upper torso to engage the stretch even deeper., Repeat with your other arm.', 'Stretches', 'Beginner'),
(369, 'Shoulders Stretch Variation Three', 'Primary Shoulders', '', 'None', '', 'Place the top of your hand into the small of your back, your arm at a 90 degree angle., Hold your elbow with the other arm, and slowly pull the arm forward until you feel your shoulder engaged in the stretch. Pause at the apex of the stretch and return to the starting position., Return to starting position, and repeat with your other arm.', 'Stretches', 'Beginner'),
(370, 'Shoulders Stretch Variation Two', 'Primary Shoulders', '', 'None', '', 'Start with your arms at either side. Begin by pushing your shoulders forward and in towards your chest., Repeat as necessary., Continue the circular motion with your shoulders, pinching up towards your ears, and then extending your chest outwards while you finish the motion.', 'Stretches', 'Beginner'),
(371, 'Shoulders Stretch Variation One', 'Primary Shoulders', '', 'None', '', 'Reach one arm behind your body, with your elbow pointing upward behind your head., Assist the stretch with your other hand on your elbow to engage your shoulder., Pause for a few seconds, then repeat the stretch with your other arm.', 'Stretches', 'Beginner'),
(372, 'Elevated Pike Press', 'Primary Shoulders Mid back Secondary Traps Tertiary Triceps', '', 'Overhand', 'Push', 'Use a bench or an object to elevate your feet., Lower your head towards the floor by bending your elbows, Push through your hands and return to the starting pike position., Repeat', 'Bodyweight', 'Intermediate'),
(373, 'Cable Overhead Press', 'Primary Shoulders Secondary Triceps', '', '', '', 'Use a handle attachment with the cable set to the bottom of the machine. Stand upright making sure to maintain a flat back. Your starting position will have your palms facing forward., Use both your elbows and shoulders to press the weight directly overhead.', 'Cables', 'Beginner'),
(374, 'Cable Lateral Raise', 'Primary Shoulders', '', 'Neutral', 'Push', 'Use a handle attachment with the cable set all the way to the bottom of the machine., You should vertically abduct at the shoulder raising your arm straight out to the side., Raise until your arm is parallel with the ground and then back to the starting position.', 'Cables', 'Beginner'),
(375, 'Cable Rear Delt Fly', 'Primary Shoulders', '', 'Neutral', '', 'Use a handle attachment or no attachment at all. The cable should be set to the top., Walk a few steps away with the cable coming from the opposing side of the working arm., From there, your shoulders should horizontally abduct and adduct, while your elbows stay in a fixed position.', 'Cables', 'Intermediate'),
(376, 'Cable Upright Row', 'Primary Shoulders Secondary Traps', 'https://www.youtube.com/embed/v7nsz05s9oM', 'Underhand', '', 'Use a handle attachment with the cable set to the bottom of the machine., Pull your elbows straight up toward the ceiling until the attachment is just below your chin then extend until your elbows are fully extended.', 'Cables', 'Advanced'),
(377, 'Band Face Pull', 'Primary Shoulders', '', '', '', 'Anchor the band at eye level. Take a few steps away until the band is taut., Use an overhand grip with both hands. Pull the band to the bridge of your nose flaring your elbows out.', 'Band', 'Intermediate'),
(378, 'Band Front Raise', 'Primary Shoulders', '', '', '', 'Stand on the band with one or 2 feet depending on how heavy you want the band to be., With your elbows extended raise your fists straight up to the ceiling until they are at eye level.', 'Band', 'Beginner'),
(379, 'Band Lateral Raise', 'Primary Shoulders', '', '', '', 'Grab the band at shoulder width. Raise your arms straight out to the side., The band should scrape against your shirt.', 'Band', 'Intermediate'),
(380, 'Band Pull Apart', 'Primary Shoulders Secondary Mid back', '', 'Overhand', 'Pull', 'Grab the band with a shoulder width grip. Point your arms straight in front of you., Retract your shoulder blades and shoulder joint until the band taps your chest.', 'Band', 'Beginner'),
(381, 'Band Single Arm Overhead Press', 'Primary Shoulders Secondary Triceps Tertiary Traps', '', '', '', 'Loop the band under one of your feet. The other end of the bed should be in your hand between your thumb and pointer finger., Press the band straight up towards the ceiling. Until your elbow is fully extended.', 'Band', 'Intermediate'),
(382, 'Barbell Front Raise', 'Primary Shoulders', '', '', '', 'Stand tall with an overhand grip on the barbell. Raise your arms until your fists are around eye level., Return to the start under control. This exercise is prone to using momentum when you begin to fatigue.', 'Barbell', 'Intermediate'),
(383, 'Barbell Overhead Press', 'Primary Shoulders', 'https://www.youtube.com/embed/yWZGBBZoVXc', 'Overhand', 'Push', 'Take a roughly shoulder width grip. There should be a straight line from your elbow to fist (vertical forearms)., Pull your chin back and press the weight toward the ceiling by extending at the elbow joint and flexing at the shoulder joint., Press until your elbows are extended and push your head forward slightly., Return to the start position with control. Pulling your chin back to allow the bar to pass your face safely.', 'Barbell', 'Intermediate'),
(384, 'Barbell Upright Row', 'Primary Shoulders Secondary Traps', 'https://www.youtube.com/embed/v7nsz05s9oM', 'Overhand', 'Pull', 'Take a double overhand roughly shoulder width grip., Pull your elbows straight up the ceiling. Aim to get the bar to chin level or slightly higher.', 'Barbell', 'Advanced'),
(385, 'Dumbbell Rear Delt Row', 'Primary Shoulders Secondary Mid back', '', '', '', 'Hinge forward at the hips while maintaining a flat back. Try to get your torso as close to parallel with the ground as your mobility will allow for., Let your arms hang in front of you. Pull your elbows back towards the ceiling while flaring your elbows outward.', 'Dumbbells', 'Advanced'),
(386, 'Barbell Z Press', 'Primary Shoulders Secondary Traps Tertiary Triceps', '', 'Overhand', 'Push', 'Begin by sitting on the ground with your legs extended and a barbell in your hands, with your palms facing forward and your elbows bent., Keeping your core engaged and your back straight, lift the barbell up from your chest and press it straight up above your head., Lower the barbell back down to your chest.', 'Barbell', 'Advanced'),
(387, 'Dumbbell Bayesian Lateral Raise', 'Primary Shoulders', '', 'Neutral', 'Pull', 'Lay on an incline bench and let your arms freely., Lateral raise your arms straight out to the side until your arms are about parallel with the ground.', 'Dumbbells', 'Beginner'),
(388, 'Dumbbell Laying Reverse Fly', 'Primary Shoulders', '', 'Neutral', 'Pull', '\"With dumbbells in either hand', 'Dumbbells', 'Beginner'),
(389, 'Dumbbell Laying Reverse Fly Internally Rotated', 'Primary Shoulders', '', 'Overhand', 'Pull', 'With dumbbells in either hand and an overhand grip, lay on an incline bench that is roughly 15 - 30 degrees high., With bent elbows, raise your arms up to shoulder level, pausing at the at the end of the motion., Slowly lower your arms back to starting position.', 'Dumbbells', 'Intermediate'),
(390, 'Dumbbell Neutral Seated Overhead Press', 'Primary Shoulders Secondary Triceps', 'https://www.youtube.com/embed/C0We_bEyxlM', 'Neutral', 'Push', 'Sit on an upright bench , grasp a pair of dumbbells and hold them at shoulder level with your palms facing each other. Keep your elbows bent and close to your body., Slowly lower the dumbbells to your shoulder joint, keeping your elbows bent and close to your body., Once the dumbbells reach your chest, pause for a moment and then press them back up to the starting position., This variation is much easier on the shoulder joints.', 'Dumbbells', 'Beginner'),
(391, 'Dumbbell Seated Arnold Press', 'Primary Shoulders Secondary Triceps', 'https://www.youtube.com/embed/C0We_bEyxlM', 'Rotating', 'Push', 'Begin seated on an upright bench with your palms facing you., Begin pressing toward the ceiling. As you press, rotate your palms., As you extend your elbows fully your palms should be facing forward.', 'Dumbbells', 'Intermediate'),
(392, 'Dumbbell Alternating Arnold Press', 'Primary Shoulders Secondary Triceps', '', 'Rotating', 'Push', 'Start in a standing position', 'Dumbbells', 'Advanced'),
(393, 'Dumbbell Alternating Overhead Press', 'Primary Shoulders Secondary Triceps', '', 'Overhand', 'Push', 'Start with two dumbbells just above your shoulders., Press one dumbbell to the ceiling while keeping the other in place., Let the first dumbbell return to its starting position before pressing the other dumbbell.', 'Dumbbells', 'Intermediate'),
(394, 'Dumbbell Arnold Press', 'Primary Shoulders', '', 'Rotating', 'Push', 'Stand with both palms facing you., Begin to press the dumbbells to the ceiling while rotating your palms., When you reach elbow extension, your palms should be facing forward.', 'Dumbbells', 'Intermediate'),
(395, 'Dumbbell Neutral Alternating Overhead Press', 'Primary Shoulders Secondary Triceps', '', 'Neutral', 'Push', 'Start with two dumbbells near your shoulders with a neutral grip., Press one of the dumbbells toward the ceiling while keeping the other in position., Let the first dumbbell return to its starting position before pressing the other.', 'Dumbbells', 'Intermediate'),
(396, 'Dumbbell Neutral Overhead Press', 'Primary Shoulders Secondary Triceps', '', 'Neutral', 'Push', 'Start with both dumbbells near your shoulders in a neutral grip., Press the dumbbells toward the ceiling., \"Make sure you dont arch your back as you fatigue.\"\"\"', 'Dumbbells', 'Beginner'),
(397, 'Dumbbell Overhead Press', 'Primary Shoulders Secondary Triceps', '', 'Overhand', 'Push', 'Start by standing with your feet shoulder-width apart and holding a dumbbell in each hand., Bring the dumbbells to your shoulders, with your palms facing forward and your elbows bent., From this starting position, extend your arms upwards so that the dumbbells are overhead., Make sure to keep your core engaged and your back straight throughout the movement.', 'Dumbbells', 'Beginner'),
(398, 'Dumbbell Push Press', 'Primary Shoulders Secondary Triceps', '', 'Overhand Neutral', 'Push', 'Start by standing with your feet shoulder-width apart and holding a dumbbell in each hand., From this starting position, dip your knees slightly and then quickly extend them as you press the dumbbells upwards., Make sure to keep your core engaged and your back straight throughout the movement., Slowly lower the dumbbells back down to the starting position, and repeat for the desired number of repetitions.', 'Dumbbells', 'Intermediate'),
(399, 'Dumbbell Single Arm Arnold Press', 'Primary Shoulders Secondary Triceps', '', 'Rotating', 'Push', 'Start by standing with your feet shoulder-width apart and holding a dumbbell in one hand., Bring the dumbbell to your shoulder, with your palm facing you and your elbow bent., From this starting position, extend your arm upwards so that the dumbbell is directly above your head., As you extend your arm, rotate your palm to face away from your body.', 'Dumbbells', 'Intermediate'),
(400, 'Dumbbell Single Arm Neutral Overhead Press', 'Primary Shoulders Secondary Triceps', '', 'Neutral', 'Push', 'Start by standing with your feet shoulder-width apart and holding a dumbbell in one hand., Bring the dumbbell to your shoulder, with your palm facing your body and your elbow bent., From this starting position, press the dumbbell upwards so that it is directly above your shoulder.', 'Dumbbells', 'Beginner'),
(401, 'Dumbbell Single Arm Overhead Press', 'Primary Shoulders Secondary Triceps', '', 'Overhand', 'Push', 'Start by standing with your feet shoulder-width apart and holding a dumbbell in one hand., Bring the dumbbell to your shoulder, with your palm facing forward and your elbow bent., From this starting position, extend your arm upwards so that the dumbbell is overhead., Make sure to keep your core engaged and your back straight throughout the movement.', 'Dumbbells', 'Beginner'),
(402, 'Plate Car Driver', 'Primary Shoulders', '', 'Neutral', 'Hold', 'Inhale and brace your abdominals, Raise the arms vertically while keeping the elbows slightly bent, Once the arms are parallel with the floor, rotate the plate clockwise and counterclockwise., Lower the plate back to the starting position.', 'Plate', 'Beginner'),
(403, 'Plate Front Raise', 'Primary Shoulders', '', 'Neutral', '', 'Stand with feet shoulder-width apart and the plate in front of the torso, palms facing the thighs., Keeping the elbows slightly bent, lift the plate up to the front of the body., Raise the plate up until the arms are fully extended, maintaining control of the plate., Lower the plate back to the starting position and repeat for desired reps.', 'Plate', 'Beginner'),
(404, 'Plate Halo', 'Primary Shoulders', '', 'Neutral', '', 'Stand with feet shoulder-width apart and the plate held in front of your face with both hands, palms facing each other., Rotate the plate around the head, keeping the arms extended and moving the plate in a circular motion., Complete one full circle, then reverse the direction to complete another circle.', 'Plate', 'Beginner'),
(405, 'Band High Face Pull', 'Primary Shoulders Secondary Mid back', '', 'Overhand', 'Pull', 'Start with a band anchored to a high point. Stagger your stance., Pull the band to the bridge of your nose.', 'Band', 'Beginner'),
(406, 'Band Low Lateral Raise', 'Primary Shoulders', '', 'Overhand', 'Push', 'Anchor a band to a low point and stand with the band crossing your body., \"While keeping your elbow mostly extended', 'Band', 'Intermediate'),
(407, 'Band Mid Lateral Raise', 'Primary Shoulders', '', 'Overhand', 'Push', 'Anchor a band to a point that is the same height as your fist when your arms are at your side and stand with the band crossing your body., \"While keeping your elbow mostly extended', 'Band', 'Beginner'),
(408, 'Band Overhead Press', 'Primary Shoulders', '', 'Overhand', 'Push', 'Stand between the two anchored bands, grasp them with an overhand grip, and position the bands at shoulder level., Brace your core and push the bands overhead, fully extending your arms., Lower the bands back to shoulder level, keeping your core engaged and arms straight. Repeat for desired number of reps.', 'Band', 'Beginner'),
(409, 'Band Rear Delt Fly', 'Primary Shoulders Secondary Mid back', '', 'Neutral', 'Pull', 'Start by standing between the two anchored bands with your feet shoulder-width apart and the bands positioned at a high point., Grasp the ends of each band with your hands, keeping your palms facing each other., Keeping your arms slightly bent and your back straight, slowly pull the bands away from your body until your arms are extended straight out to the sides. Hold for a second, then slowly release back to the starting position. Repeat for desired reps.', 'Band', 'Intermediate'),
(410, 'Plate Full Lateral Raise', 'Primary Shoulders', '', 'Rotating', 'Push', 'Stand tall holding two plates at your side., Lateral raise the plates to your side and continue until the plates are pointed at the ceiling., Let your shoulder joint naturally rotate. At the bottom you should have a neutral or overhand grip. By the time the plates are overhead, your palms should be facing forward.', 'Plate', 'Intermediate'),
(411, 'Plate Internally Rotated Rear Delt Fly', 'Primary Shoulders Mid back Secondary Traps', '', 'Overhand', 'Pull', 'With plates in either hand, bend your knees with your feet slightly bowed out. Hinge forward with a flat back., Maintain an overhand grip and internally rotated shoulders., Raise your arms backward toward the ceiling while maintaining a light band in your elbows.', 'Plate', 'Intermediate'),
(412, 'Plate Lateral Raise', 'Primary Shoulders', '', 'Neutral', 'Push', 'Stand with feet shoulder-width apart and grip two plates in each hand at your sides., Keeping your arms straight, raise the plates out to your sides until they are in line with your shoulders., Lower the plates back to your sides with control. Repeat for desired number of reps.', 'Plate', 'Beginner'),
(413, 'Plate Rear Delt Fly', 'Primary Shoulders Secondary Mid back', '', 'Neutral', 'Pull', 'Keep your back straight and bend forward slightly at the waist., With arms extended, raise the weights out to your sides until they are in line with your shoulders., Pause briefly and then lower the weights back to the starting position.', 'Plate', 'Beginner'),
(414, 'Plate Reach', 'Primary Shoulders', '', 'Neutral', 'Push', 'Start in a standing position with a plate against your chest., Press the plate away from your body until your elbows are extended.', 'Plate', 'Beginner'),
(415, 'Laying Lateral Raise', 'Primary Shoulders', '', 'Overhand Neutral', 'Pull', 'Lay on your side on a bench or box. Place one hand on the floor and the other hand is holding the dumbbell., While keeping your elbow mostly extended, raise the dumbbell to the side., The end of the range of motion is when the dumbbell is pointed toward the ceiling., \"Make sure you dont flex and extend your elbow.\"\"\"', 'Dumbbells', 'Beginner'),
(416, 'Dumbbell Single Arm Upright Row', 'Primary Shoulders', '', 'Overhand', 'Pull', 'Stand with your feet shoulder-width apart, holding a dumbbell in one hand with an overhand grip., Keep your back straight and chest lifted, engage your core to maintain balance., Begin the movement by lifting the dumbbell straight up towards your chin, keeping your elbow close to your body and the weight in line with your shoulder., Continue to lift the weight until it reaches the chin or neck level, keeping the elbow high and close to the body., Lower the weight back down to the starting position with control.', 'Dumbbells', 'Beginner'),
(417, 'Dumbbell Upright Row', 'Primary Shoulders', '', 'Overhand', 'Pull', 'Stand with your feet shoulder-width apart, holding 2 dumbbells in each hand., Keep your back straight and chest lifted, engage your core to maintain balance., Begin the movement by lifting the dumbbells straight up towards your chin, keeping your elbow close to your body and the weight in line with your shoulder., Continue to lift the weight until it reaches the chin or neck level, keeping the elbow high and close to the body.', 'Dumbbells', 'Beginner'),
(418, 'TRX I Raise', 'Primary Shoulders', '', 'Overhand', 'Pull', 'Stand facing the TRX straps and hold onto the handles with an overhand grip, keeping your arms straight and hands slightly wider than shoulder-width apart., Keeping your core engaged, slowly raise your arms forward until they are overhead., Pause for a moment at the top of the movement, then slowly lower your arms back to the starting position.', 'TRX', 'Beginner'),
(419, 'TRX Rear Delt Fly', 'Primary Shoulders Secondary Mid back', '', 'Overhand Neutral', 'Pull', 'Stand facing away from the TRX straps and grab the handles with both hands., Step forward so that your body is in a plank position, with your arms straight and your body in a straight line from your head to your heels., Keeping your arms straight, slowly lift your arms out to the side, keeping them at the same height as your shoulders. Your body should form a \"T\"\" shape.\"', 'TRX', 'Intermediate'),
(420, 'TRX Rear Delt Row', 'Primary Shoulders Secondary Mid back', '', 'Overhand', 'Pull', 'Stand facing away from the TRX straps and grab the handles with both hands., Step away so your body is leaning away from the anchor point., Flex your elbows and pull your elbows straight back behind your body., Make sure to keep your elbows flared outward.', 'TRX', 'Beginner'),
(421, 'TRX W Raise', 'Primary Shoulders Secondary Mid back', '', 'Overhand', 'Pull', 'Hold the TRX straps and lean away from the anchor point., Pull your elbows back while externally rotating your shoulder joint. Your arms should make a W shape at the back portion of the exercise.', 'TRX', 'Beginner'),
(422, 'TRX Y Raise', 'Primary Shoulders Secondary Traps Tertiary Mid back', '', 'Overhand', 'Pull', 'Hold the TRX straps and lean away from the anchor point until the straps are taut., Keep both elbows mostly extended and raise both arms out and upwards., You should make a Y shape with your arms during the back portion of the exercise.', 'TRX', 'Beginner'),
(423, 'Plate Squat Hold Reach', 'Primary Triceps Shoulders Secondary Quads Glutes', '', '', '', 'Hold the plate with both hands at chest height., Keeping your core tight and your chest up, squat down as low as you can go while keeping your feet flat on the ground., Hold the squat position and reach the plate forward, keeping your arms straight and your back straight., Return the plate to your chest while you hold the squat position and repeat reaching the plate in front of you', 'Plate', 'Intermediate'),
(424, 'Elevated Pike Shoulder Shrug', 'Primary Traps Shoulders', '', '', '', 'Use a bench or an object like a chair to elevate your feet., Slowly lower your body (scapula) while keeping your elbows locked (this is key), Slowly raise your body back to the start position.', 'Bodyweight', 'Beginner'),
(425, 'Band Horizontal Shrug', 'Primary Traps Shoulders Mid back', '', '', '', 'Anchor the band at shoulder height. Take a few steps away until the band is taut., Retract your shoulder blades straight back in schools. Then protract to the starting position.', 'Band', 'Advanced'),
(426, 'Dumbbell Alternating Single Arm Press', 'Primary Chest Shoulders Secondary Triceps', '', 'Overhand', 'Push', 'Start in a bench press position with two dumbbells., Break at one of your elbows first while keeping the other elbow extended., Your working arm should reach your side before pressing back to the ceiling., Then alternate to the other arm.', 'Dumbbells', 'Intermediate'),
(427, 'Dumbbell Decline Squeeze Press', 'Primary Chest Shoulders Secondary Triceps', '', 'Neutral', 'Push', 'Lay flat on a decline bench with two dumbbells pressed to one another., Break at the elbows and keep your elbows tucked as you bring the dumbbells to your chest., Continue to press the dumbbells into one another throughout the rep., Once the dumbbells reach your chest, press back toward the ceiling.', 'Dumbbells', 'Intermediate'),
(428, 'Dumbbell Thruster', 'Primary Quads Secondary Shoulders Tertiary Glutes', '', 'Neutral', 'Push', 'Start by standing with your feet shoulder-width apart and holding a dumbbell in each hand., Bring the dumbbells to your shoulders, with your palms facing inward and your elbows bent., From this starting position, dip your knees slightly and then quickly extend them as you press the dumbbells upwards., As you press the dumbbells upwards, also push your hips forward to help generate additional power., Slowly lower the dumbbells back down to the starting position, and repeat for the desired number of repetitions.', 'Dumbbells', 'Intermediate'),
(429, 'Plate Clean And Press', 'Primary Glutes Secondary Shoulders', '', '', 'Push', 'Stand with feet shoulder-width apart and the plate in front of the feet., Bend the knees and hips to pick up the plate, keeping the back straight and core engaged., Stand up, lock the hips and legs, and press the plate overhead with straight arms., Lower the plate back to the starting position, keeping control of it, and repeat for desired reps.', 'Plate', 'Intermediate'),
(430, 'Plate Curl To Overhead Press', 'Primary Biceps Secondary Shoulders', '', 'Neutral', 'Push', 'Stand with feet shoulder-width apart and the plate in front of the torso, palms facing forward., Curl the plate up to the front of the shoulder while keeping the elbows close to the body., Rotate the palms to face forward and press the plate overhead, locking out the arms., Lower the plate back to the starting position, keeping control of it, and repeat for desired reps.', 'Plate', 'Intermediate'),
(431, 'Plate Curl To Overhead Press To Tricep Extension', 'Primary Biceps Triceps Secondary Shoulders', '', 'Neutral', '', 'Stand with feet shoulder-width apart and the plate in front of the torso, palms facing forward., Curl the plate up to the front of the shoulder while keeping the elbows close to the body., Rotate the palms to face forward and press the plate overhead, locking out the arms., Lower the plate back behind the head to the starting position of a tricep extension., Push the plate back up overhead, then lower it back to the starting position and repeat for desired reps.', 'Plate', 'Advanced'),
(432, 'Plate Overhead Press To Tricep Extension', 'Primary Triceps Secondary Shoulders', '', '', '', 'Brace your core and press the weight plate overhead until your arms are fully extended. Keep your shoulders down and back., While your arms are fully extended, lower the weight behind your head to perform the extension, After completing the extension, lower the weight to your chest, and perform the overhead press. Repeat', 'Plate', ''),
(433, 'Plate Overhead Carry', 'Primary Traps Abdominals Secondary Shoulders', '', 'Neutral', 'Hold', 'Start by standing upright with your feet shoulder-width apart. Hold a single weight plate overhead with both hands, keeping your arms extended straight above your head., Begin walking forward, taking slow and steady steps while maintaining a straight line. Your focus should be on maintaining balance and control of the weight plate overhead., As you walk, continue to keep your arms extended and the weight plate overhead. Try to maintain good posture and avoid leaning forward or backward, as this could result in losing control of the weight plate., Continue walking for a set distance or for a certain amount of time, keeping your focus on proper form and technique. When finished, return to the starting position and repeat as desired.', 'Plate', 'Beginner'),
(434, 'Plate Thruster', 'Primary Quads Secondary Shoulders Tertiary Glutes', '', 'Overhand', 'Push', 'Start with the plate at chest height. Begin a squat., Make sure to hit parallel depth. On your way back up, use the momentum you generate with your legs to help push the plate into an overhead position., Let the plate return to chest height before initiating the next squat.', 'Plate', 'Intermediate'),
(435, 'Dumbbell Overhead Squat', 'Primary Quads Secondary Glutes Tertiary Shoulders', '', 'Neutral', 'Push', 'Stand with your feet slightly wider than shoulder-width apart and hold two dumbbells overhead with both hands, using an overhand grip., Begin the squat by bending at the hips and knees, keeping your core tight and the dumbbells balanced overhead., Lower your body as far as you can while keeping the dumbbells overhead, then push back up to the starting position., Repeat for the desired number of repetitions, making sure to keep your back straight, chest lifted, and the dumbbells stable overhead throughout the entire movement.', 'Dumbbells', 'Advanced'),
(436, 'Plank IYTW', 'Primary Abdominals Secondary Shoulders', '', 'Neutral', 'Hold', 'Start in a plank position. Hold one dumbbell., Raise the dumbbell straight out in front of you (I), at a 45 degree angle (Y), out to the side (T), and then bend at the elbow while raising (W)., Going through IYTW counts as one rep., \"Make sure to keep your hips stable. If youre unable to', 'Dumbbells', 'Intermediate'),
(437, 'Single Arm Overhead Squat', 'Primary Quads Secondary Shoulders Tertiary Glutes', '', '', 'Push', 'Stand with your feet shoulder-width apart and a dumbbell in your right hand, extending it overhead., Inhale and begin to squat down, keeping the dumbbell overhead and your back straight., Go down as far as you can while still maintaining control, then exhale and return to standing position, keeping the dumbbell overhead. Repeat for desired reps and then switch to the other arm.', 'Dumbbells', 'Advanced'),
(438, 'TRX Pushup', 'Primary Chest Secondary Triceps Tertiary Shoulders', '', 'Overhand', 'Push', 'Start by setting up the TRX straps to a secure anchor point and adjust the straps so that they hang about waist height when you stand under them., Stand facing the TRX straps, grab the handles with both hands, and step forward so that your body is in a plank position with your hands directly beneath your shoulders., Lower your body down by bending your elbows until your chest nearly touches the handles. Keep your core tight and your body in a straight line., Push your body back up to the starting position, straightening your arms as you do so. Make sure to engage your chest, triceps, and core throughout the movement.', 'TRX', 'Beginner'),
(439, 'Eagle Arm Pose', 'Primary Shoulders', '', '', '', 'Begin seated or standing with your arms at your sides., Inhale and reach your arms out to the sides at shoulder height., Cross your right arm over your left arm, bringing your palms to touch if possible., Hold for a few breaths and then release the arms and repeat with the left arm over the right.', 'Yoga', ''),
(440, 'Wide Legged Forward Fall', 'Primary Shoulders', '', '', 'Hold', 'Start standing at the top of your mat with your feet wide apart., Interlace your fingers behind your back and inhale to expand your chest., Exhale and fold forward from your hips, bringing your hands up and over your head towards the ground., Hold for a few breaths and then release the hands and come back up to standing.', 'Yoga', 'Beginner'),
(441, 'Puppy Pose', 'Primary Shoulders Secondary Lats', '', '', 'Hold', 'Begin in a tabletop position with your hands and knees on the ground., Walk your hands forward as you lower your chest towards the ground., Keep your hips over your knees and extend your arms out in front of you., Hold for a few breaths and then release.', 'Yoga', 'Beginner'),
(442, 'Reverse Prayer', 'Primary Shoulders', '', '', 'Hold', 'Start seated or standing with your arms at your sides., Bring your hands behind your back and interlace your fingers with your palms facing up., Inhale and draw your shoulder blades together, lifting your chest., Exhale and gently press your hands down towards the floor as you continue to lift your chest.', 'Yoga', 'Beginner'),
(443, 'Reverse Prayer Variation', 'Primary Shoulders', '', '', '', 'Stand with a straight back and tall Chest, Bring your hands behind your back, making fists, Bring your two fists together in the center of your back', 'Yoga', ''),
(444, 'Headstand', 'Primary Shoulders', '', '', 'Hold', 'Begin on your hands and knees with your wrists under your shoulders and your knees under your hips., Place your forearms on the floor and interlace your fingers., Place the crown of your head on the floor and tuck your toes under., Walk your feet towards your head and lift your hips up., Straighten your legs and find your balance. Hold for several breaths and then release.', 'Yoga', 'Advanced'),
(445, 'Machine Pulldown', 'Primary Lats', '', 'Overhand', 'Pull', 'Grip the bar with the palms facing forward, your hands need to be spaced out at a distance wider than shoulder width., As you have both arms extended in front of you holding the bar, bring your torso back around 30 degrees while sticking your chest out., Pull the bar down to about chin level or a little lower in a smooth movement whilst squeezing the shoulder blades together., After a second of squeezing, slowly raise the bar back to the starting position when your arms are fully extended.', 'Machine', 'Beginner'),
(446, 'Machine Seated Cable Row', 'Primary Lats Secondary Biceps Tertiary Mid back', '', 'Overhand Underhand Neutral', 'Pull', 'Sit with your back straight on the machine and grip the handles., Pull the handles back using your arms. Your legs and torso should be at a 90Â° angle. Push out your chest., Pull the handles towards your body until your hands are beside your abdomen.', 'Machine', 'Beginner'),
(447, 'Pull Ups', 'Primary Lats Secondary Mid back Tertiary Biceps', 'https://www.youtube.com/embed/y6x0AIhgjsM', 'Overhand', 'Pull', 'Grasp the bar with an overhand grip, arms and shoulders fully extended., Pull your body up until your chin is above the bar., Lower your body back to starting position.', 'Bodyweight', 'Intermediate'),
(448, 'Dumbbell Row Bilateral', 'Primary Lats Mid back Secondary Biceps Tertiary Lower back', '', '', '', 'Grab both dumbbells and hinge forward at the hips. Make sure you keep a flat back., \"The closer your torso is to parallel with the ground the longer the range of motion will be at your shoulder. The better the results youll get from the exercise.\"\"', 'Dumbbells', 'Intermediate'),
(449, 'Barbell Bent Over Row', 'Primary Lats Secondary Biceps Tertiary Forearms', 'https://www.youtube.com/embed/tRck-5tE8mU', 'Underhand', 'Pull', 'Grab a barbell with a shoulder width pronated or supinated grip., Bend forward at your hips while maintaining a flat back., Pull the weight toward your upper abdomen., Lower the weight in a controlled manner and repeat.', 'Barbell', 'Intermediate'),
(450, 'Kettlebell Row', 'Primary Lats Mid back Secondary Biceps Tertiary Forearms', '', '', '', 'Stand with your feet shoulder width apart holding the kettlebell with both hands in front of your thighs., Bend forward at the hips bringing the kettlebell to the floor while you slightly bend your knees, keeping your back straight., Lift the kettlebell upwards towards your chest and lower - repeat.', 'Kettlebells', 'Beginner'),
(451, 'Kettlebell Shoulder Extension', 'Primary Lats', '', '', '', 'Stand with your feet shoulder width apart bending forward at the hips as far as comfortable. Place off-hand on the same side thigh for stability., Hold the kettlebell in one hand, straight armed and pointing towards the floor., Keeping your arm straight, swing the kettlebell behind you as far as comfortable., Return to the starting position and repeat.', 'Kettlebells', 'Intermediate'),
(452, 'Kettlebell Single Arm Row', 'Primary Lats', '', '', '', 'Stand with your feet shoulder width apart, shifting one foot behind you. Hold the kettlebell in the same hand as the leg shifted backwards., Bend forwards at the hips bringing the kettlebell to the floor while you slightly bend your knee, keeping your back straight., Lift the kettlebell upwards towards your chest and lower - repeat.', 'Kettlebells', 'Beginner'),
(453, 'Kettlebell Pullover', 'Primary Lats', '', '', '', 'Rest your upper back on the bench and place soles of your feet firmly on the floor keeping torso and thighs suspended and parallel to the floor., Hold the kettlebell with both hands above your chest, keeping your arms straight., Keeping your arms straight, move the kettlebell in the direction of your head until your arms are parallel to the floor., Return to starting to position and repeat.', 'Kettlebells', 'Advanced'),
(454, 'Lats Stretch Variation Three', 'Primary Lats', '', 'None', '', 'Find a wall to lean against, lay your tricep against the wall with your hand behind your back., Slowly lean into the wall., Return to starting position, then switch sides.', 'Stretches', 'Beginner'),
(455, 'Lats Stretch Variation Two', 'Primary Lats', '', 'None', '', 'Stand up right and raise your hand to a full stretch above your head., Slowly lean across your body and trace the opposite hand down your leg., Hold at the bottom of the stretch, return to starting position and then switch sides.', 'Stretches', 'Beginner'),
(456, 'Lats Stretch Variation One', 'Primary Lats', '', 'None', '', 'Place both hands on the wall in front of you., Slowly lower your body until you feel the stretch., Hold at the bottom of the stretch, return to starting position.', 'Stretches', 'Beginner'),
(457, 'Inverted Row', 'Primary Lats Mid back Secondary Biceps Tertiary Forearms', '', 'Overhand Underhand', 'Pull', 'Lay underneath a fixed horizontal bar and grab the bar with a wide overhand grip., Pull your body straight up to the bar., Return to the starting position, your arms fully extended. Repeat.', 'Bodyweight', 'Beginner'),
(458, 'Cable Lat Prayer', 'Primary Lats', '', 'Overhand', '', 'You can use any attachment. Cable should be set all the way to the top of the machine., Grab your attachment and walk a few steps back. Push your butt back to where you are leaning forward. Your ears should be between your arms., Initiate the movement with your shoulders and not your elbows. As you flex at the shoulder joint push your hips forward until your hips meet the attachment.', 'Cables', 'Advanced'),
(459, 'Cable Pullover', 'Primary Lats', '', 'Overhand', 'Pull', 'You can use any attachment. Cable should be set all the way to the top of the machine., Grab your attachment and walk a few steps back. Push your butt back to where you are leaning forward somewhat., Initiate the movement with your shoulders and not your elbows. Imagine trying to pull the attachment through your thighs at the bottom.', 'Cables', 'Intermediate'),
(460, 'Cable Pull In', 'Primary Lats', '', 'Overhand', 'Pull', 'Use a handle attachment set all the way to the top of the cable crossover machine., Walk a few steps away from the cable machine. Face your body sideways., Face your palm up toward the ceiling. Then pull your elbow in toward your rib cage.', 'Cables', 'Intermediate'),
(461, 'Band Kneeling Single Arm Pulldown', 'Primary Lats Secondary Biceps', '', '', '', 'Place the band at about eye level. Fall into a kneeling position., The knee that is on the ground should be the same side as the working arm. Stick your chest out., And then pull your elbow straight back. Flex your lats and then extend into the starting position.', 'Band', 'Beginner'),
(462, 'Band Pullover', 'Primary Lats', '', '', '', 'Place the band just above our level. Take a few steps away until the band is taut., Push your hips back slightly. Extend and flex at the shoulder joint.', 'Band', 'Intermediate'),
(463, 'Band Row', 'Primary Lats', '', '', '', 'Stand on your band with both feet., Hinge forward at the hips while keeping a flat back., Let your arms hang freely, then pull your elbows straight back toward the ceiling.', 'Band', 'Beginner'),
(464, 'Dumbbell Row Unilateral', 'Primary Lats Mid back Secondary Biceps Tertiary Forearms', 'https://www.youtube.com/embed/r4-3p0KgEA8', 'Neutral', 'Pull', 'Brace your off arm against something stable (bench, box). Stagger your stance so your leg on the side of your working arm is back., Try to get your torso to parallel with the ground. That will extend your range of motion., \"Let your arm hang freely and then pull your elbow back. Imagine youve got a tennis ball in your armpit and squeeze it each rep.\"\"\"', 'Dumbbells', 'Beginner'),
(465, 'Barbell Long Landmine Row', 'Primary Lats Secondary Biceps Tertiary Forearms', '', 'Neutral', 'Pull', 'Position a barbell in a landmine or corner of a room and stand facing it with your feet shoulder-width apart., Grasp the end of the barbell with an overhand grip, keeping your arms straight and your shoulders relaxed., Bend at the hips and knees to lower your torso until it is almost parallel to the ground., Pull the barbell towards your chest, squeezing your shoulder blades together as you row the weight up.', 'Barbell', 'Advanced'),
(466, 'Barbell Meadows Row', 'Primary Lats Secondary Biceps Tertiary Forearms', '', 'Overhand', 'Pull', 'Position a barbell in a landmine or corner of a room and stand facing sideways with a staggered stance and your feet shoulder-width apart., Grasp the end of the barbell with an overhand grip, keeping your arms straight and your shoulders relaxed., Bend at the hips and knees to lower your torso until it is almost parallel to the ground., Pull the barbell towards your chest, squeezing your shoulder blades together as you row the weight up., *Named after the late John Meadows', 'Barbell', 'Intermediate'),
(467, 'Barbell Pronated Pendlay Row', 'Primary Lats Secondary Biceps Tertiary Lower back', '', 'Overhand', 'Pull', 'Start hinged forward with a flat back and the plates resting on the floor. Make sure your elbows are fully extended., Pull the bar to your sternum., Return the plates back to the floor and let the bar come to a full dead stop., Then initiate the next rep.', 'Barbell', 'Advanced'),
(468, 'Barbell Pronated Row', 'Primary Lats Secondary Biceps Tertiary Forearms', '', 'Overhand', 'Pull', 'Grab the barbell with an overhand grip. Hinge forward while keeping your back flat until your torso is about parallel with the ground., Let your arms hang freely., Pull the bar to your torso while tucking your elbows in slightly., Extend your elbows fully before initiating the next rep.', 'Barbell', 'Beginner'),
(469, 'Barbell Supinated Pendlay Row', 'Primary Lats Secondary Biceps Tertiary Lower back', '', 'Underhand', 'Pull', 'Begin by standing in front of a barbell with your feet about hip-width apart and your palms facing towards your body., Bend at the hips and knees to grab the barbell with a pronated (overhand) grip, positioning the barbell about an inch away from your shins., Keeping your back straight and your core engaged, use your back muscles to lift the barbell straight up towards your chest., As the barbell reaches your chest, rotate your wrists so that your palms are facing towards your body (supinated grip), and then lower the barbell back to the starting position.', 'Barbell', 'Advanced'),
(470, 'Barbell Supinated Row', 'Primary Lats Lower back Secondary Biceps', '', 'Underhand', 'Pull', 'Begin by standing in front of a barbell with your feet about hip-width apart., Bend at the hips and knees to grab the barbell with a supinated (underhand) grip, positioning the barbell about an inch away from your shins., Keeping your back straight and your core engaged, use your back muscles to lift the barbell straight up towards your chest., As the barbell reaches your chest, pause briefly, and then lower the barbell back to the starting position.', 'Barbell', 'Advanced'),
(471, 'Dumbbell Pullover', 'Primary Lats', '', 'Neutral', 'Pull', 'Grab a dumbbell and hold with both hands. Point your dumbbell at the ceiling., Flex at the shoulder joint while keeping your elbows mostly extended., Flex until you feel a stretch in your lats then bring the dumbbell back to the ceiling.', 'Dumbbells', 'Intermediate'),
(472, 'Dumbbell Alternating Pendlay Row', 'Primary Lats Secondary Biceps', '', 'Neutral', 'Pull', 'The dumbbells should be set on the ground. Hinge forward while keeping a flat back., Pull one elbow back toward the ceiling and then return the dumbbell to the ground softly., Then pull with the other arm.', 'Dumbbells', 'Advanced'),
(473, 'Dumbbell Kneeling Single Arm Row', 'Primary Lats Secondary Biceps', '', 'Neutral', 'Pull', 'Place one hand and one knee on a bench. Make sure you keep a flat back., Pull the dumbbell toward the ceiling., Make sure to avoid any jerking or rotating.', 'Dumbbells', 'Beginner'),
(474, 'Dumbbell Laying Incline Row', 'Primary Lats Secondary Biceps', '', 'Neutral', 'Pull', 'Lay on an incline bench that is between 30-45 degrees high., Let your arms hang freely. Pull your elbows straight back toward the ceiling.', 'Dumbbells', 'Beginner'),
(475, 'Dumbbell Pendlay Row', 'Primary Lats Secondary Biceps', '', 'Neutral', 'Pull', 'The dumbbells should be placed on the floor., Hinge forward with a flat back and grab the dumbbells., Pull both dumbbells back and your elbows toward the ceiling., Return the dumbbells back to the floor softly on each rep.', 'Dumbbells', 'Intermediate'),
(476, 'Dumbbell Renegade Row', 'Primary Lats Secondary Abdominals', '', 'Neutral', 'Pull', 'Start by getting into a plank position with a dumbbell in each hand. Your feet should be shoulder-width apart and your hands should be placed on the dumbbells, with your palms facing each other., From this starting position, row one dumbbell up towards your chest, keeping your elbow close to your body and your core engaged., Slowly lower the dumbbell back down to the starting position, and repeat on the other side., Make sure to keep your body straight and your hips level throughout the movement.', 'Dumbbells', 'Intermediate'),
(477, 'Dumbbell Shoulder Extension', 'Primary Lats', '', 'Neutral', 'Pull', 'Start by hinging over with your feet shoulder-width apart and holding a dumbbell in each hand., Bring the dumbbells to your sides, with your palms facing forward and your elbows slightly bent., Make sure to keep your core engaged and your back straight throughout the movement.', 'Dumbbells', 'Intermediate'),
(478, 'Dumbbell Single Arm Row', 'Primary Lats Secondary Biceps', '', 'Neutral', 'Pull', 'Make sure to keep your elbow close to your body and your core engaged throughout the movement., Start by standing in front of a flat bench with your feet shoulder-width apart and holding a dumbbell in one hand., Place your other hand on the bench for support, and bend at the waist so that your chest is parallel to the ground., From this starting position, row the dumbbell up towards your chest, squeezing your shoulder blade together as you do so.', 'Dumbbells', 'Beginner'),
(479, 'Dumbbell Single Arm Row Knee', 'Primary Lats Secondary Biceps', '', 'Neutral', 'Pull', 'Start by placing your elbow on one knee., Hold a dumbbell in your other hand, with your palm facing inwards towards your body., From this starting position, row the dumbbell up towards your chest, keeping your elbow close to your body and your core engaged.', 'Dumbbells', 'Beginner'),
(480, 'Plate Alternating Deadstop Row', 'Primary Lats', '', 'Neutral', 'Pull', 'Stand with your feet shoulder width apart. Hold each plate to the side of you., Bend forward at the hips bringing the plates to the floor while you slightly bend your knees, keeping your back straight., One at a time, lift the plate up. Return the plate to the floor and do the other side.', 'Plate', 'Intermediate'),
(481, 'Plate Glute Bridge to Pullover', 'Primary Lats Secondary Glutes', '', 'Neutral', 'Pull', 'Lay flat on the ground with a plate on your chest., Push your hips up toward the ceiling and maintain that extended position., Extend your elbows mostly, break at the shoulder joint while maintaining hip extension and the slight bend in your elbows., Let the plate tap the floor (or get close) and then pull the plate back into the starting position.', 'Plate', 'Beginner'),
(482, 'Lawnmower Row', 'Primary Lats Secondary Biceps', '', 'Rotating', 'Pull', 'Stand with feet staggered and one hand on a bench/box. Hold a dumbbell in the opposing hand., Hinge forward at the hips, keeping the back flat, and eyes facing forward. The weight should hang directly below the shoulders., Start with an overhand grip, as you pull rotate the dumbbell. When your upper arm reaches your side your palm should be facing forward.', 'Dumbbells', 'Intermediate'),
(483, 'Dumbbell Knee Lawnmower Row', 'Primary Lats Secondary Biceps', '', 'Rotating', 'Pull', 'Stand with feet staggered and one hand on your knee. Hold a dumbbell in the opposing hand., Hinge forward at the hips, keeping the back flat, and eyes facing forward. The weight should hang directly below the shoulders., Start with an overhand grip, as you pull rotate the dumbbell. When your upper arm reaches your side your palm should be facing forward.', 'Dumbbells', 'Intermediate'),
(484, 'Dumbbell Supinated Row', 'Primary Lats Secondary Biceps', '', 'Underhand', 'Pull', 'Grab both dumbbells and hinge forward at the hips. Make sure you keep a flat back., \"The closer your torso is to parallel with the ground the longer the range of motion will be at your shoulder. The better the results youll get from the exercise.\"\"', 'Dumbbells', 'Beginner'),
(485, 'TRX Rotating Row', 'Primary Lats Secondary Biceps', '', 'Rotating', 'Pull', 'Grab the TRX straps and lean away from the anchor point until the strap is taut., Start with an overhand grip. Pull your elbows back and rotate your grip., By the time your elbows reach your side, your palms should be facing upwards.', 'TRX', 'Beginner'),
(486, 'TRX Single Arm row', 'Primary Lats', '', 'Neutral', 'Pull', 'Hold the TRX strap with one hand., Lean back until the strap is taut. Fully extend your elbow., Pull your elbow behind the body. Make sure to avoid twisting. Try to keep your shoulders even.', 'TRX', 'Intermediate'),
(487, 'Chin Ups', 'Primary Lats Secondary Biceps Tertiary Forearms', 'https://www.youtube.com/embed/y6x0AIhgjsM', 'Underhand', 'Pull', 'Grab the bar shoulder width apart with a supinated grip (palms facing you), With your body hanging and arms fully extended, pull yourself up until your chin is past the bar., Slowly return to starting position. Repeat.', 'Bodyweight', 'Intermediate');
INSERT INTO `workout` (`ID`, `exercise_name`, `target`, `youtubeURL`, `Grips`, `Force`, `steps`, `Category`, `Difficulty`) VALUES
(488, 'Barbell Landmine Row', 'Primary Lats Mid back Secondary Biceps Tertiary Forearms', '', 'Overhand', 'Pull', 'Position a barbell in a landmine or corner of a room and stand facing it with your feet shoulder-width apart., Grasp the end of the barbell with a neutral grip, keeping your arms straight and your shoulders relaxed., Bend at the hips and knees to lower your torso until it is almost parallel to the ground., Pull the barbell towards your chest, squeezing your shoulder blades together as you row the weight up.', 'Barbell', 'Intermediate'),
(489, 'Plate Pinch Grip Row', 'Primary Forearms Secondary Lats Tertiary Biceps', '', 'Pinch Grip', 'Pull', 'Make sure to keep your elbow close to your body and your core engaged throughout the movement., Bend forward at your hips while maintaining a flat back., Pull the plate toward your upper abdomen., Lower the weight in a controlled manner and repeat.', 'Plate', 'Intermediate'),
(490, 'Plate Pinch Grip Deadlift', 'Primary Lats Forearms Secondary Hamstrings Glutes Tertiary Lower back', '', 'Pinch Grip', '', 'Stand facing the plate with your feet shoulder-width apart and the plate positioned between your feet., Bend down and grasp the plate from the top edges with both hands, making sure your thumbs and fingers are evenly spaced., Keep your back straight, chest up and core engaged, and stand up with the plate, keeping it close to your body., Lower the plate back down to the ground, keeping control and maintaining good posture.', 'Plate', ''),
(491, 'Pushup to Renegade Row', 'Primary Chest Secondary Lats Tertiary Abdominals', '', 'Neutral', 'Push', 'Start in a high plank position with a pair of dumbbells positioned under your shoulders. Your hands should grip the dumbbells, palms facing each other., Keep your core engaged and your back straight as you row one dumbbell up towards your hip, keeping your elbow close to your body., Lower the dumbbell back down to the starting position and repeat on the other side.', 'Dumbbells', 'Intermediate'),
(492, 'TRX Ab Rollout', 'Primary Abdominals Secondary Lats Tertiary Lower back', '', 'Overhand', 'Pull', 'Grasp the TRX handles with both hands, keeping your arms extended and your shoulders away from your ears., Keep your abs engaged, roll forward onto your toes, and extend your arms in front of you., Slowly lower your body down to the ground, keeping your arms straight and your abs engaged., When your head gets between your arms, pause for a moment and then use your abs to pull your body back up to the starting position.', 'TRX', 'Advanced'),
(493, 'TRX Kneeling Ab Rollout', 'Primary Abdominals Secondary Lats', '', 'Overhand', 'Pull', 'Begin in a kneeling position with the TRX straps adjusted to mid-calf length., Grasp the TRX handles with both hands, keeping your arms extended and your shoulders away from your ears., Keep your abs engaged, roll forward onto your toes, and extend your arms in front of you., Slowly lower your body down to the ground, keeping your arms straight and your abs engaged.', 'TRX', 'Intermediate'),
(494, 'Child Pose Arms Extended', 'Primary Lats Secondary Biceps Tertiary Lower back', '', '', 'Hold', 'Begin in a tabletop position with your hands and knees on the ground., Sit back onto your heels and extend your arms out in front of you., Rest your forehead on the ground., Hold for a few breaths and then release.', 'Yoga', 'Beginner'),
(495, 'Child Pose Arms Extended Left Right', 'Primary Lats Secondary Obliques', '', '', 'Hold', 'Begin in a tabletop position with your hands and knees on the ground., Sit back onto your heels and extend your arms out in front of you to one side., Rest your forehead on the ground., Hold for a few breaths and then repeat on the other side.', 'Yoga', 'Beginner'),
(496, 'Child Pose Arms On Side', 'Primary Lats Secondary Shoulders Tertiary Lower back', '', '', 'Hold', 'Begin in a tabletop position with your hands and knees on the ground., Sit back onto your heels and place your arms at your sides with your palms facing up., Rest your forehead on the ground., Hold for a few breaths and then release.', 'Yoga', 'Beginner'),
(497, 'extended-side-angle-pose', 'Primary Lats', '', '', 'Hold', 'Begin in a lunge position with your right foot forward and your left foot back., Place your right hand on a block or the ground on the outside of your right foot., Reach your left arm overhead and then round your spine, bringing your left hand to your right ear., Look down towards your right foot as you reach your left arm towards the front of the room, feeling a stretch in your side body. Hold for a few breaths and then switch sides.', 'Yoga', 'Beginner'),
(498, 'Extended side Angle Pose With Block', 'Primary Lats Secondary Obliques', '', '', 'Hold', 'Begin in a lunge position with your right foot forward and your left foot back., Place a block on the outside of your right foot and place your right hand on top of it., Look down towards your right foot as you reach your left arm towards the front of the room, feeling a stretch in your side body. Hold for a few breaths and then switch sides.', 'Yoga', 'Beginner'),
(499, 'Gate Pose Rounding Spine Looking Up', 'Primary Lats Secondary Obliques', '', '', 'Hold', 'Begin in a kneeling position with your knees hip-width apart., Extend your right leg out to the side, placing your foot flat on the ground., Reach your right arm overhead and then round your spine, bringing your right hand to your left ear., Look down towards your left foot as you reach your left arm towards your right foot, feeling a stretch in your side body. Hold for a few breaths and then switch sides.', 'Yoga', 'Intermediate'),
(500, 'Two Arms Cow Face Pose', 'Primary Lats Secondary Triceps', '', '', 'Hold', 'Begin in a comfortable seated position on your mat, with your legs crossed and your spine straight., Extend your right arm up toward the ceiling and bend it at the elbow, bringing your right hand behind your head., Reach your left arm behind your back and bend it at the elbow, bringing your left hand up between your shoulder blades., \"Clasp your hands behind your back', 'Yoga', 'Beginner'),
(501, 'Barbell Stiff Leg Deadlifts', 'Primary Hamstrings', 'https://www.youtube.com/embed/qWb5Zr98B-M', 'Overhand Mixed', 'Pull', 'Stand with a barbell at your shins with your feet shoulder width apart., Bend forward at your hips and keep your knees as fully extended as possible., Grab the barbell and then extend your hips while maintaining a straight back., From the standing position, lower the weight in a controlled manner., You can either lower the weight to the floor or before you touch the floor, depending on your mobility.', 'Barbell', 'Intermediate'),
(502, 'Machine Hamstring Curl', 'Primary Hamstrings', '', 'None', 'Pull', 'Lay down on the machine, placing your legs beneath the padded lever. Position your legs so that the padded lever is below your calve muscles., Support yourself by grabbing the side handles of the machine, and slowly raise the weight with your legs, toes pointed straight., Pause at the apex of the motion, then slowly return to starting position.', 'Machine', 'Beginner'),
(503, 'Ball Hamstring Curl', 'Primary Hamstrings', '', '', '', 'Lay on your back with a stability ball underneath your feet., Fully extend your knees and hips simultaneously., Flex your knees while keeping your hips extended., Flex your hamstrings and then return to the starting position.', 'Bodyweight', 'Intermediate'),
(504, 'Kettlebell Deadlift', 'Primary Hamstrings Mid back Secondary Glutes Tertiary Lower back', '', '', '', 'Stand with your feet shoulder width apart holding the kettlebell with both hands in front of your thighs., Bend forward at the hips bringing the kettlebell to the floor while you slightly bend your knees, keeping your back straight., Return to standing position and repeat.', 'Kettlebells', 'Beginner'),
(505, 'Kettlebell Swing', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Overhand', 'Pull', 'Stand with your feet slightly wider than shoulder width apart holding the kettlebell between your legs, with your knees slightly bent., Keeping your back straight, swing the kettlebell upwards until the kettlebell is level with your chest and carefully lower., Repeat', 'Kettlebells', 'Intermediate'),
(506, 'Kettlebell Single Leg Deadlift', 'Primary Hamstrings', '', '', '', 'Stand with your feet shoulder width apart, lifting one foot off the floor behind you. Hold the kettlebell in the same arm as the lifted leg., Bend forward at the hips bringing the kettlebell to the floor while you extend your lifted leg behind you, keeping your back straight - holding out your arm to the side for balance., Return to the upright position and repeat.', 'Kettlebells', 'Advanced'),
(507, 'Kettlebell Staggered Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Stand with your feet shoulder width apart, shifting one foot behind you. Hold the kettlebell in both hands in front of your thighs., Bend forward at the hips bringing the kettlebell to the floor while you slightly bend your knees and keep your back straight., Return to the upright position and repeat.', 'Kettlebells', 'Beginner'),
(508, 'Hamstrings Stretch Variation Four', 'Primary Hamstrings', '', 'None', '', 'Sit on the floor with your legs out in front of you., Reach forward to try and touch your toes. Reach as far forward as you can go., Hold the stretch for a few seconds then return to starting position.', 'Stretches', 'Beginner'),
(509, 'Hamstrings Stretch Variation Three', 'Primary Hamstrings', '', 'None', '', 'Sit on the floor with one leg outstretched and the other bent at the knee, foot resting on the opposite inner thigh., Reach forward to touch the toes of the outstretched leg., Hold the stretch for a few seconds then return to starting position.', 'Stretches', 'Beginner'),
(510, 'Hamstrings Stretch Variation Two', 'Primary Hamstrings', '', 'None', '', 'Bend your rear leg as much as you can, keeping the front leg straight., Hold the stretch for a few seconds then return to starting position., Stand with one foot in front of the other.', 'Stretches', 'Beginner'),
(511, 'Hamstrings Stretch Variation One', 'Primary Hamstrings', '', 'None', '', 'Lie down with your legs reaching up the wall. your bum should be slightly off the floor., Bend one leg and leave the other outstretched., Push your thigh towards the wall., Hold the stretch for a few seconds then return to starting position.', 'Stretches', 'Beginner'),
(512, 'Good Mornings', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Stand with your feet a little past shoulder width apart with a slight bend at your knee. Place your hands behind the back of your head., Keeping your back straight, rotate your hips to bring your shoulders down towards the floor. Stop when your chest is parallel with the floor.', 'Bodyweight', 'Beginner'),
(513, 'Kickbacks', 'Primary Hamstrings', '', '', '', 'Get down on all fours and position your hands under your shoulders and your knees under your hips., Kick back with one leg and squeeze your glutes., Slowly return to starting position by bending your knee and lowering your leg., Switch Legs. Repeat.', 'Bodyweight', 'Beginner'),
(514, 'Single Legged Romanian Deadlifts', 'Primary Hamstrings', 'https://www.youtube.com/embed/qWb5Zr98B-M', '', '', 'Stand with your feet shoulder-width apart and knees slightly bent and raise one leg., \"Without changing the bend in your knee', 'Bodyweight', 'Beginner'),
(515, 'Nordic Hamstring Curl', 'Primary Hamstrings', '', 'None', 'Pull', 'Go to your knees and ask someone to hold your ankles., Put your hands in front of you, With a slight bend in your knees slowly bring your body to the ground (slower is better), Push up and reset to the starting position.', 'Bodyweight', 'Advanced'),
(516, 'Cable Romanian Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', 'https://www.youtube.com/embed/qWb5Zr98B-M', 'Overhand', '', 'Start with a straight bar or ez bar attachment set to the bottom of the machine. Squat the weight up into a standing position and walk a few steps back, \"Push your butt back to initiate the movement and then push your butt forward until youre back in a standing position.\"\"', 'Cables', 'Intermediate'),
(517, 'Cable Single Leg Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Overhand', '', 'Start with a straight bar or easy bar attachment. Squat the weight up into a standing position and walk a few steps back., \"Push your butt back to initiate the movement while lifting one of your legs.\\nThen push your butt forward until youre back in a standing position.\"\"', 'Cables', 'Advanced'),
(518, 'Cable Pull Through', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Neutral', 'Pull', 'This exercise works best with a rope attachment but can be done with anything. The attachment should be set to the bottom of the machine., Face away from the cable machine, straddle the cable itself, and grab the attachment.\\nWalk a few steps away., Break at the hips while maintaining a flat back and mostly extended knees., Push hips forward until you are back in a standing position.', 'Cables', 'Beginner'),
(519, 'Cable Hamstring Curl', 'Primary Hamstrings', '', '', '', 'Use an ankle attachment with the cable set all the way to the bottom of the machine., Stand upright or try bending slightly forward., Flex at the knee and pull your ankle straight back.', 'Cables', 'Advanced'),
(520, 'Band Good Morning', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Anchor the band at hip level. Take a few steps away until the band is taut., Push your hips back while keeping your back flat until you feel a stretch in your hamstrings.', 'Band', 'Beginner'),
(521, 'Band Pullthrough', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Anchor the band to the lowest point possible. Straddle the band and then grab with both hands., Take a few steps away until the band is taut., Push your butt back until you feel a stretch in your hamstrings. Then extend the hips back into the starting position.', 'Band', 'Beginner'),
(522, 'Band Romanian Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', 'https://www.youtube.com/embed/qWb5Zr98B-M', '', '', 'Stand on your band with both feet., Hinge forward at the hips while keeping a flat back., Hinge until you feel a stretch in your hamstrings., Push your hips back forward until you are in a standing position.', 'Band', 'Intermediate'),
(523, 'Dumbbell Goblet Good Morning', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Neutral', 'Pull', 'Hold the dumbbell in a goblet position. With your elbows directly under your fists., Push your hips back while leaving your knees MOSTLY extended. You should feel a stretch in your hamstrings., \"When you feel the stretch push your hips forward until youre back in a standing position.\"\"\"', 'Dumbbells', 'Advanced'),
(524, 'Dumbbell Romanian Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', 'https://www.youtube.com/embed/qWb5Zr98B-M', 'Overhand Neutral', 'Pull', 'Stand with a shoulder width stance. Push your butt back while leaving your knees MOSTLY extended., You should feel a stretch in your hamstrings. When you feel the stretch, push your hips forward to complete the rep., Do not push your hips all the way forward. This will hyperextend your spine. Just go to a normal standing position.', 'Dumbbells', 'Intermediate'),
(525, 'Barbell High Bar Good Morning', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', '\"Place the bar on your traps. You may need a pad so the bar doesnt dig into your cervical spine.\"\"', 'Barbell', 'Advanced'),
(526, 'Barbell Low Bar Good Morning', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Overhand', 'Pull', 'Place the bar between your traps and rear delts. Pull your shoulder blades back to give the bar a shelf to sit on., \"Push your hips back until you feel a stretch in your hamstrings. This means youre nearing the end of your Range of Motion with out rounding your spine.\"\"', 'Barbell', 'Advanced'),
(527, 'Barbell Staggered Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Place one foot behind. You should only be on your toes on that back foot. Your front foot should be planted flat the entire time., Bend your back knee and push your hips back. Look for a stretch in your front hamstring., \"When you feel the stretch', 'Barbell', 'Advanced'),
(528, 'Barbell Single Leg Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Start by pushing your hips straight back and extending your non-working leg behind you. It is ok to touch your other foot back to the ground each rep., \"Leave your knee mostly extended. Look for a stretch in your hamstring. When you feel the stretch', 'Barbell', 'Advanced'),
(529, 'Barbell Romanian Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', 'https://www.youtube.com/embed/qWb5Zr98B-M', '', '', 'Take a shoulder width, double overhand or mixed grip., Push your hips back while leaving your knees mostly extended. Look for a stretch in your hamstrings., \"When you feel the stretch', 'Barbell', 'Intermediate'),
(530, 'Kettlebell Goblet Good Morning', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Stand with your feet shoulder width apart holding the kettlebell with both hands in front of your chest., Bend forward at the hips keeping your back and knees straight and the kettlebell close to your chest., Return to standing position and thrust your pelvis forward.', 'Kettlebells', 'Beginner'),
(531, 'Barbell Snatch Grip Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', 'Pull', 'Start by standing with your feet about hip-width apart, with your toes pointed slightly outward., Place a barbell on the floor in front of you, with the weight plates on either side. Make sure that the bar is directly in line with your feet., Bend down and grab the barbell with an overhand grip, your hands much wider than shoulder-width apart. Keep your back straight and your shoulders down as you grip the bar., Stand up straight, lifting the barbell off the ground and pulling it toward your hips. Keep your elbows close to your body and your shoulders down as you do this., Slowly lower the barbell back to the ground, making sure to keep your back straight and your shoulders down throughout the movement.', 'Barbell', 'Advanced'),
(532, 'Barbell Snatch Grip Romanian Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', 'https://www.youtube.com/embed/qWb5Zr98B-M', 'Overhand Mixed', 'Pull', '\"Start by standing with your feet about hip-width apart. The bar should be in a rack or box so its lifted off the ground.\"\"', 'Barbell', 'Advanced'),
(533, 'Barbell Zercher Good Morning', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'None', 'Pull', 'Begin by standing with your feet about hip-width apart and holding a barbell in the crook of your elbows, with your palms facing towards your body., Keeping your core engaged and your back straight, bend at the hips and lower your torso forward until it is nearly parallel to the ground., Push through your heels to extend your hips and raise your body back to the starting position.', 'Barbell', 'Advanced'),
(534, 'Dumbbell Leg Curl', 'Primary Hamstrings', '', 'None', 'Pull', 'Lay flat on a bench with a dumbbell between your feet., Flex your knees and raise the dumbbell until the soles of your shoes are pointed at the ceiling.', 'Dumbbells', 'Beginner'),
(535, 'Dumbbell Single Leg Single Arm Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Overhand Neutral', 'Pull', 'Start by standing on one leg with a dumbbell in one hand., Slowly bend at the hips and lower the dumbbell towards the ground while extending your free leg behind you., Keep your core engaged and your back straight as you lower the weight., Once the dumbbell is close to the ground, push through your heel to return to the starting position.', 'Dumbbells', 'Advanced'),
(536, 'Dumbbell Single Leg Stiff Leg Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Overhand Neutral', 'Pull', 'Start by standing on one leg with your feet shoulder-width apart and holding two dumbbells., From this starting position, hinge at the hips to lower the dumbbell towards the ground, keeping your back straight and your core engaged., Once you feel a stretch in your hamstring, slowly return to the starting position., Make sure to keep your standing leg slightly bent and avoid rounding your back as you lower the dumbbell.', 'Dumbbells', 'Intermediate'),
(537, 'Dumbbell Staggered Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Overhand Neutral', 'Pull', 'Start by standing with your feet shoulder-width apart and holding a dumbbell in each hand., Place one foot slightly behind the other, with the heel of your back foot elevated on a step or weight plate., From this starting position, bend at the hips and knees to lower the dumbbells towards the ground, keeping your back straight and your core engaged., Once the dumbbells are below your knees, drive through your heels to stand back up to the starting position.', 'Dumbbells', 'Intermediate'),
(538, 'Dumbbell Swing', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Overhand Neutral', 'Push', 'Start by standing with your feet shoulder-width apart and holding a dumbbell in both hands., Bend your knees slightly and hinge at the hips to swing the dumbbell between your legs., From this starting position, explosively extend your hips and swing the dumbbell up to chest height., Allow the momentum of the swing to carry the dumbbell back down between your legs, and then repeat the movement for the desired number of repetitions.', 'Dumbbells', 'Beginner'),
(539, 'Plate Single Leg Pinch Grip Deadlift', 'Primary Hamstrings Forearms Secondary Glutes Tertiary Lower back', '', 'Pinch Grip', 'Pull', 'Stand with your feet shoulder-width apart and facing forwards. Grip the sides of a weight plate with your palms facing down., Balance on one foot, as you raise the weight keep your arms and back straight, while you bring your other foot back towards the ground, Keep four raised leg straight and in alignment with your back as you lower the plate back down to the starting position.', 'Plate', 'Intermediate'),
(540, 'Plate Single Leg Deadlift', 'Primary Quads Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Engage your core and straighten your legs to lift the plate off the ground., Stand on one leg, keeping the other leg off the ground, while holding the plate in front of your body., Keep your back straight and maintain a neutral head position while looking straight ahead., Lower the plate back down to the ground while keeping your balance on one leg.', 'Plate', 'Advanced'),
(541, 'Band Leg Curl', 'Primary Hamstrings', '', 'None', 'Pull', 'Loop the band around a sturdy anchor point such as a weightlifting rack or a door handle., Lie on your back with your heels placed inside the band. Make sure the band is centered under your heels and your feet are hip-width apart., Using your hamstrings, curl your heels towards your glutes while keeping your hips on the ground.', 'Band', 'Beginner'),
(542, 'Plate Staggered Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Neutral', 'Pull', 'Start by standing with your feet shoulder-width apart and holding a weight plate., Place one foot slightly behind the other, with the heel of your back foot elevated., From this starting position, bend at the hips and knees to lower the plate towards the ground, keeping your back straight and your core engaged., Once the plate is below your knees, drive through your heels to stand back up to the starting position.', 'Plate', 'Advanced'),
(543, 'Plate Staggered Pinch Grip Deadlift', 'Primary Hamstrings Forearms Secondary Glutes Tertiary Lower back', '', 'Pinch Grip', 'Pull', 'Start by standing with your feet shoulder-width apart and holding two plates with a pinch grip., Place one foot slightly behind the other, with the heel of your back foot elevated., From this starting position, bend at the hips and knees to lower the plates towards the ground, keeping your back straight and your core engaged.', 'Plate', 'Advanced'),
(544, 'TRX Hamstring Curl', 'Primary Hamstrings', '', 'None', 'Pull', 'Start by facing the TRX straps and positioning yourself in a push-up position with your hands on the ground and your feet in the TRX foot cradles., Keeping your core engaged and body in a straight line, bend your knees and bring your heels towards your glutes., Pause for a moment at the top of the movement, then slowly lower your legs back to the starting position.', 'TRX', 'Beginner'),
(545, 'Plate Good Morning', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Neutral', '', 'Stand with feet hip-width apart, the plate held behind the neck and the knees slightly bent., Bend forward at the hips, keeping the back straight, until the torso is parallel to the floor., Push through the hips to return to the starting position.', 'Plate', 'Intermediate'),
(546, 'Plate Good Morning Anterior', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Neutral', '', 'Stand with feet hip-width apart, the plate held in front of you, and the knees slightly bent., Bend forward at the hips, keeping the back straight, until the torso is parallel to the floor., Push through the hips to return to the starting position.', 'Plate', 'Intermediate'),
(547, 'Dumbbell Glute Bridge', 'Primary Quads Hamstrings Glutes', '', '', '', 'Lay flat on the ground with both feet planted. Make sure you keep your heels glued to the floor., Place the dumbbell on your hips. Push your hips toward the ceiling until you feel your glutes contract., Then return to the floor.', 'Dumbbells', 'Beginner'),
(548, 'Barbell Suitcase Deadlift', 'Primary Hamstrings Glutes Obliques Secondary Lower back Tertiary Forearms', '', '', 'Pull', 'Begin by standing with your feet hip-width apart and holding a barbell at one side of your body, with your palms facing your thighs., Keeping your core engaged and your back straight, bend at the hips and knees to lower the barbell down towards the ground., As the barbell reaches the ground, use your legs and glutes to push through your heels and extend your hips and knees to raise your body back to the starting position., Repeat the movement for the desired number of reps, then switch sides and repeat the exercise with the barbell on the opposite side of your body.', 'Barbell', 'Advanced'),
(549, 'Bodyweight Reverse Lunge', 'Primary Quads Hamstrings Secondary Glutes', '', 'None', 'Push', 'Push through your right heel to return to standing, bringing your left leg back to starting position., Repeat the movement on the other side, stepping backwards with your right leg and lowering your body until your right thigh is parallel to the ground and your left knee is bent at a 90-degree angle., Take a big step backwards with your left leg, lowering your body until your left thigh is parallel to the ground and your right knee is bent at a 90-degree angle.', 'Bodyweight', 'Beginner'),
(550, 'Half Monkey Pose', 'Primary Hamstrings', '', '', 'Hold', 'Start in a tabletop position with your hands and knees on the floor., Step your right foot forward, placing it between your hands., Straighten your right leg and flex your toes towards your face, keeping your hands on the floor for support., Hold for several breaths, then switch sides and repeat.', 'Yoga', 'Beginner'),
(551, 'Seated Forward Bend', 'Primary Hamstrings Secondary Lower back', '', '', 'Hold', 'Sit on the floor with your legs extended in front of you., Inhale and lengthen your spine, then exhale and fold forward from your hips, keeping your spine long., Reach for your feet or ankles, keeping your elbows straight., Hold for several breaths, then release.', 'Yoga', 'Beginner'),
(552, 'Pyramid Pose', 'Primary Hamstrings Secondary Calves', '', '', 'Hold', 'Begin in Tadasana (Mountain Pose)., Step your left foot back about 3-4 feet and place your heel down, keeping your toes pointing forward., Inhale and lengthen your spine, then exhale and fold forward from your hips, bringing your hands to the floor on either side of your right foot., Hold for several breaths, then switch sides and repeat.', 'Yoga', 'Beginner'),
(553, 'Pyramid Pose Blocks', 'Primary Hamstrings Secondary Calves', '', '', 'Hold', 'Begin in Tadasana (Mountain Pose) with two blocks at the top of your mat., Step your left foot back about 3-4 feet and place your heel down, keeping your toes pointing forward., Place the blocks on either side of your right foot at a height that allows you to comfortably place your hands on them., Inhale and lengthen your spine, then exhale and fold forward from your hips, bringing your hands to the blocks. Hold for several breaths, then switch sides and repeat.', 'Yoga', 'Beginner'),
(554, 'Standing Forward Bend', 'Primary Hamstrings', '', '', 'Hold', 'Stand with your feet hip-width apart., Hinge forward at your hips, reaching your hands towards the ground., Allow your head to hang heavy and feel a stretch in the back of your legs., Hold for a few breaths and then slowly roll up to a standing position.', 'Yoga', 'Beginner'),
(555, 'Standing Forward Bend Blocks', 'Primary Hamstrings Secondary Calves', '', '', 'Hold', 'Stand with your feet hip-width apart and place a block on the ground in front of you., Hinge forward at your hips and place your hands on the block., Allow your head to hang heavy and feel a stretch in the back of your legs., Hold for a few breaths and then slowly roll up to a standing position.', 'Yoga', 'Beginner'),
(556, 'Warrior Three', 'Primary Hamstrings Secondary Glutes', '', '', 'Hold', 'Start in mountain pose, standing with your feet hip-width apart and arms at your sides., Step your left foot back about 3-4 feet and raise it off the ground, while keeping your right foot facing forward., Inhale and reach your arms forward, parallel to the ground., Exhale and hinge forward at your hips, bringing your torso parallel to the ground and extending your left leg straight back. Keep your arms and right leg straight and hold the pose for a few breaths, inhale and return to mountain pose. Repeat on the other side.', 'Yoga', 'Intermediate'),
(557, 'Wide Legged Standing Forward Bend Holding Heels', 'Primary Hamstrings Secondary Calves', '', '', 'Hold', 'Start standing at the top of your mat with your feet wide apart., Start standing at the top of your mat with your feet wide apart., Exhale and fold forward from your hips, bringing your hands up and over your head towards the ground., Hold for a few breaths and then release the hands and come back up to standing.', 'Yoga', 'Beginner'),
(558, 'Glute Bridge', 'Primary Glutes', '', '', '', 'Lie down with your knees bent and your feet flat on the floor., Push your hips up so that your butt is elevated and your back straight., Tense your glutes and raise your hips towards the ceiling., Once you are at the highest point you can manage, hold the position for a few seconds, and then slowly return to the starting position', 'Bodyweight', 'Beginner'),
(559, 'Barbell Hip Thrust', 'Primary Glutes', 'https://www.youtube.com/watch?v=yTnWiBECvLk', 'Overhand Underhand Mixed', 'Push', 'Sit on the ground with a bench behind you. Have the barbell over your legs just above your hips., Lean back against the bench so that your shoulders are resting upon it, stretch your arms out to either side using the bench as support., Raise the weight by driving through your feet and extending your hips upwards. Support the weight with your shoulders and feet., Slowly extend as far as you can, and then slowly return to the starting position.', 'Barbell', 'Advanced'),
(560, 'Single Leg Glute Bridge', 'Primary Glutes', '', '', '', 'Lie down with one knee bent and and one with a slight bend., Push your hips up, so that your butt is elevated and your back straight., Tense your glutes and raise your hips towards the ceiling., Once you are at the highest point you can manage, hold the position for a few seconds, and then slowly return to the starting position.', 'Bodyweight', 'Beginner'),
(561, 'Kettlebell Glute Bridge', 'Primary Glutes', '', '', '', 'Laying on the floor with your knees bent and feet firmly on the floor, rest the kettlebell on your pelvis., Pushing through the legs and keeping the core muscles engaged, raise your pelvis off the floor until your stomach, pelvis and thighs make a straight line., Slowly lower to the ground to the starting position and repeat.', 'Kettlebells', 'Beginner'),
(562, 'Kettlebell Single Leg Glute Bridge', 'Primary Glutes', '', '', '', 'Laying on the floor with one knee bent with the foot firmly on the floor and one leg extended, rest the kettlebell on your pelvis., Pushing through the bent leg and keeping the core muscles engaged, raise your pelvis and the extended leg off the floor until your stomach, pelvis and thighs make a straight line., Slowly lower to the ground to the starting position and repeat.', 'Kettlebells', 'Intermediate'),
(563, 'Kettlebell Hip Thrust', 'Primary Glutes', '', '', '', 'Sitting on the floor with your knees bent and feet planted firmly on the floor, lean your back against the bench., Resting the kettlebell on your pelvis, raise your pelvis until your stomach, pelvis and thighs are in line., Slowly lower until you are in the starting position and repeat.', 'Kettlebells', 'Beginner'),
(564, 'Kettlebell Single Leg Hip Thrust', 'Primary Glutes', '', '', '', 'Sitting on the floor with one knee bent with the foot firmly on the floor and one leg extended, lean your back against the bench., Resting the kettlebell on your pelvis, raise your pelvis until your stomach, pelvis and thighs are in line., Slowly lower until you are in the starting position and repeat.', 'Kettlebells', 'Intermediate'),
(565, 'Glutes Stretch Variation Three', 'Primary Glutes', '', 'None', '', 'Lay flat and bring your left leg to 90 degrees., Hold your left leg with both hands and slowly rotate your hips to the right hand side., Hold at the peak of the stretch, then slowly return to starting position., Switch sides and repeat.', 'Stretches', 'Beginner'),
(566, 'Glutes Stretch Variation Two', 'Primary Glutes', '', 'None', '', 'Sit on the ground and lay one leg flat and the other over the top., Hold your leg with the same side arm and slowly rotate your hips and back., Hold at the peak of the stretch, then slowly return to starting position., Switch sides and repeat.', 'Stretches', 'Beginner'),
(567, 'Glutes Stretch Variation One', 'Primary Glutes', '', 'None', '', 'On your front, place one leg forward and then stretch out the same side arm across the mat/floor., Fully extend your leg and arm., Hold at the peak of the stretch, then slowly return to starting position., Switch sides and repeat.', 'Stretches', 'Beginner'),
(568, 'Cable Hip Abduction', 'Primary Glutes', '', 'None', '', 'Use an ankle attachment. Cable should be set all the way to the bottom., Face sideways with the ankle attachment on your outside leg. Walk a few steps away., Abduct at the hips and raise your leg out to the side., Return to the starting position and stop just short of your foot touching back to the ground (to keep tension on the muscle).', 'Cables', 'Beginner'),
(569, 'Cable Glute Kickback', 'Primary Glutes Lower back', '', '', '', 'Use an ankle attachment. The cable should be set to the bottom of the crossover machine., Push your ankle straight back, extending at the hip. Hold for a one count when you feel a contraction in your glutes., Then flex at the hips until you are back into your original position.', 'Cables', 'Beginner'),
(570, 'Band Glute Kickback', 'Primary Glutes', '', '', '', 'Get into a quadruped position. Loop the band around both of your wrists and one of your feet., Extend your knee and hip straight back until you feel like contraction in the glutes., Flex at the knee until back in the starting position.', 'Band', 'Intermediate'),
(571, 'Dumbbell Hip Thrust', 'Primary Glutes', 'https://www.youtube.com/watch?v=yTnWiBECvLk', '', '', '\"Place your shoulder blades against a box or bench that wont move.\"\"', 'Dumbbells', 'Beginner'),
(572, 'Dumbbell Glute Bridge', 'Primary Quads Hamstrings Glutes', '', '', '', 'Lay flat on the ground with both feet planted. Make sure you keep your heels glued to the floor., Place the dumbbell on your hips. Push your hips toward the ceiling until you feel your glutes contract., Then return to the floor.', 'Dumbbells', 'Beginner'),
(573, 'Barbell Sumo Deadlift', 'Primary Glutes Secondary Quads Tertiary Lower back', 'https://www.youtube.com/embed/C6aXGfIilEE', '', '', 'Stance width can vary with a sumo DL. Some prefer toes near the plates. Others prefer a more narrow stance to get more drive off the floor., Make sure your arms are inside your legs. The bar should be just over your mid-foot. Flatten your back and push your heels into the floor., Try to extend your knees and hips simultaneously.', 'Barbell', 'Intermediate'),
(574, 'Barbell Glute Bridge', 'Primary Glutes', '', 'Overhand Mixed', 'Push', 'Lie on your back on the floor with your knees bent and your feet flat on the ground. Place a barbell across your hips and grasp it with both hands., Keeping your arms straight, push through your heels to lift your hips off the ground and raise the barbell., Hold the bridge position for a moment, then slowly lower your hips back to the starting position.', 'Barbell', 'Beginner'),
(575, 'Barbell Suitcase Deadlift', 'Primary Hamstrings Glutes Obliques Secondary Lower back Tertiary Forearms', '', '', 'Pull', 'Begin by standing with your feet hip-width apart and holding a barbell at one side of your body, with your palms facing your thighs., Keeping your core engaged and your back straight, bend at the hips and knees to lower the barbell down towards the ground., As the barbell reaches the ground, use your legs and glutes to push through your heels and extend your hips and knees to raise your body back to the starting position., Repeat the movement for the desired number of reps, then switch sides and repeat the exercise with the barbell on the opposite side of your body.', 'Barbell', 'Advanced'),
(576, 'Dumbbell Figure Four Glute Bridge', 'Primary Glutes', '', 'None', 'Push', 'Lay flat on the ground with one leg crossed over the other and your heel planted on the ground., Push your hips toward the ceiling and hold for a second.', 'Dumbbells', 'Intermediate'),
(577, 'Dumbbell Figure Four Hip Thrust', 'Primary Glutes', '', 'None', 'Push', 'Put your shoulder blades on a bench and cross one leg over the other., Start with your hips up., Drop your hips and then push your hips up toward the ceiling.', 'Dumbbells', 'Intermediate'),
(578, 'Dumbbell Single Leg Glute Bridge', 'Primary Glutes', '', 'Overhand Neutral', 'Push', 'Start by lying on your back with your feet flat on the ground and a dumbbell on your hips., Lift one leg off the ground and engage your glutes to raise your hips up towards the ceiling., From this starting position, slowly lower your hips back down to the starting position.', 'Dumbbells', 'Intermediate'),
(579, 'Dumbbell Single Leg Hip Thrust', 'Primary Glutes', '', 'Overhand Neutral', 'Push', 'Start by lying on a bench with your feet flat on the ground and a dumbbell on your hips., Lift one leg off the ground and engage your glutes to raise your hips up towards the ceiling., From this starting position, slowly lower your hips back down to the starting position.', 'Dumbbells', 'Beginner'),
(580, 'Dumbbell Staggered Glute Bridge', 'Primary Glutes', '', 'Overhand Neutral', 'Push', 'Start by lying on your back with your feet on the ground and a dumbbell in each hand., Place one foot on the ground and the other slightly in front, with your knees bent., From this starting position, raise your hips up off the ground by pushing through your heels and squeezing your glutes.', 'Dumbbells', 'Beginner'),
(581, 'Dumbbell Staggered Hip thrust', 'Primary Glutes', '', 'Overhand Neutral', 'Push', 'Start by lying on your back on a bench with your feet on the ground and a dumbbell on your hips., Place one foot on the ground and the other slightly in front, with your knees bent., From this starting position, raise your hips up off the ground by pushing through your heels and squeezing your glutes.', 'Dumbbells', 'Beginner'),
(582, 'Plate Clean And Press', 'Primary Glutes Secondary Shoulders', '', '', 'Push', 'Stand with feet shoulder-width apart and the plate in front of the feet., Bend the knees and hips to pick up the plate, keeping the back straight and core engaged., Stand up, lock the hips and legs, and press the plate overhead with straight arms., Lower the plate back to the starting position, keeping control of it, and repeat for desired reps.', 'Plate', 'Intermediate'),
(583, 'Plate Clean', 'Primary Glutes', '', 'Neutral', '', 'Stand with feet shoulder-width apart and the plate in front of the feet., Bend the knees and hips to pick up the plate, keeping the back straight and core engaged., Stand up while pulling the plate towards the chest., Keep shoulder level while keeping the elbows high and in front of the plate.', 'Plate', 'Intermediate'),
(584, 'Band Hip Abduction', 'Primary Glutes', '', 'None', 'Push', 'Attach the band around your ankle. Stand with feet hip-width apart, feet facing forward, and hands on hips., Lift your right leg away from your body to the side, keeping your knee straight. This is your starting position., Maintaining proper form, move your right leg as far to the side as you can, keeping your left leg planted firmly on the ground. Hold for a second and then return your right leg to the starting position.', 'Band', 'Intermediate'),
(585, 'Band Hip Adduction', 'Primary Glutes', '', 'None', 'Pull', 'Attach a band to a stable anchor point, such as a door handle or post., Stand with feet shoulder-width apart and place the band around your ankles., Keeping your feet together, move your legs out to the sides and then back to the midline of your body. Repeat for the desired number of repetitions.', 'Band', 'Intermediate'),
(586, 'Plate Squat Hold', 'Primary Quads Glutes', '', 'Neutral', 'Hold', 'Start by standing with your feet shoulder-width apart, with a weight plate held in both hands at chest height., Lower your body by bending your knees and hips, keeping your back straight and your weight in your heels., Go down until your thighs are parallel to the floor, then pause for a moment., Hold the squat position for the desired amount of time, keeping the weight plate steady and your core engaged.', 'Plate', 'Beginner'),
(587, 'Barbell Feet Elevated Figure Four Glute Bridge', 'Primary Glutes', '', 'Overhand Mixed', 'Push', 'Start by lying flat on your back with your feet resting on top of a box, the barbell placed over your hips, and cross one leg over the other., Push through your heels and raise your hips towards the ceiling, keeping your feet flat on the box., Engage your glutes at the top of the movement, pause briefly and then lower your hips back down to the starting position.', 'Barbell', 'Intermediate'),
(588, 'Barbell Feet Elevated Glute Bridge', 'Primary Glutes', '', 'Overhand Mixed', 'Push', 'Start by lying flat on your back with your feet resting on top of a box, the barbell placed over your hips., Push through your heels and raise your hips towards the ceiling, keeping your feet flat on the box., Engage your glutes at the top of the movement, pause briefly and then lower your hips back down to the starting position.', 'Barbell', 'Intermediate'),
(589, 'Barbell Feet Elevated Single Leg Glute Bridge', 'Primary Glutes', '', 'Overhand Mixed', 'Push', 'Start by lying flat on your back with your feet resting on top of a box, the barbell placed over your hips. Raise one leg up and off the box., Push through your heels and raise your hips towards the ceiling, keeping your feet flat on the box., Engage your glutes at the top of the movement, pause briefly and then lower your hips back down to the starting position.', 'Barbell', 'Advanced'),
(590, 'Barbell Feet Elevated Staggered Glute Bridge', 'Primary Glutes', '', 'Overhand Mixed', 'Push', 'Start by lying flat on your back with your feet resting on top of a box, the barbell placed over your hips. Place one foot about 6-9 inches in front of the other., Push through your heels and raise your hips towards the ceiling, keeping your feet flat on the box., Engage your glutes at the top of the movement, pause briefly and then lower your hips back down to the starting position.', 'Barbell', 'Intermediate'),
(591, 'Bodyweight Feet Elevated Figure Four Glute Bridge', 'Primary Glutes', '', 'None', 'Push', 'Start by lying on your back with your knees bent and feet on top of a sturdy box, bench or elevated surface., Drive through your heels to lift your hips off the floor, making sure to keep your feet and knees pointing forward.', 'Bodyweight', 'Intermediate'),
(592, 'Bodyweight Feet Elevated Glute Bridge', 'Primary Glutes', '', 'None', 'Push', 'Start by lying on your back with your knees bent and feet on top of a sturdy box, bench or elevated surface., Drive through your heels to lift your hips off the floor, making sure to keep your feet and knees pointing forward.', 'Bodyweight', 'Beginner'),
(593, 'Bodyweight Feet Elevated Single Leg Glute Bridge', 'Primary Glutes', '', 'None', 'Push', 'Start by lying on your back with your knees bent and feet on top of a sturdy box, bench or elevated surface. Lift one leg off the box., Drive through your heel to lift your hips off the floor, making sure to keep your feet and knees pointing forward.', 'Bodyweight', 'Advanced'),
(594, 'Bodyweight Feet Elevated Staggered Glute Bridge', 'Primary Glutes', '', 'None', 'Push', 'Start by lying on your back with your knees bent and feet in a staggered position on the box., Drive through your heels to lift your hips off the floor, making sure to keep your feet and knees pointing forward.', 'Bodyweight', 'Intermediate'),
(595, 'Dumbbell Feet Elevated Figure Four Glute Bridge', 'Primary Glutes', '', 'Overhand Neutral', 'Push', 'Start by lying flat on your back with your feet resting on top of a box, the dumbbell placed over your hips, and cross one leg over the other., Push through your heels and raise your hips towards the ceiling, keeping your feet flat on the box., Engage your glutes at the top of the movement, pause briefly and then lower your hips back down to the starting position.', 'Dumbbells', 'Beginner'),
(596, 'Dumbbell Feet Elevated Glute Bridge', 'Primary Glutes', '', 'Overhand Neutral', 'Push', 'Start by lying on your back with your knees bent and feet on top of a sturdy box, bench or elevated surface. Place a dumbbell over your hips., Drive through your heels to lift your hips off the floor, making sure to keep your feet and knees pointing forward.', 'Dumbbells', 'Beginner'),
(597, 'Dumbbell Feet Elevated Single Leg Glute Bridge', 'Primary Glutes', '', 'Overhand Neutral', 'Push', 'Start by lying flat on your back with your feet resting on top of a box, the dumbbell placed over your hips. Raise one leg up and off the box., Push through your heels and raise your hips towards the ceiling, keeping your feet flat on the box., Engage your glutes at the top of the movement, pause briefly and then lower your hips back down to the starting position.', 'Dumbbells', 'Advanced'),
(598, 'Dumbbell Feet Elevated Staggered Glute Bridge', 'Primary Glutes', '', 'Overhand Neutral', 'Push', 'Start by lying on your back with your knees bent and feet in a staggered position on the box., Drive through your heels to lift your hips off the floor, making sure to keep your feet and knees pointing forward.', 'Dumbbells', 'Intermediate'),
(599, 'Dumbbell Standing Hip Abduction', 'Primary Glutes', '', 'Neutral', 'Push', 'Stand with a dumbbell held on the side of your leg., Raise that leg laterally as high as your hip range of motion allows for.', 'Dumbbells', 'Beginner'),
(600, 'TRX Glute Bridge', 'Primary Glutes', '', 'None', 'Push', 'Lie on your back with your feet in the foot cradles of the TRX straps, with your knees bent and feet flat on the ground., Place your arms by your sides with your palms facing down., Drive your hips up towards the ceiling, squeezing your glutes and hamstrings at the top of the movement.', 'TRX', 'Beginner'),
(601, 'Barbell Deadlift', 'Primary Lower back Mid back Secondary Glutes', 'https://www.youtube.com/embed/C6aXGfIilEE', 'Overhand Mixed', 'Pull', 'Stand with your mid-foot under the bar and grip the bar with your hands, about a shoulder width apart., Bend your knees, then lift the bar by straightening your back. It is important to keep your back straight., Stand to your full height and hold., Lower the bar to the floor by bending your knees and keeping your back straight.', 'Barbell', 'Advanced'),
(602, 'Barbell Squat', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/NcMEyM7qyuk', 'Overhand', 'Push', 'Stand with your feet shoulder-width apart. Maintain the natural arch in your back, squeezing your shoulder blades and raising your chest., Grip the bar across your shoulders and support it on your upper back. Unwrack the bar by straightening your legs, and take a step back., Bend your knees as you lower the weight without altering the form of your back until your hips are below your knees., Raise the bar back to starting position, lift with your legs and exhale at the top.', 'Barbell', 'Advanced');
INSERT INTO `workout` (`ID`, `exercise_name`, `target`, `youtubeURL`, `Grips`, `Force`, `steps`, `Category`, `Difficulty`) VALUES
(603, 'Dumbbell Goblet Squat', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Hold the weight tucked into your upper chest area, keeping your elbows in. Your feet should be slightly wider than shoulder width., Sink down into the squat, keeping your elbows inside the track of your knees., Push through your heels while keeping your chest up and return to starting position.', 'Dumbbells', 'Beginner'),
(604, 'Side Lunges', 'Primary Quads Secondary Glutes', '', '', '', 'Take a slow, lateral step to the right., Extend the left knee, and drive your weight into your right side., Flex your knee and lower your body., Push back to the starting position.', 'Bodyweight', 'Beginner'),
(605, 'Forward Lunges', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/Y9zHge5sRj4', 'None', 'Push', 'Step forward with one leg., Lower your body until your rear knee nearly touches the ground., Ensure you remain upright, and your front knee stay above the front foot., Push off the floor with your front foot until you return to the starting position. Switch legs.', 'Bodyweight', 'Beginner'),
(606, 'Jump Squats', 'Primary Quads Secondary Glutes', '', '', '', 'Stand with your feet shoulder-width apart., Start by doing a regular squat, then engage your core and jump up explosively., When you land, lower your body back into the squat position.', 'Bodyweight', 'Beginner'),
(607, 'Good Mornings', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Stand with your feet a little past shoulder width apart with a slight bend at your knee. Place your hands behind the back of your head., Keeping your back straight, rotate your hips to bring your shoulders down towards the floor. Stop when your chest is parallel with the floor.', 'Bodyweight', 'Beginner'),
(608, 'Barbell Curtsy Lunge', 'Primary Quads Secondary Glutes', '', 'Overhand', 'Push', 'Place the barbell on your back, Step your foot back and around while simultaneously bringing the weight down., Return to start and repeat on other leg.', 'Barbell', 'Intermediate'),
(609, 'Cable Goblet Squat', 'Primary Quads Secondary Glutes Tertiary Lower back', '', 'Neutral', '', 'Start with a straight bar or easy bar attachment ., Get your elbows directly underneath the bar, squat straight up and then walk back a few steps., Break at your knees and hips to initiate the squat. Make sure you maintain a flat back., Squat all the way back to a standing position.', 'Cables', 'Beginner'),
(610, 'Cable Split Squat', 'Primary Quads Secondary Glutes', '', 'Overhand', '', 'You can use any attachment on this exercise. Cable should be set all to the bottom., Grab the attachment and walk back a few steps. Take a split stance. One leg in front and one behind., Break at the knees and hips to initiate the split squat. Make sure your back knee touches the floor., Squat back up into a standing position.', 'Cables', 'Advanced'),
(611, 'Cable Romanian Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', 'https://www.youtube.com/embed/qWb5Zr98B-M', 'Overhand', '', 'Start with a straight bar or ez bar attachment set to the bottom of the machine. Squat the weight up into a standing position and walk a few steps back, \"Push your butt back to initiate the movement and then push your butt forward until youre back in a standing position.\"\"', 'Cables', 'Intermediate'),
(612, 'Cable Single Leg Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Overhand', '', 'Start with a straight bar or easy bar attachment. Squat the weight up into a standing position and walk a few steps back., \"Push your butt back to initiate the movement while lifting one of your legs.\\nThen push your butt forward until youre back in a standing position.\"\"', 'Cables', 'Advanced'),
(613, 'Cable Pull Through', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Neutral', 'Pull', 'This exercise works best with a rope attachment but can be done with anything. The attachment should be set to the bottom of the machine., Face away from the cable machine, straddle the cable itself, and grab the attachment.\\nWalk a few steps away., Break at the hips while maintaining a flat back and mostly extended knees., Push hips forward until you are back in a standing position.', 'Cables', 'Beginner'),
(614, 'Band Spanish Squat', 'Primary Quads Secondary Glutes', '', '', '', '', 'Band', 'Advanced'),
(615, 'Band Good Morning', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Anchor the band at hip level. Take a few steps away until the band is taut., Push your hips back while keeping your back flat until you feel a stretch in your hamstrings.', 'Band', 'Beginner'),
(616, 'Band Leg Press', 'Primary Quads Secondary Glutes', '', '', '', 'Lay flat on the ground with your head by the anchor point of the band, which should be 6 to 12 inches above your head, Place the band around your midfoot and extend at the knees and hip.', 'Band', 'Advanced'),
(617, 'Band Pullthrough', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Anchor the band to the lowest point possible. Straddle the band and then grab with both hands., Take a few steps away until the band is taut., Push your butt back until you feel a stretch in your hamstrings. Then extend the hips back into the starting position.', 'Band', 'Beginner'),
(618, 'Band Romanian Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', 'https://www.youtube.com/embed/qWb5Zr98B-M', '', '', 'Stand on your band with both feet., Hinge forward at the hips while keeping a flat back., Hinge until you feel a stretch in your hamstrings., Push your hips back forward until you are in a standing position.', 'Band', 'Intermediate'),
(619, 'Band Squat', 'Primary Quads Secondary Glutes', '', '', '', 'Stand with a shoulder width grip. Both feet should be on the band with the band loops in each of your hand., Push your butt back and also break at the knees., \"Try to hit parallel depth (bottom of your leg should be parallel with the ground). If you cant tell your squat depth', 'Band', 'Beginner'),
(620, 'Dumbbell Goblet Good Morning', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Neutral', 'Pull', 'Hold the dumbbell in a goblet position. With your elbows directly under your fists., Push your hips back while leaving your knees MOSTLY extended. You should feel a stretch in your hamstrings., \"When you feel the stretch push your hips forward until youre back in a standing position.\"\"\"', 'Dumbbells', 'Advanced'),
(621, 'Dumbbell Goblet Bulgarian Split Squat', 'Primary Quads Secondary Glutes', '', '', '', 'Holding the dumbbell in a goblet squat place your foot on a bench or box., Break at the hips and knees. Try to go to the point where your hamstring is parallel with the ground. Or until your back knee taps the floor., Make sure to keep your back flat and your elbows directly under your fists.', 'Dumbbells', 'Advanced'),
(622, 'Dumbbell Romanian Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', 'https://www.youtube.com/embed/qWb5Zr98B-M', 'Overhand Neutral', 'Pull', 'Stand with a shoulder width stance. Push your butt back while leaving your knees MOSTLY extended., You should feel a stretch in your hamstrings. When you feel the stretch, push your hips forward to complete the rep., Do not push your hips all the way forward. This will hyperextend your spine. Just go to a normal standing position.', 'Dumbbells', 'Intermediate'),
(623, 'Barbell Bulgarian Split Squat', 'Primary Quads Secondary Glutes', '', '', '', 'Find a box or bench that is roughly knee height or a few inches lower. (The lower the box is the lower the mobility requirement)., You can either place your toes or the top of your foot on the box. Point your front foot forward or slightly turned outwards and break at the knees and hips simultaneously., Aim to get your front leg to hit parallel depth (hamstring parallel with the ground) or lower., The further forward your front foot is, the longer the Range of Motion is at your hips. The further back your front foot is, the longer the ROM at your knees.', 'Barbell', 'Advanced'),
(624, 'Barbell Front Squat Olympic', 'Primary Quads Secondary Glutes Tertiary Abdominals', '', '', 'Push', '\"Measure your hands by the markings on the bar to make sure youre even. Then throw your elbows underneath the bar.\"\"', 'Barbell', 'Advanced'),
(625, 'Barbell Front Squat Bodybuilding', 'Primary Quads Secondary Glutes Tertiary Abdominals', '', '', 'Push', '\"Measure your shoulders against the bar to make sure youre even. Throw your shoulders under the bar. The bar should be digging into your neck slightly.\"\"', 'Barbell', 'Advanced'),
(626, 'Barbell High Bar Good Morning', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', '\"Place the bar on your traps. You may need a pad so the bar doesnt dig into your cervical spine.\"\"', 'Barbell', 'Advanced'),
(627, 'Barbell Low Bar Good Morning', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Overhand', 'Pull', 'Place the bar between your traps and rear delts. Pull your shoulder blades back to give the bar a shelf to sit on., \"Push your hips back until you feel a stretch in your hamstrings. This means youre nearing the end of your Range of Motion with out rounding your spine.\"\"', 'Barbell', 'Advanced'),
(628, 'Barbell Staggered Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Place one foot behind. You should only be on your toes on that back foot. Your front foot should be planted flat the entire time., Bend your back knee and push your hips back. Look for a stretch in your front hamstring., \"When you feel the stretch', 'Barbell', 'Advanced'),
(629, 'Barbell Single Leg Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Start by pushing your hips straight back and extending your non-working leg behind you. It is ok to touch your other foot back to the ground each rep., \"Leave your knee mostly extended. Look for a stretch in your hamstring. When you feel the stretch', 'Barbell', 'Advanced'),
(630, 'Barbell Romanian Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', 'https://www.youtube.com/embed/qWb5Zr98B-M', '', '', 'Take a shoulder width, double overhand or mixed grip., Push your hips back while leaving your knees mostly extended. Look for a stretch in your hamstrings., \"When you feel the stretch', 'Barbell', 'Intermediate'),
(631, 'Barbell High Bar Squat', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/NcMEyM7qyuk', '', 'Push', 'Place the bar on top of your traps., Push your hips back and break at the knees. Push your elbows forward to keep yourself from getting pitched too far forward., Aim to get your hamstrings to parallel with the floor., Push your heels into the floor and squat back to a standing position.', 'Barbell', 'Beginner'),
(632, 'Barbell Low Bar Squat', 'Primary Quads Secondary Glutes Tertiary Lower back', 'https://www.youtube.com/embed/NcMEyM7qyuk', '', '', 'Pull your shoulder blades back really hard and place the bar on your posterior delts., Push your hips back and break at the knees to initiate the squat., Aim to get your hamstrings to parallel with the ground. Push your heels into the floor and squat back up to a standing position.', 'Barbell', 'Advanced'),
(633, 'Dumbbell Bulgarian Split Squat', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Find a box or bench that is roughly knee height or a few inches lower. (The lower the box is the lower the mobility requirement)., You can either place your toes or the top of your foot on the box. Point your front foot forward or slightly turned outwards and break at the knees and hips simultaneously., Aim to get your front leg to hit parallel depth (hamstring parallel with the ground) or lower., The further forward your front foot is, the longer the Range of Motion is at your hips. The further back your front foot is, the longer the ROM at your knees.', 'Dumbbells', 'Advanced'),
(634, 'Barbell Coan Deadlift', 'Primary Quads Secondary Glutes Tertiary Lower back', 'https://www.youtube.com/embed/C6aXGfIilEE', 'Overhand Mixed', '', 'Stand with your feet hip-width apart and place a barbell on the floor in front of you., Bend at the hips and knees to grasp the barbell with an overhand grip, positioning your hands just outside of your hips., Keeping your back straight and your core engaged, lift the barbell off the floor by extending your hips and knees., As you lift the barbell, pull your shoulders back and squeeze your glutes to fully extend your hips at the top of the movement.', 'Barbell', 'Intermediate'),
(635, 'Barbell Forward Lunge', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/Y9zHge5sRj4', 'Overhand', 'Push', 'Stand with your feet shoulder-width apart and hold a barbell at your shoulders with an overhand grip., Step forward with your right foot and bend both knees to lower your body until your right thigh is parallel to the ground., Push off your right foot to return to the starting position.', 'Barbell', 'Advanced'),
(636, 'Barbell Front Rack Step Up', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/c-Cc-LDrpL4', 'None', 'Push', 'Stand in front of a step or bench with your feet shoulder-width apart and hold a barbell at your shoulders with an overhand grip., Step up onto the bench with your right foot, pushing off your foot to raise your body up onto the bench., Bring your left foot up onto the bench and stand up straight, keeping the barbell at your shoulders., Step back down with your left foot, followed by your right foot, to return to the starting position.', 'Barbell', 'Advanced'),
(637, 'Barbell Heels Up Back Squat', 'Primary Quads Secondary Glutes', '', 'Overhand', 'Push', 'Stand with your feet shoulder-width apart and your heels on a plate or low step., Bend your knees and lower your body into a squat position., Push through your heels to return to the starting position.', 'Barbell', 'Advanced'),
(638, 'Barbell Heels Up Front Squat', 'Primary Quads Secondary Glutes', '', 'None', 'Push', 'Stand with your feet shoulder-width apart and feet on a plate. Place the bar in a front rack position., Bend your knees and lower your body into a squat position, keeping your heels raised off the ground., Push through your heels to return to the starting position.', 'Barbell', 'Advanced'),
(639, 'Barbell Lateral Lunge', 'Primary Quads Secondary Glutes', '', 'Overhand', 'Push', 'Stand with your feet shoulder-width apart and hold a barbell at your shoulders with an overhand grip., Step out to the side with your right foot and bend your right knee to lower your body until your right thigh is parallel to the ground., Push off your right foot to return to the starting position., Repeat on the opposite side by stepping out with your left foot.', 'Barbell', 'Advanced'),
(640, 'Barbell Pause Box Squat', 'Primary Quads Secondary Glutes Tertiary Lower back', '', 'Overhand', 'Push', 'Stand in front of a box or bench with your feet shoulder-width apart and a barbell resting on your upper back., Lower your body into a squat position, sitting back onto the box and pausing for a moment before standing back up., Push through your heels to return to the standing position.', 'Barbell', 'Advanced'),
(641, 'Barbell Pause Squat', 'Primary Quads Secondary Glutes Tertiary Lower back', '', 'Overhand', 'Push', 'Stand with your feet shoulder-width apart and a barbell resting on your upper back., Lower your body into a squat position, pausing for a moment at the bottom of the movement before standing back up., Push through your heels to return to the standing position.', 'Barbell', 'Intermediate'),
(642, 'Barbell Reverse Deadlift', 'Primary Quads Secondary Glutes Tertiary Lower back', '', 'Overhand Underhand', 'Push', 'Start by standing with your feet about hip-width apart, with your toes pointed slightly outward., Place a barbell on the floor behind you, with the weight plates on either side., Bend down and grab the barbell with an overhand grip, your hands slightly wider than shoulder-width apart. Keep your back straight and your shoulders down as you grip the bar., Stand up straight, lifting the barbell off the ground and pulling it toward your hips. Keep your elbows close to your body and your shoulders down as you do this.', 'Barbell', 'Advanced'),
(643, 'Barbell Reverse Lunge', 'Primary Quads Secondary Glutes', '', 'Overhand', 'Push', 'Start by standing with your feet about hip-width apart, with a barbell on your shoulders. Your feet should be slightly pointed outward, and your shoulders should be down and relaxed., Step backward with one foot, lowering your body until your front thigh is parallel to the ground and your back knee is hovering just above the ground., Push off your back foot, returning to the starting position.', 'Barbell', 'Intermediate'),
(644, 'Barbell Side Step Up', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/c-Cc-LDrpL4', 'Overhand', 'Push', 'Start by standing with your feet about hip-width apart, with a barbell on your shoulders. Your feet should be slightly pointed outward, and your shoulders should be down and relaxed., Place a step or box beside you, and step up onto it with one foot. Your other foot should remain on the ground., Push off your grounded foot, raising your body up onto the step., Step back down with the same foot, returning to the starting position.', 'Barbell', 'Intermediate'),
(645, 'Barbell Snatch Grip Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', 'Pull', 'Start by standing with your feet about hip-width apart, with your toes pointed slightly outward., Place a barbell on the floor in front of you, with the weight plates on either side. Make sure that the bar is directly in line with your feet., Bend down and grab the barbell with an overhand grip, your hands much wider than shoulder-width apart. Keep your back straight and your shoulders down as you grip the bar., Stand up straight, lifting the barbell off the ground and pulling it toward your hips. Keep your elbows close to your body and your shoulders down as you do this., Slowly lower the barbell back to the ground, making sure to keep your back straight and your shoulders down throughout the movement.', 'Barbell', 'Advanced'),
(646, 'Barbell Snatch Grip Romanian Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', 'https://www.youtube.com/embed/qWb5Zr98B-M', 'Overhand Mixed', 'Pull', '\"Start by standing with your feet about hip-width apart. The bar should be in a rack or box so its lifted off the ground.\"\"', 'Barbell', 'Advanced'),
(647, 'Barbell Split Squat', 'Primary Quads Secondary Glutes', '', 'Overhand', 'Push', 'Take a large step forward with one leg, and lower your body until your back knee nearly touches the ground. Your front knee should be bent at a 90-degree angle, and your back leg should be straight., Push through the heel of your front foot to raise your body back to the starting position.', 'Barbell', 'Intermediate'),
(648, 'Barbell Step Up Balance', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/c-Cc-LDrpL4', 'Overhand', 'Push', 'Begin by standing in front of a sturdy box or bench that is about knee-height, holding a barbell across your upper back and shoulders, using an overhand grip., Place your right foot on the box and push through the heel to lift your body up and onto the box, keeping your left foot off the ground., Step back down with your right foot, lowering your left foot to the ground.', 'Barbell', 'Intermediate'),
(649, 'Barbell Step Up', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/c-Cc-LDrpL4', 'Overhand', 'Push', 'Begin by standing in front of a sturdy box or bench that is about knee-height, holding a barbell across your upper back and shoulders, using an overhand grip., Place your right foot on the box and push through the heel to lift your body up and onto the box., Step back down with your right foot.', 'Barbell', 'Beginner'),
(650, 'Barbell Tap Pause Squat', 'Primary Quads Secondary Glutes', '', 'Overhand', 'Push', 'Begin by standing in front of a sturdy box or bench that is about knee-height, holding a barbell across your upper back and shoulders, using an overhand grip., Sit back and lower your body down until your glutes tap the top of the box, keeping your back straight and your core engaged., Push through your heels to extend your hips and stand up, bringing your body back to the starting position.', 'Barbell', 'Intermediate'),
(651, 'Barbell Zercher Good Morning', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'None', 'Pull', 'Begin by standing with your feet about hip-width apart and holding a barbell in the crook of your elbows, with your palms facing towards your body., Keeping your core engaged and your back straight, bend at the hips and lower your torso forward until it is nearly parallel to the ground., Push through your heels to extend your hips and raise your body back to the starting position.', 'Barbell', 'Advanced'),
(652, 'Barbell Zercher Squat', 'Primary Quads Secondary Glutes Tertiary Lower back', '', '', 'Push', 'Begin by standing with your feet about hip-width apart and holding a barbell in the crook of your arms, near your belly. Your palms should be facing towards your body., Sit back and lower your body down until your thighs are parallel to the ground, keeping your back straight and your core engaged., Push through your heels to extend your hips and stand back up to the starting position.', 'Barbell', 'Advanced'),
(653, 'Assisted Bulgarian Split Squat', 'Primary Quads Secondary Glutes', '', 'None', 'Push', 'Grab a stick of some sort or hold onto something stable (rack, bench)., Grab a box that is knee height or slightly lower., Hold the stick in your right arm and put your right leg on the box., Begin your squat by breaking at the knees. Get your knee close to the floor or all the way to the floor. Make sure to keep your chest tall.', 'Bodyweight', 'Beginner'),
(654, 'Bulgarian Split Squat', 'Primary Quads', '', '', '', 'Stand with your back to a bench (or raised surface) and place one of your feet on the bench., Squat down until your front leg is about parallel to the floor., Go back to the starting position. After completing the desired amount of reps, switch legs and repeat.', 'Bodyweight', 'Beginner'),
(655, 'Curtsy Lunge', 'Primary Quads Secondary Glutes', '', 'None', 'Push', 'Step your left foot diagonally behind your right leg, crossing it behind your body. Bend your knees and lower your hips until your left thigh is parallel to the floor and your right knee is hovering just above the ground., Push through your right foot to straighten your legs and return to the starting position., Repeat the movement with your other leg.', 'Bodyweight', 'Intermediate'),
(656, 'Forward Lunge', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/Y9zHge5sRj4', 'None', 'Push', 'Step forward with one leg and lower your body until your front thigh is parallel to the floor and your back knee almost touches the ground. Keep your chest up and your front foot flat on the floor., Push through your front foot to straighten your legs and return to the starting position., Repeat the movement with your other leg.', 'Bodyweight', 'Beginner'),
(657, 'Heels Up Squat', 'Primary Quads Secondary Glutes', '', 'None', 'Push', 'Place your heels on a plate or very low step., Keep your chest tall and initiate the squat by pushing your hips back and breaking at the knees., You should notice yourself able to squat lower than normal.', 'Bodyweight', 'Intermediate'),
(658, 'Lateral Lunge', 'Primary Quads Secondary Glutes', '', 'None', 'Push', 'Engage your core by drawing your belly button towards your spine. This will help to stabilize your body., Take a large step to the side with your right foot, planting it firmly on the ground., As you step, bend your right knee and lower your hips until your right thigh is parallel to the ground. Your left leg should remain straight., Push through your right heel to return to the starting position. Repeat on the opposite side by stepping out with your left foot.', 'Bodyweight', 'Intermediate'),
(659, 'Split Squat', 'Primary Quads Secondary Glutes', '', 'None', 'Push', 'Take a split stance. Make sure to keep your chest tall., Bring your rear knee to the floor or close to it., Drive through your front heel and your rear foot back to the starting position.', 'Bodyweight', 'Beginner'),
(660, 'Dumbbell Bulgarian Split Squat', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Find a box or bench that is roughly knee height or a few inches lower. (The lower the box is the lower the mobility requirement)., You can either place your toes or the top of your foot on the box. Point your front foot forward or slightly turned outwards and break at the knees and hips simultaneously., Aim to get your front leg to hit parallel depth (hamstring parallel with the ground) or lower., The further forward your front foot is, the longer the Range of Motion is at your hips. The further back your front foot is, the longer the ROM at your knees.', 'Dumbbells', 'Advanced'),
(661, 'Dumbbell Forward Lunge', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/Y9zHge5sRj4', 'Neutral', 'Push', 'Stand with your feet hip-width apart and hold a dumbbell in each hand with your palms facing each other., Take a big step forward with your right foot, lowering your hips until your right thigh is parallel to the ground and your left knee is hovering just above the floor., Push through your right heel to straighten your right leg and return to the starting position.', 'Dumbbells', 'Intermediate'),
(662, 'Dumbbell Goblet Bulgarian Split Squat', 'Primary Quads Secondary Glutes', '', '', '', 'Holding the dumbbell in a goblet squat place your foot on a bench or box., Break at the hips and knees. Try to go to the point where your hamstring is parallel with the ground. Or until your back knee taps the floor., Make sure to keep your back flat and your elbows directly under your fists.', 'Dumbbells', 'Advanced'),
(663, 'Dumbbell Goblet Curtsy Lunge', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Stand with your feet hip-width apart and a dumbbell in a goblet position., Step your left foot behind and to the right of your right foot, crossing it behind your right leg., Bend your knees and lower your hips down and back, as if you were about to sit in a chair., Push through your right heel to straighten your right leg and return to the starting position.', 'Dumbbells', 'Advanced'),
(664, 'Dumbbell Goblet Forward Lunge', 'Primary Quads Secondary Glutes', 'https://www.youtube.com/embed/Y9zHge5sRj4', 'Neutral', 'Push', 'Hold a dumbbell in a goblet position., Step forward with one of your legs while bending at your back leg. Bring your back knee to the floor or close to the floor., Push through your front leg explosively to get back to your starting position.', 'Dumbbells', 'Intermediate'),
(665, 'Dumbbell Goblet Lateral Lunge', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Engage your core by drawing your belly button towards your spine. This will help to stabilize your body. Hold a dumbbell in a goblet position., Take a large step to the side with your right foot, planting it firmly on the ground., As you step, bend your right knee and lower your hips until your right thigh is parallel to the ground. Your left leg should remain straight., Push through your right heel to return to the starting position. Repeat on the opposite side by stepping out with your left foot.', 'Dumbbells', 'Advanced'),
(666, 'Dumbbell Goblet Reverse Lunge', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Hold a dumbbell in a goblet position., Take a big step backwards with your left leg, lowering your body until your left thigh is parallel to the ground and your right knee is bent at a 90-degree angle., Push through your right heel to return to standing, bringing your left leg back to starting position., Repeat the movement on the other side, stepping backwards with your right leg and lowering your body until your right thigh is parallel to the ground and your left knee is bent at a 90-degree angle.', 'Dumbbells', 'Beginner'),
(667, 'Dumbbell Goblet Split Squat', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Hold a dumbbell in a goblet position., Take a large step forward with one leg, and lower your body until your back knee nearly touches the ground. Your front knee should be bent at a 90-degree angle, and your back leg should be straight., Push through the heel of your front foot to raise your body back to the starting position.', 'Dumbbells', 'Intermediate'),
(668, 'Dumbbell Lateral Lunge Reach', 'Primary Quads Secondary Glutes Tertiary Lower back', '', 'Overhand Neutral', 'Push', 'Engage your core by drawing your belly button towards your spine. This will help to stabilize your body. Hold a dumbbell in a goblet position., Take a large step to the side with your right foot and reach toward the floor with your single dumbbell., As you step, bend your right knee and lower your hips until your right thigh is parallel to the ground. Your left leg should remain straight., Push through your right heel to return to the starting position. Repeat on the opposite side by stepping out with your left foot.', 'Dumbbells', 'Intermediate'),
(669, 'Dumbbell Reverse Lunge', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Hold two dumbbells by your side., Take a big step backwards with your left leg, lowering your body until your left thigh is parallel to the ground and your right knee is bent at a 90-degree angle., Push through your right heel to return to standing, bringing your left leg back to starting position., Repeat the movement on the other side, stepping backwards with your right leg and lowering your body until your right thigh is parallel to the ground and your left knee is bent at a 90-degree angle.', 'Dumbbells', 'Beginner'),
(670, 'Dumbbell Split Squat', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Hold a dumbbell in a goblet position., Take a large step forward with one leg, and lower your body until your back knee nearly touches the ground. Your front knee should be bent at a 90-degree angle, and your back leg should be straight., Push through the heel of your front foot to raise your body back to the starting position.', 'Dumbbells', 'Beginner'),
(671, 'Dumbbell Front Rack Pause Squat', 'Primary Quads Lower back Secondary Glutes', '', 'Neutral', 'Push', 'Place two dumbbells on your shoulders and make sure you force your elbows upward., Push your hips back and break at the knees., When you reach parallel (hamstrings parallel with the ground) pause for 1-3 seconds. Then push through your heels back to the starting position.', 'Dumbbells', 'Advanced'),
(672, 'Dumbbell Front Rack Squat', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Place two dumbbells on your shoulders and push your elbows upward., Push your hips back and break at the knees., Hit parallel depth and then drive through your heels back to the starting position.', 'Dumbbells', 'Intermediate'),
(673, 'Dumbbell Goblet Pulse Squat', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Hold a dumbbell in goblet position., Squat down to parallel depth and then halfway back up., Stay in this short range of motion for the duration of the set.', 'Dumbbells', 'Beginner'),
(674, 'Dumbbell Heels Up Goblet Squat', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Take a half kneeling stance. The dumbbell should start over your knee that is up., Rotate at the spine and point the dumbbell to the opposing side.', 'Dumbbells', 'Beginner'),
(675, 'Dumbbell Heels Up Narrow Goblet Squat', 'Primary Quads Secondary Glutes', '', 'Neutral', 'Push', 'Stand with your feet on a plate. Your feet should be very close to one another, but not touching., Begin your squat by pushing your hips back and breaking at the knees. Try to keep your chest tall while squatting.', 'Dumbbells', 'Advanced'),
(676, 'Dumbbell Offset Squat', 'Primary Quads Secondary Glutes Tertiary Obliques', '', 'Neutral', 'Push', 'Hold a dumbbell over one shoulder., Initiate the squat by pushing your hips back., Avoid doing any twisting. Try to keep your shoulders even.', 'Dumbbells', 'Intermediate'),
(677, 'Dumbbell Single Leg Single Arm Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Overhand Neutral', 'Pull', 'Start by standing on one leg with a dumbbell in one hand., Slowly bend at the hips and lower the dumbbell towards the ground while extending your free leg behind you., Keep your core engaged and your back straight as you lower the weight., Once the dumbbell is close to the ground, push through your heel to return to the starting position.', 'Dumbbells', 'Advanced'),
(678, 'Dumbbell Single Leg Stiff Leg Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Overhand Neutral', 'Pull', 'Start by standing on one leg with your feet shoulder-width apart and holding two dumbbells., From this starting position, hinge at the hips to lower the dumbbell towards the ground, keeping your back straight and your core engaged., Once you feel a stretch in your hamstring, slowly return to the starting position., Make sure to keep your standing leg slightly bent and avoid rounding your back as you lower the dumbbell.', 'Dumbbells', 'Intermediate'),
(679, 'Dumbbell Staggered Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Overhand Neutral', 'Pull', 'Start by standing with your feet shoulder-width apart and holding a dumbbell in each hand., Place one foot slightly behind the other, with the heel of your back foot elevated on a step or weight plate., From this starting position, bend at the hips and knees to lower the dumbbells towards the ground, keeping your back straight and your core engaged., Once the dumbbells are below your knees, drive through your heels to stand back up to the starting position.', 'Dumbbells', 'Intermediate'),
(680, 'Dumbbell Sumo Squat', 'Primary Quads Secondary Glutes Tertiary Lower back', '', 'Neutral', 'Push', 'Start by standing with your feet wider than shoulder-width apart and holding a dumbbell in each hand., Place the dumbbells on the ground between your feet., From this starting position, bend your knees and lower your body down into a squat., Make sure to keep your core engaged and your back straight throughout the movement.', 'Dumbbells', 'Beginner'),
(681, 'Dumbbell Swing', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Overhand Neutral', 'Push', 'Start by standing with your feet shoulder-width apart and holding a dumbbell in both hands., Bend your knees slightly and hinge at the hips to swing the dumbbell between your legs., From this starting position, explosively extend your hips and swing the dumbbell up to chest height., Allow the momentum of the swing to carry the dumbbell back down between your legs, and then repeat the movement for the desired number of repetitions.', 'Dumbbells', 'Beginner'),
(682, 'Dumbbell Thruster', 'Primary Quads Secondary Shoulders Tertiary Glutes', '', 'Neutral', 'Push', 'Start by standing with your feet shoulder-width apart and holding a dumbbell in each hand., Bring the dumbbells to your shoulders, with your palms facing inward and your elbows bent., From this starting position, dip your knees slightly and then quickly extend them as you press the dumbbells upwards., As you press the dumbbells upwards, also push your hips forward to help generate additional power., Slowly lower the dumbbells back down to the starting position, and repeat for the desired number of repetitions.', 'Dumbbells', 'Intermediate'),
(683, 'Burpee', 'Primary Chest Abdominals Secondary Quads Tertiary Glutes', '', 'Overhand', 'Push', 'Start standing with your feet hip-width apart. Lower your body into a squat position, placing your hands on the ground in front of you., Quickly jump your feet back into a plank position, keeping your body in a straight line from your head to your toes., Perform a push-up, keeping your core tight and your elbows close to your body., Jump your feet back towards your hands, and then jump up into the air, reaching your arms overhead., Land softly and immediately lower your body back into the squat position to repeat the movement.', 'Bodyweight', 'Intermediate'),
(684, 'Plate Squat Hold Reach', 'Primary Triceps Shoulders Secondary Quads Glutes', '', '', '', 'Hold the plate with both hands at chest height., Keeping your core tight and your chest up, squat down as low as you can go while keeping your feet flat on the ground., Hold the squat position and reach the plate forward, keeping your arms straight and your back straight., Return the plate to your chest while you hold the squat position and repeat reaching the plate in front of you', 'Plate', 'Intermediate'),
(685, 'Plate Curtsy Lunge', 'Primary Quads Secondary Glutes', '', 'Neutral', '', 'Stand with feet shoulder-width apart and the plate in front of the torso., Step back and across the body with the right foot, bending both legs to lower into a lunge position., Push through the front foot to return to the starting position., Repeat on the other side by stepping back and across with the left foot., Continue alternating sides for desired reps.', 'Plate', 'Intermediate'),
(686, 'Plate Single Leg Pinch Grip Deadlift', 'Primary Hamstrings Forearms Secondary Glutes Tertiary Lower back', '', 'Pinch Grip', 'Pull', 'Stand with your feet shoulder-width apart and facing forwards. Grip the sides of a weight plate with your palms facing down., Balance on one foot, as you raise the weight keep your arms and back straight, while you bring your other foot back towards the ground, Keep four raised leg straight and in alignment with your back as you lower the plate back down to the starting position.', 'Plate', 'Intermediate'),
(687, 'Plate Lateral Lunge', 'Primary Quads Secondary Glutes', '', 'Neutral', '', 'Stand with feet hip-width apart and the plate held in front of the torso., Take a step to the side with one foot, bending the knee and lowering the hips into a lunge., Push through the front foot to return to the starting position., Repeat on the other side by stepping to the other side with the other foot.', 'Plate', 'Beginner'),
(688, 'Plate Forward Lunge', 'Primary Quads Secondary Glutes', '', 'Neutral', '', 'Stand with feet hip-width apart and the plate held in front of the torso., Take a step forward with one foot, bending the front knee to lower the hips into a lunge., Push through the front foot to return to the starting position., Repeat on the other side by stepping forward with the other foot.', 'Plate', 'Beginner'),
(689, 'Plate Single Leg Deadlift', 'Primary Quads Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Engage your core and straighten your legs to lift the plate off the ground., Stand on one leg, keeping the other leg off the ground, while holding the plate in front of your body., Keep your back straight and maintain a neutral head position while looking straight ahead., Lower the plate back down to the ground while keeping your balance on one leg.', 'Plate', 'Advanced'),
(690, 'Plate Deficit Lunge', 'Primary Quads Secondary Glutes', '', 'None', 'Push', 'Stand on a plate with both feet., Step back with one foot to the floor and let your back knee get close to or tap the floor., Push through your front leg until both feet are back on the plate.', 'Plate', 'Advanced'),
(691, 'Plate Glute Bridge to Chest Press', 'Primary Chest Secondary Glutes', '', 'Neutral', 'Push', 'Lay flat on the ground with a plate on your chest., Push your hips up toward the ceiling and maintain that extended position., With your hips up, press the plate toward the ceiling.', 'Plate', 'Beginner'),
(692, 'Plate Glute Bridge to Pullover', 'Primary Lats Secondary Glutes', '', 'Neutral', 'Pull', 'Lay flat on the ground with a plate on your chest., Push your hips up toward the ceiling and maintain that extended position., Extend your elbows mostly, break at the shoulder joint while maintaining hip extension and the slight bend in your elbows., Let the plate tap the floor (or get close) and then pull the plate back into the starting position.', 'Plate', 'Beginner'),
(693, 'Plate Overhead Walking Lunge', 'Primary Quads Secondary Glutes Tertiary Shoulders', '', 'Neutral', 'Push', 'Stand tall with a plate overhead., Step with one foot forward bringing your back knee to the floor. Gently tap your knee then drive through your front leg., Go back to a standing position and then step forward with the opposing legs.', 'Plate', 'Intermediate'),
(694, 'Plate Staggered Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Neutral', 'Pull', 'Start by standing with your feet shoulder-width apart and holding a weight plate., Place one foot slightly behind the other, with the heel of your back foot elevated., From this starting position, bend at the hips and knees to lower the plate towards the ground, keeping your back straight and your core engaged., Once the plate is below your knees, drive through your heels to stand back up to the starting position.', 'Plate', 'Advanced'),
(695, 'Plate Staggered Pinch Grip Deadlift', 'Primary Hamstrings Forearms Secondary Glutes Tertiary Lower back', '', 'Pinch Grip', 'Pull', 'Start by standing with your feet shoulder-width apart and holding two plates with a pinch grip., Place one foot slightly behind the other, with the heel of your back foot elevated., From this starting position, bend at the hips and knees to lower the plates towards the ground, keeping your back straight and your core engaged.', 'Plate', 'Advanced'),
(696, 'Plate Thruster', 'Primary Quads Secondary Shoulders Tertiary Glutes', '', 'Overhand', 'Push', 'Start with the plate at chest height. Begin a squat., Make sure to hit parallel depth. On your way back up, use the momentum you generate with your legs to help push the plate into an overhead position., Let the plate return to chest height before initiating the next squat.', 'Plate', 'Intermediate'),
(697, 'Dumbbell Overhead Squat', 'Primary Quads Secondary Glutes Tertiary Shoulders', '', 'Neutral', 'Push', 'Stand with your feet slightly wider than shoulder-width apart and hold two dumbbells overhead with both hands, using an overhand grip., Begin the squat by bending at the hips and knees, keeping your core tight and the dumbbells balanced overhead., Lower your body as far as you can while keeping the dumbbells overhead, then push back up to the starting position., Repeat for the desired number of repetitions, making sure to keep your back straight, chest lifted, and the dumbbells stable overhead throughout the entire movement.', 'Dumbbells', 'Advanced'),
(698, 'Single Arm Overhead Squat', 'Primary Quads Secondary Shoulders Tertiary Glutes', '', '', 'Push', 'Stand with your feet shoulder-width apart and a dumbbell in your right hand, extending it overhead., Inhale and begin to squat down, keeping the dumbbell overhead and your back straight., Go down as far as you can while still maintaining control, then exhale and return to standing position, keeping the dumbbell overhead. Repeat for desired reps and then switch to the other arm.', 'Dumbbells', 'Advanced'),
(699, 'TRX Curtsy Lunge', 'Primary Quads Secondary Glutes', '', 'Overhand Neutral', 'Push', 'Stand facing away from the anchor point, holding onto the TRX handles with both hands., Step your left foot behind your right, crossing it behind your right leg, and lower yourself into a curtsy lunge., Push through your right foot to return to the starting position and repeat the movement with the other leg.', 'TRX', 'Intermediate'),
(700, 'TRX Jumping Squat', 'Primary Quads Secondary Glutes', '', 'Overhand Neutral', 'Push', 'Stand facing the TRX straps and place your feet shoulder-width apart., Begin by lowering into a squat position by bending at the knees and hips, keeping your back straight and chest up., Explosively jump up off the ground, using your arms for momentum if desired., Land softly back in the squat position, making sure to absorb the impact with your legs.', 'TRX', 'Intermediate'),
(701, 'TRX Lateral Lunge', 'Primary Quads Secondary Glutes', '', 'Overhand Neutral', 'Push', 'Grasp the TRX handles with both hands and extend your arms straight in front of you., Step laterally to one side, keeping your arms extended and your core engaged., Lower your body into a lunge position, keeping your front knee bent at a 90-degree angle and your back leg straight., Push off your front foot to return to the starting position, keeping your arms extended and your core engaged.', 'TRX', 'Intermediate'),
(702, 'TRX Reverse Lunge', 'Primary Quads Secondary Glutes', '', 'Overhand Neutral', 'Push', 'Hold on to the TRX straps and step away until the strap is taut., Step backwards with one leg in to a lunge position and gently tap your knee to the floor., Lunge back to the starting position and repeat with the other leg.', 'TRX', 'Beginner'),
(703, 'TRX Split Squat', 'Primary Quads Secondary Glutes', '', 'Overhand Neutral', 'Push', 'Hold the two TRX straps and step away until the straps are taut. Take a split stance., Bring your back knee to the floor and gently tap the floor., Use your arms and the TRX to help assist you getting back into the starting position.', 'TRX', 'Beginner'),
(704, 'TRX Squat', 'Primary Quads Secondary Glutes', '', 'Overhand Neutral', 'Push', 'Hold the two TRX straps and lean away from the anchor point., Flex at the hips and knees in to a squat position., You can use your arms and the straps to assist you back into the starting position.', 'TRX', 'Beginner'),
(705, 'Double Pigeon Pose', 'Primary Glutes', '', '', 'Hold', 'Sit on the ground with your legs extended in front of you., Place a block under your left knee, then bend your right knee and stack it on top of your left ankle., Flex both feet and gently press your knees towards the ground, feeling a stretch in your hips., Hold the pose for a few breaths, then switch sides and repeat.', 'Yoga', 'Beginner'),
(706, 'Pigeon Pose', 'Primary Glutes Secondary Quads', '', '', 'Hold', 'Extend your right leg behind you and place your right ankle on top of your left thigh, with your right knee pointing out to the side., Sitting up straight, use your hands to gently press down on your right knee while at the same time pulling your left buttock back., Hold for 5-10 deep breaths, then release and repeat on the other side.', 'Yoga', 'Beginner'),
(707, 'Reclining Pigeon Pose', 'Primary Glutes', '', '', 'Hold', 'Lie down on your back with your knees bent and your feet on the ground., Cross your right ankle over your left knee, creating a figure four shape., Use your hands to gently pull your left knee towards your chest, feeling a stretch in your right hip., Hold the pose for a few breaths, then switch sides and repeat.', 'Yoga', 'Beginner'),
(708, 'Restful Cow Face Pose Legs', 'Primary Glutes', '', '', 'Hold', 'Start in a seated position with your legs extended in front of you., Cross your right leg over your left leg, placing your right foot on the floor beside your left hip., Cross your left leg over your right leg, placing your left foot on the floor beside your right hip., Relax your arms by your sides or place them on your abdomen and take deep breaths.', 'Yoga', 'Intermediate'),
(709, 'Standing Figure Four', 'Primary Glutes', '', '', 'Hold', 'Begin in a standing position with your feet hip-distance apart., Lift your right foot and place it on your left thigh, just above the knee. Keep your right knee out to the side and your foot flexed., Press your hands together in front of your chest or reach your arms overhead, keeping your shoulders relaxed and away from your ears., Hold the pose for several breaths before releasing and switching sides.', 'Yoga', 'Beginner'),
(710, 'Half Lotus', 'Primary Glutes', '', '', 'Hold', 'Sit on a yoga mat with legs straight in front of you., Bend the right knee and place the right foot on top of the left thigh. Allow the right knee to fall towards the floor., Bring the left foot onto the right thigh. Keep the spine straight and rest hands on the knees., Hold for several breaths and then release.', 'Yoga', 'Beginner'),
(711, 'Barbell Behind The Back Wrist Curl', 'Primary Forearms', '', '', '', 'Stand straight and hold the barbell behind you using a pronated grip with your hands and feet shoulder-width apart., Slowly curl your wrists in a semi-circular motion upwards., Hold the barbell at the apex of the motion and then slowly lower the barbell back down to starting position.', 'Barbell', 'Intermediate'),
(712, 'Dumbbell Wrist Curl', 'Primary Forearms', 'https://www.youtube.com/embed/S-ynXc4M-mY', 'Underhand', 'Pull', 'Grip the dumbbell with your palm facing upwards with your forearm rested against the bench., Slowly curl your wrist upwards in a semicircular motion., Return to starting position and repeat.', 'Dumbbells', 'Beginner'),
(713, 'Barbell Wrist Curl', 'Primary Forearms', '', '', '', 'Grab a straight bar with a supinated (underhand) grip., Kneel down next to a flat bench and place your forearms on the bench with your wrists just off the bench., Let the barbell pull down on your wrists until they are extended., Curl the barbell until your wrists are fully flexed., Lower in a controlled manner and repeat.', 'Barbell', 'Beginner'),
(714, 'Kettlebell Farmers Carry', 'Primary Forearms', '', 'Neutral', 'Pull', 'Standing straight with a kettlebell in one hand, lift the knee of one leg off the floor to knee height., Bring the leg back to standing and repeat with the other leg, keeping the kettlebell on the same side., Repeat.', 'Kettlebells', 'Beginner');
INSERT INTO `workout` (`ID`, `exercise_name`, `target`, `youtubeURL`, `Grips`, `Force`, `steps`, `Category`, `Difficulty`) VALUES
(715, 'Kettlebell Wrist Extension', 'Primary Forearms', '', '', '', 'Stand up straight with a kettlebell in both hands in front of your pelvis, with your forearms facing inwards., Flex your wrist upwards until wrists are fully extended., Lower to the starting position and repeat.', 'Kettlebells', 'Beginner'),
(716, 'Kettlebell Wrist Curl', 'Primary Forearms', '', '', '', 'Stand up straight with a kettlebell in both hands in front of your pelvis, with your forearms facing outwards., Flex your wrist towards your body until the kettlebell is level with your stomach., Lower to the starting position and repeat.', 'Kettlebells', 'Beginner'),
(717, 'Forearms Stretch Variation Four', 'Primary Forearms', '', 'None', '', 'Raise your arm to shoulder height at 90 degrees to your body., Press lightly on your hand and push towards you., Rotate the arm across your body., Switch sides and repeat.', 'Stretches', 'Beginner'),
(718, 'Forearms Stretch Variation Three', 'Primary Forearms', '', 'None', '', 'Place your hands together., Rotate your arms and hands 180 degrees., Hold at the peak of the stretch., Return to starting position.', 'Stretches', 'Beginner'),
(719, 'Forearms Stretch Variation Two', 'Primary Forearms', '', 'None', '', 'Place your hand against a wall, palms facing upward, with finger tips touching., Hold at the peak of the stretch., Return to starting position., Slowly lean into your palm.', 'Stretches', 'Beginner'),
(720, 'Forearms Stretch Variation One', 'Primary Forearms', '', 'None', '', 'Place your palm flush against the wall., Take one step forward and straighten your arm slowly to extend your bicep., Hold at the peak of the stretch., Return to starting position.', 'Stretches', 'Beginner'),
(721, 'Cable Wrist Curl', 'Primary Forearms', '', 'Underhand', '', 'Use a handle attachment. The cable should be set all the way to the bottom of the machine., Face away from the cable machine. Stagger your stance so you have a better base of support., Start with your elbow at a 90Â° angle with your palm facing up. Flex and extend at the wrist.', 'Cables', 'Intermediate'),
(722, 'Cable Wrist Extension', 'Primary Forearms', '', 'Overhand', '', 'Use a handle attachment. The cable should be set all the way to the bottom of the machine., Face away from the cable machine. Stagger your stance so you have a better base of support., Start with your elbow at a 90 degree angle with your palm facing down. Flex and extend at the wrist.', 'Cables', 'Advanced'),
(723, 'Dumbbell Wrist Extension', 'Primary Forearms', 'https://www.youtube.com/embed/lyMpCIUE6ms', 'Overhand', 'Pull', 'Grab two dumbbells with an overhand grip and lay your forearms across your knees., Let your wrists flex fully, then extend your wrists.', 'Dumbbells', 'Beginner'),
(724, 'Plate Pinch Grip Side Bend', 'Primary Forearms Secondary Obliques', '', 'Pinch Grip', '', 'Stand straight with the plate held at your side, Keep your hips and shoulders facing forward, bend to one side slowly, Return to the upright position., Switch hands and repeat', 'Plate', 'Beginner'),
(725, 'Plate Pinch Grip Farmer\'s Carry', 'Primary Forearms', '', 'Pinch Grip', 'Hold', 'Stand tall holding two plates in each hand with a pinch grip., Walk for the desired distance or time.', 'Plate', 'Intermediate'),
(726, 'Plate Pinch Grip Deadlift', 'Primary Lats Forearms Secondary Hamstrings Glutes Tertiary Lower back', '', 'Pinch Grip', '', 'Stand facing the plate with your feet shoulder-width apart and the plate positioned between your feet., Bend down and grasp the plate from the top edges with both hands, making sure your thumbs and fingers are evenly spaced., Keep your back straight, chest up and core engaged, and stand up with the plate, keeping it close to your body., Lower the plate back down to the ground, keeping control and maintaining good posture.', 'Plate', ''),
(727, 'Plate Pinch Grip Row', 'Primary Forearms Secondary Lats Tertiary Biceps', '', 'Pinch Grip', 'Pull', 'Make sure to keep your elbow close to your body and your core engaged throughout the movement., Bend forward at your hips while maintaining a flat back., Pull the plate toward your upper abdomen., Lower the weight in a controlled manner and repeat.', 'Plate', 'Intermediate'),
(728, 'Inverted Row', 'Primary Lats Mid back Secondary Biceps Tertiary Forearms', '', 'Overhand Underhand', 'Pull', 'Lay underneath a fixed horizontal bar and grab the bar with a wide overhand grip., Pull your body straight up to the bar., Return to the starting position, your arms fully extended. Repeat.', 'Bodyweight', 'Beginner'),
(729, 'Chin Ups', 'Primary Lats Secondary Biceps Tertiary Forearms', 'https://www.youtube.com/embed/y6x0AIhgjsM', 'Underhand', 'Pull', 'Grab the bar shoulder width apart with a supinated grip (palms facing you), With your body hanging and arms fully extended, pull yourself up until your chin is past the bar., Slowly return to starting position. Repeat.', 'Bodyweight', 'Intermediate'),
(730, 'Cable Reverse Curl', 'Primary Biceps Secondary Forearms', '', '', '', 'Use a handle attachment. The cable should be set all the way to the bottom of the machine., Face away from the cable machine. Stagger your stance so you have a better base of support., Face your palm downwards. Flex at the elbow and extend.', 'Cables', 'Intermediate'),
(731, 'Band Bayesian Hammer Curl', 'Primary Biceps Forearms', 'https://www.youtube.com/embed/tvRlfjjH5NA', '', '', 'Attach band to a low anchor point. Use a neutral grip., Flex your elbow until you feel your biceps contract. Then fully extend elbows.', 'Band', 'Intermediate'),
(732, 'Band Bayesian Reverse Curl', 'Primary Biceps Secondary Forearms', '', '', '', 'Attach band to a low anchor point. . Point your palms toward your thighs., Flex your elbow until you feel your biceps contract. Then fully extend elbows.', 'Band', 'Intermediate'),
(733, 'Dumbbell Reverse Curl', 'Primary Biceps Secondary Forearms', '', 'Overhand', 'Pull', '\"Grab the dumbbells with a pronated (overhand) grip. You can do this exercise thumbless if its more comfortable on your wrists.\"\"', 'Dumbbells', 'Intermediate'),
(734, 'Dumbbell Row Unilateral', 'Primary Lats Mid back Secondary Biceps Tertiary Forearms', 'https://www.youtube.com/embed/r4-3p0KgEA8', 'Neutral', 'Pull', 'Brace your off arm against something stable (bench, box). Stagger your stance so your leg on the side of your working arm is back., Try to get your torso to parallel with the ground. That will extend your range of motion., \"Let your arm hang freely and then pull your elbow back. Imagine youve got a tennis ball in your armpit and squeeze it each rep.\"\"\"', 'Dumbbells', 'Beginner'),
(735, 'Barbell Reverse Curl', 'Primary Biceps Secondary Forearms', '', 'Overhand', 'Pull', '\"Take a double overhand grip thats about shoulder width. Flex your elbows while keeping your elbows tucked in. Try not to let them flare out.\"\"', 'Barbell', 'Advanced'),
(736, 'Barbell Landmine Row', 'Primary Lats Mid back Secondary Biceps Tertiary Forearms', '', 'Overhand', 'Pull', 'Position a barbell in a landmine or corner of a room and stand facing it with your feet shoulder-width apart., Grasp the end of the barbell with a neutral grip, keeping your arms straight and your shoulders relaxed., Bend at the hips and knees to lower your torso until it is almost parallel to the ground., Pull the barbell towards your chest, squeezing your shoulder blades together as you row the weight up.', 'Barbell', 'Intermediate'),
(737, 'Barbell Long Landmine Row', 'Primary Lats Secondary Biceps Tertiary Forearms', '', 'Neutral', 'Pull', 'Position a barbell in a landmine or corner of a room and stand facing it with your feet shoulder-width apart., Grasp the end of the barbell with an overhand grip, keeping your arms straight and your shoulders relaxed., Bend at the hips and knees to lower your torso until it is almost parallel to the ground., Pull the barbell towards your chest, squeezing your shoulder blades together as you row the weight up.', 'Barbell', 'Advanced'),
(738, 'Barbell Meadows Row', 'Primary Lats Secondary Biceps Tertiary Forearms', '', 'Overhand', 'Pull', 'Position a barbell in a landmine or corner of a room and stand facing sideways with a staggered stance and your feet shoulder-width apart., Grasp the end of the barbell with an overhand grip, keeping your arms straight and your shoulders relaxed., Bend at the hips and knees to lower your torso until it is almost parallel to the ground., Pull the barbell towards your chest, squeezing your shoulder blades together as you row the weight up., *Named after the late John Meadows', 'Barbell', 'Intermediate'),
(739, 'Barbell Pronated Row', 'Primary Lats Secondary Biceps Tertiary Forearms', '', 'Overhand', 'Pull', 'Grab the barbell with an overhand grip. Hinge forward while keeping your back flat until your torso is about parallel with the ground., Let your arms hang freely., Pull the bar to your torso while tucking your elbows in slightly., Extend your elbows fully before initiating the next rep.', 'Barbell', 'Beginner'),
(740, 'Dumbbell Incline Reverse Curl', 'Primary Biceps Secondary Forearms', '', 'Overhand', 'Pull', 'Lay on an incline bench., \"Grab the dumbbells with a pronated (overhand) grip. You can do this exercise thumbless if its more comfortable on your wrists.\"\"', 'Dumbbells', 'Beginner'),
(741, 'Dumbbell Incline Zottman Curl', 'Primary Biceps Secondary Forearms', '', 'Rotating', 'Pull', 'Lay on an incline bench with your palms facing forward., Flex at the elbows until your forearms touch your biceps., When you hit the top of the movement, rotate your palms and then lower the dumbbells slowly.', 'Dumbbells', 'Intermediate'),
(742, 'Plate Single Leg Pinch Grip Deadlift', 'Primary Hamstrings Forearms Secondary Glutes Tertiary Lower back', '', 'Pinch Grip', 'Pull', 'Stand with your feet shoulder-width apart and facing forwards. Grip the sides of a weight plate with your palms facing down., Balance on one foot, as you raise the weight keep your arms and back straight, while you bring your other foot back towards the ground, Keep four raised leg straight and in alignment with your back as you lower the plate back down to the starting position.', 'Plate', 'Intermediate'),
(743, 'Band High Reverse Curl', 'Primary Biceps Secondary Forearms', '', 'Overhand', 'Pull', 'Start with a band attached to a high point, your feet staggered, and an overhand grip., Flex your elbows pulling your forearms to your biceps. Your fists should land by your forehead.', 'Band', 'Intermediate'),
(744, 'Band Reverse Curl', 'Primary Biceps Secondary Forearms', '', 'Overhand', 'Pull', 'Start by standing on the center of the band with both feet, shoulder-width apart., Hold the band with both hands, palms facing down, arms extended straight in front of you., Slowly curl your hands towards your shoulders, pulling the band tight, then lower your arms back to the starting position, keeping tension on the band throughout the exercise.', 'Band', 'Intermediate'),
(745, 'Plate Staggered Pinch Grip Deadlift', 'Primary Hamstrings Forearms Secondary Glutes Tertiary Lower back', '', 'Pinch Grip', 'Pull', 'Start by standing with your feet shoulder-width apart and holding two plates with a pinch grip., Place one foot slightly behind the other, with the heel of your back foot elevated., From this starting position, bend at the hips and knees to lower the plates towards the ground, keeping your back straight and your core engaged.', 'Plate', 'Advanced'),
(746, 'TRX Reverse Curl', 'Primary Biceps Secondary Forearms', '', 'Overhand', 'Pull', 'Stand facing the TRX straps and grasp the handles with both hands, palms facing away from your body., Walk back until the straps are taut, keeping your arms straight., Slowly curl your hands towards your shoulders, keeping your elbows close to your body.', 'TRX', 'Intermediate'),
(747, 'Table Top Pose Variation 1', 'Primary Forearms', '', '', 'Hold', 'Start in a tabletop position on your hands and knees with your hands directly under your shoulders and your knees directly under your hips., Turn your hands so that your fingers are pointing towards the sides of your mat and the tops of your hands are on the floor., Keep your arms straight and your shoulders away from your ears., Hold the pose for several breaths or as long as is comfortable.', 'Yoga', 'Beginner'),
(748, 'Table Top Pose Variation 2', 'Primary Forearms', '', '', 'Hold', 'Start in a tabletop position on your hands and knees with your hands directly under your shoulders and your knees directly under your hips., Turn your hands so that your fingers are pointing towards your knees and the tops of your hands are on the floor., Keep your arms straight and your shoulders away from your ears., Hold the pose for several breaths or as long as is comfortable.', 'Yoga', 'Beginner'),
(749, 'Table Top Pose Variation 3', 'Primary Forearms', '', '', 'Hold', 'Come onto your hands and knees on the mat with your wrists directly under your shoulders and your knees under your hips., Turn your palms down and spread your fingers wide, with your fingertips pointing towards your knees., Press into your palms and extend your arms fully to lift your shoulders away from your ears., Hold the pose for a few breaths, and then release by slowly lowering your hands to the mat and coming back to a neutral tabletop position.', 'Yoga', 'Beginner'),
(750, 'Vajrasana Variation 1', 'Primary Forearms Secondary Mid back', '', '', 'Hold', 'Begin in a kneeling position with your knees together and your shins and feet on the floor., Sit back on your heels and place your palms face down on your thighs, with your fingers pointing towards your knees., Relax your shoulders and take deep breaths., Hold the pose for several breaths or as long as is comfortable.', 'Yoga', 'Beginner'),
(751, 'Vajrasana Variation 2', 'Primary Forearms Secondary Mid back', '', '', 'Hold', 'Begin in a kneeling position with your knees together and your shins and feet on the floor., Sit back on your heels and place the tops of your hands on your thighs, with your fingers pointing towards your knees., Relax your shoulders and take deep breaths., Hold the pose for several breaths or as long as is comfortable.', 'Yoga', 'Beginner'),
(752, 'Machine Standing Calf Raises', 'Primary Calves', '', 'None', 'Push', 'Adjust the machine in accordance with your height and place your shoulders underneath the padded lever., The balls of your feet should be supporting your weight on the calve block, your heels extending off of it., Extend your heels upwards while keeping your knees stationary, and pause at the contracted position., Slowly return to the starting position. Repeat.', 'Machine', 'Beginner'),
(753, 'Machine Seated Calf Raises', 'Primary Calves', '', 'None', 'Push', 'Get comfortable on the machine, then place your lower thighs beneath the padded lever. Place your toes and the balls of your feet onto the foot supports., Prevent the weight from slipping forward by gripping the handles, and release the safety bar. Lower the weight until your calves are extended., Push your heels up to lift the padded lever and hold the contracted position, then slowly lower back down to the starting position. Repeat.', 'Machine', 'Beginner'),
(754, 'Kettlebell Single Leg Calf Raise', 'Primary Calves', '', 'Overhand Neutral', 'Push', 'Standing straight with a kettlebell in one hand, lift the same foot as the side without the kettlebell off the floor., Raise your heel upwards while keeping your knees stationary., Pause when your heels are fully extended and then slowly return to the starting position and repeat.', 'Kettlebells', 'Beginner'),
(755, 'Kettlebell Calf Raise', 'Primary Calves', '', '', '', 'Standing straight with a kettlebell in each hand, raise your heels upwards while keeping your knees stationary., Pause when your heels are fully extended and then slowly return to the starting position and repeat.', 'Kettlebells', 'Beginner'),
(756, 'Kettlebell Seated Calf Raise', 'Primary Calves', '', 'Overhand Neutral', 'Push', 'Sitting comfortably on a chair with your core stomach muscles engaged, place your feet on the floor., Place the kettlebell on your legs, just above the knees and raise your heels upwards off the floor., Pause when your heels are fully extended and then slowly return to the starting position and repeat.', 'Kettlebells', 'Beginner'),
(757, 'Calves Stretch Variation Three', 'Primary Calves', '', 'None', '', 'Balance the balls of your feet on the platform, leaning forward to use the wall to assist with balance. Engage your calves so that your feet are flat before beginning the stretch., Lower the heels of your feet towards the ground and pause, then push through the balls of your feet like you are standing tip toe, pausing at the apex of the stretch., Repeat as necessary.', 'Stretches', 'Beginner'),
(758, 'Calves Stretch Variation Two', 'Primary Calves', '', 'None', '', 'With both feet flat on the ground, place one leg in front of your shoulder, your other leg should mirror this position behind your other shoulder. In the starting position both of your knees should be straight., Place the palms of your hands against the wall at shoulder height in front of you, lean towards the wall and bend your front leg at the knee while keeping your back leg straight., Pause at the apex of the stretch and return to the starting position. Switch the position of your legs and repeat.', 'Stretches', 'Beginner'),
(759, 'Calves Stretch Variation One', 'Primary Calves', '', 'None', '', 'Place the ball of your foot against the wall with your heel on the ground. Place your hands on the wall in front of your shoulders., Keeping your knee straight, slowly lean towards the wall, pause for a few seconds when you feel the stretch extending through your calf muscle., Return to the starting position, and repeat the stretch with your other leg.', 'Stretches', 'Beginner'),
(760, 'Tip Toe Walking', 'Primary Calves', '', '', '', 'Point your toes into the floor., Slowly walk forwards and backward, Keep your heels in the air as long as possible while walking., It may be beneficial to do this last at the end of your workout.', 'Bodyweight', 'Beginner'),
(761, 'Calf Raises', 'Primary Calves', '', 'None', 'Push', 'Balance on the balls of your feet on the platform or plates, leaning forward to use the wall to assist with balance., Lower the heels of your feet towards the ground and pause, then push through the balls of your feet like you are standing tip toe, pausing at the apex of the motion., Repeat as necessary.', 'Bodyweight', 'Beginner'),
(762, 'Walking Calf Raises', 'Primary Calves', '', 'None', 'Push', 'Walk forward and push your toe into the ground, Lift you heel off the ground each step and tense your calf muscle, Repeat on each leg and walk forward slowly', 'Bodyweight', 'Beginner'),
(763, 'Barbell Calf Raises', 'Primary Calves', '', 'Overhand', 'Push', 'Place the bar on your back, Start with feet flat on the ground, Extend your heels upwards while keeping your knees stationary, and pause at the contracted position., Slowly return to the starting position. Repeat.', 'Barbell', 'Intermediate'),
(764, 'Cable Calve Raise', 'Primary Calves', '', '', '', 'Use a straight bar or ez bar attachment with the cable set all the way to the bottom., Walk a few steps back., Pull your heels straight up until you feel a contraction in your calves and then back to original position.', 'Cables', 'Beginner'),
(765, 'Band Calf Raise', 'Primary Calves', '', '', '', 'Attach band to a low anchor point., Take a few steps away until the band is taut., Lean back slightly. Raise your heels straight up and down', 'Band', 'Beginner'),
(766, 'Dumbbell Calf Raise', 'Primary Calves', '', 'Overhand Neutral', 'Push', 'Stand tall with your feet on the ground. You can put the the balls of your feet on top of a plate to extend the range of motion., Imagine you have a string attached to your heels and pull your heels up toward the ceiling.', 'Dumbbells', 'Beginner'),
(767, 'Barbell Calf Jump', 'Primary Calves', '', 'None', 'Push', 'Stand with your feet shoulder-width apart and your knees slightly bent., \"Shift your weight onto the balls of your feet and raise stay flat footed. Using your calf muscles', 'Barbell', 'Advanced'),
(768, 'Barbell Seated Calf Raise', 'Primary Calves', '', 'Overhand Underhand Mixed', 'Push', 'Start by sitting on a bench with a barbell resting on your knees. Your feet should be flat on the ground, about hip-width apart., Place your hands on the barbell for support, and lift your heels off the ground., Hold this position for a moment, then slowly lower your heels back to the ground.', 'Barbell', 'Beginner'),
(769, 'Barbell Toes Up Calf Raise', 'Primary Calves', '', 'Overhand', 'Push', 'Begin by standing on a raised surface (such as a step or box) with the balls of your feet on the edge of the surface and your heels hanging off. Hold a barbell across your upper back and shoulders, using an overhand grip., Keeping your core engaged and your back straight, push through the balls of your feet to raise your heels up as high as you can., Lower your heels back down to the starting position, allowing your calves to stretch.', 'Barbell', 'Beginner'),
(770, 'Dumbbell Seated Calf Raise', 'Primary Calves', '', 'Overhand', 'Push', 'Start by sitting on a bench with your feet shoulder-width apart and a dumbbell in each hand placed on each leg., From this starting position, raise your heels as high as you can by pushing up through your toes., Slowly lower your heels back down to the starting position, and repeat for the desired number of repetitions.', 'Dumbbells', 'Beginner'),
(771, 'TRX Calf Raise', 'Primary Calves', '', 'Neutral', 'Push', 'Hold the TRX straps under your arms and the handles in your armpits., Lean forward against the straps., Keep your body straight and initiate the calf raise by raising your heels off the ground.', 'TRX', 'Beginner'),
(772, 'Downward Dog Toe To Heel', 'Primary Calves Secondary Hamstrings', '', '', 'Hold', 'Begin in a downward-facing dog position with your hands shoulder-width apart and your feet hip-width apart., Lift your right foot off the mat and place your right toes on top of your left heel., Press your palms and fingers firmly into the mat and lift your hips up towards the ceiling, deepening the stretch in your hamstrings and calves., Hold the pose for a few breaths, and then release by slowly walking your hands back out to a downward-facing dog position.', 'Yoga', 'Beginner'),
(773, 'Pyramid Pose Calf Blocks', 'Primary Calves Secondary Hamstrings', '', '', 'Hold', 'Start standing with a flat and vertical block on the matt, Bend forward, placing hand on vertical block, and front of foot on the flat block., Press the bottom of your foot towards the ground, and repeat on each side.', 'Yoga', 'Beginner'),
(774, 'Walk Your Downward Dog', 'Primary Calves Secondary Hamstrings', '', '', 'Hold', 'Begin in a downward-facing dog position with your hands shoulder-width apart and your feet hip-width apart., Walk your feet towards your hands, bending your knees as needed to allow your heels to touch the mat., Press your palms and fingers into the mat and lift your hips up towards the ceiling to deepen the stretch in your hamstrings and calves., Hold the pose for a few breaths, and then release by slowly walking your hands back out to a downward-facing dog position.', 'Yoga', 'Beginner'),
(775, 'Crunches', 'Primary Abdominals', 'https://www.youtube.com/embed/QSkX3gA8cd0', 'None', 'Push', 'Lay flat on your back with your knees bent and your feet flat on the ground, about a foot from your lower back., Place your fingertips on your temples with your palms facing out., Draw your belly into the base of your spine to engage the muscles, then raise your head and shoulders off the floor. Return to starting position and repeat.', 'Bodyweight', 'Beginner'),
(776, 'Laying Leg Raises', 'Primary Abdominals', '', 'None', 'Pull', 'Lay on your back with your arms palms down on either side., Keep your legs together and as straight as possible., Slowly raise your legs to a 90Â° angle, pause at this position, or as high as you can reach your legs, and then slowly lower your legs back down., Duration of these movements should be slow so that you do not utilize momentum, enabling you to get the most out of the exercise.', 'Bodyweight', 'Beginner'),
(777, 'Machine Roll Outs', 'Primary Abdominals', '', '', '', 'Hold the Ab Roller with both hands and kneel on the floor with your feet up and crossed., Slowly roll the ab roller straight forward, stretching your body into a straight position., After a pause at the stretched position, start pulling yourself back to the starting position. This should be a slow and controlled movement.', 'Machine', 'Intermediate'),
(778, 'Kettlebell Situp', 'Primary Abdominals', 'https://www.youtube.com/embed/rPy1_8f3vIY', '', '', 'Lay flat on your back with straight legs and hold the kettle bell above your chest at arms length., Draw your belly into the base of your spine to engage the muscles, then raise your head and shoulders off the floor with the kettlebell raised at arms length above your chest., Return to the starting position and repeat.', 'Kettlebells', 'Beginner'),
(779, 'Abdominals Stretch Variation Four', 'Primary Abdominals', '', 'None', '', 'Lay on a ball or a Bosu ball with your feet firmly planted on the floor., Lean all the way back until you feel a stretch in your abdomen., Crunch upwards and hold for a 1-2 count., Slowly return to the starting position and repeat.', 'Stretches', 'Beginner'),
(780, 'Abdominals Stretch Variation Three', 'Primary Abdominals Obliques', '', 'None', '', 'Reach up with your left arm and then lean slowly to the right. Lean until a stretch is felt in the obliques., Stand upright., After completing the desired amount of reps with the left arm, switch to the right arm and lean to the left.', 'Stretches', 'Beginner'),
(781, 'Abdominals Stretch Variation Two', 'Primary Abdominals Obliques', '', 'None', '', 'Stand upright., Reach with both hands upwards until you can interlock your fingers., With your hands above your head, lean back until a stretch is felt in the abdominals.', 'Stretches', 'Beginner'),
(782, 'Abdominals Stretch Variation One', 'Primary Abdominals', '', 'None', '', 'Lay on your stomach on the floor with your forearms flat on the ground., Extend your elbows and push your upper body upwards., Push your upper body upwards until you feel a stretch in your abs, then return to the starting position and repeat.', 'Stretches', 'Beginner'),
(783, 'Forearm Plank', 'Primary Abdominals', 'https://www.youtube.com/embed/QMXncCeneZI', 'Overhand Neutral', 'Hold', 'Place forearms on the ground with your elbows bent at a 90Â° angle aligned beneath your shoulders, with your arms parallel at shoulder-width., Your feet should be together, with only your toes touching the floor., Lift your belly off the floor and form a straight line from your heels to the crown of your head and hold.', 'Bodyweight', 'Beginner'),
(784, 'Hanging Knee Raises', 'Primary Abdominals', 'https://www.youtube.com/embed/F3-vRC8f5IY', 'Overhand Neutral', 'Pull', 'Grab the bar and hang, your body still and your legs straight., Slowly draw your knees up to your chest, Once you have raised your knees as high as possible, lower your legs and repeat. Duration of these movements should be slow so that you do not utilize momentum, enabling you to get the most out of the exercise., Duration of these movements should be slow so that you do not utilize momentum, enabling you to get the most out of the exercise.', 'Bodyweight', 'Beginner'),
(785, 'Barbell Roll Outs', 'Primary Abdominals', '', 'Overhand', 'Pull', 'Hold the Barbell with both hands and kneel on the floor with your feet up, Slowly roll the Barbell straight forward, stretching your body into a straight position., After a pause at the stretched position, start pulling yourself back to the starting position. This should be a slow and controlled movement.', 'Barbell', 'Intermediate'),
(786, 'Cable Standing Crunch', 'Primary Abdominals', '', 'Neutral', '', 'Use a double handle attachment and set the cable all the way to the top., Face away from the cable tower and walk a few steps forward., Push your hips back, and flex your abs., Return to the starting standing position and repeat.', 'Cables', 'Advanced'),
(787, 'Cable Kneeling Crunch', 'Primary Abdominals', '', 'Neutral', '', 'Use a double handle attachment and set the cable all the way to the top., Walk a few steps forward then fall into a kneeling position., Push your hips back and flex your abs, then push hips forward until in the starting position.', 'Cables', 'Beginner'),
(788, 'Band Crunch', 'Primary Abdominals', '', '', '', 'Place the band at the highest anchor point you have available. Grab both ends of the band and fall into a kneeling position., Push your hips back flexing at the spine., Squeeze your abs and then extend at the hips and spine back to the starting position.', 'Band', 'Intermediate'),
(789, 'Dumbbell Situp', 'Primary Abdominals', 'https://www.youtube.com/embed/rPy1_8f3vIY', '', '', 'Lay flat on the ground with your spine neutral. Extend you arms so your fists are pointed at the ceiling., Sit-up by flexing your abs. Continue to reach for the ceiling for the entire rep.', 'Dumbbells', 'Intermediate'),
(790, 'Dumbbell Plank Pullthrough', 'Primary Abdominals', '', '', '', 'Get into a plank position. Either from your knees or toes., Start with the DB by your right hand. Reach with your left and pull the dumbbell to your left arm. Then repeat with the right.', 'Dumbbells', 'Intermediate'),
(791, 'Barbell Situp', 'Primary Abdominals', 'https://www.youtube.com/embed/rPy1_8f3vIY', 'Overhand', 'Push', 'Hold the bar with a slightly wider than shoulder width grip. Push the bar up toward the ceiling and maintain that elbow extension., Flex your abs and sit-up. Aim to get your torso vertical while maintaining the barbell overhead.', 'Barbell', 'Intermediate'),
(792, 'Long Lever Plank', 'Primary Abdominals', 'https://www.youtube.com/embed/gD7rdimyXBU', 'Overhand', 'Hold', 'Start in a hand plank position and walk your hands further out until your ears are between your biceps., Hold in this position for the allotted amount of time., Make sure you keep a flat back for the whole set.', 'Bodyweight', 'Advanced'),
(793, 'Hand Plank', 'Primary Abdominals', 'https://www.youtube.com/embed/QMXncCeneZI', 'Overhand', 'Hold', 'Start in a kneeling position with your hands planted on the ground., Pick your knees up off the ground. Hold in this position with a flat back.', 'Bodyweight', 'Beginner'),
(794, 'Hollow Hold', 'Primary Abdominals', 'https://www.youtube.com/embed/gD7rdimyXBU', 'None', 'Hold', 'Lay flat on the ground with your arms extended over head., Pick your upper back and your legs off the ground. Your body should be in a banana shape., Hold in this position.', 'Bodyweight', 'Advanced'),
(795, 'Dumbbell Hollow Hold', 'Primary Abdominals', 'https://www.youtube.com/embed/gD7rdimyXBU', 'Overhand Neutral', 'Hold', 'Lay flat on the ground with a dumbbell overhead., Pull your upper back and legs off the ground. Make a banana shape with your body., The dumbbell should be held in your hands and your ears should be between your biceps.', 'Dumbbells', 'Advanced'),
(796, 'Mountain Climber', 'Primary Abdominals', '', 'Overhand', 'Pull', 'To perform a bodyweight mountain climber, start in a plank position with your hands placed directly under your shoulders., Bring your right knee towards your chest, keeping your foot close to your hand. Quickly switch legs, bringing your left knee towards your chest., Continue this movement, alternating legs, at a quick pace for a set amount of time or repetitions.', 'Bodyweight', 'Beginner'),
(797, 'Plate Situp', 'Primary Abdominals', '', 'Neutral', 'Push', 'Start by lying on your back on a mat with your legs bent and feet flat on the ground., Hold a weight plate over your head with both hands, making sure to keep your arms straight., Engage your core and use your abdominal muscles to sit up, keeping the weight plate towards the ceiling.', 'Plate', 'Beginner'),
(798, 'Weighted Forearm Plank', 'Primary Abdominals', '', 'None', 'Hold', 'Get into a kneeling position with both forearms pressed into the ground., Place a weight plate on your back (or have a partner do it for you)., Hold the plank for the allotted amount of time.', 'Plate', 'Intermediate'),
(799, 'Weighted Hand Plank', 'Primary Abdominals', '', 'Overhand', 'Hold', 'Start in a kneeling position with your hands planted on the ground., Place a weight plate on your back or have a friend do it for you., Pick your knees up off the ground. Hold in this position with a flat back.', 'Plate', 'Intermediate'),
(800, 'Plate Hollow Hold', 'Primary Abdominals', 'https://www.youtube.com/embed/gD7rdimyXBU', 'Neutral', 'Hold', 'Lay flat on the ground with your arms extended over head holding a weight plate., Pick your upper back and your legs off the ground. Your body should be in a banana shape.', 'Plate', 'Advanced'),
(801, 'Dumbbell Crunch', 'Primary Abdominals', '', 'Underhand', 'Push', 'Start by lying flat on your back with your feet flat on the floor and your knees bent., Hold a dumbbell over your clavicle., Engage your core muscles and lift your torso off the floor until your shoulders are a few inches off the ground., Lower yourself back down to the starting position, but avoid resting completely on the floor between reps.', 'Dumbbells', 'Intermediate'),
(802, 'Plank IYTW', 'Primary Abdominals Secondary Shoulders', '', 'Neutral', 'Hold', 'Start in a plank position. Hold one dumbbell., Raise the dumbbell straight out in front of you (I), at a 45 degree angle (Y), out to the side (T), and then bend at the elbow while raising (W)., Going through IYTW counts as one rep., \"Make sure to keep your hips stable. If youre unable to', 'Dumbbells', 'Intermediate'),
(803, 'Dumbbell Suitcase Crunch', 'Primary Abdominals', '', 'Neutral', 'Push', 'Lay flat on the ground with a dumbbell. Point the soles of your shoes at the ceiling as well as the dumbbell., Reach for your feet with the dumbbell before bringing your spine back to the floor.', 'Dumbbells', 'Advanced'),
(804, 'TRX Ab Rollout', 'Primary Abdominals Secondary Lats Tertiary Lower back', '', 'Overhand', 'Pull', 'Grasp the TRX handles with both hands, keeping your arms extended and your shoulders away from your ears., Keep your abs engaged, roll forward onto your toes, and extend your arms in front of you., Slowly lower your body down to the ground, keeping your arms straight and your abs engaged., When your head gets between your arms, pause for a moment and then use your abs to pull your body back up to the starting position.', 'TRX', 'Advanced'),
(805, 'TRX Forearm Plank', 'Primary Abdominals', '', 'Neutral None', 'Hold', 'Lie face down on the ground and place your forearms on the ground directly under your shoulders., With your feet in the stirrups, kick both feet into the TRX straps and hold the handles above your head., Tighten your abs, glutes, and legs to form a straight line from your head to your feet.', 'TRX', 'Beginner'),
(806, 'TRX Hand Plank', 'Primary Abdominals', '', 'Overhand', 'Hold', 'Start by facing away from the TRX straps and placing your feet in the TRX handles with your palms on the ground., Lift your knees off the ground to begin the set., Engage your core and maintain this position for the desired amount of time, holding a steady plank.', 'TRX', 'Intermediate'),
(807, 'TRX Kneeling Ab Rollout', 'Primary Abdominals Secondary Lats', '', 'Overhand', 'Pull', 'Begin in a kneeling position with the TRX straps adjusted to mid-calf length., Grasp the TRX handles with both hands, keeping your arms extended and your shoulders away from your ears., Keep your abs engaged, roll forward onto your toes, and extend your arms in front of you., Slowly lower your body down to the ground, keeping your arms straight and your abs engaged.', 'TRX', 'Intermediate'),
(808, 'TRX Knee Tuck', 'Primary Abdominals', '', 'Overhand', 'Pull', 'Place your feet in the TRX stirrups and get in to a hand plank position., Pull your knees to your chest and flex your abs.', 'TRX', 'Beginner'),
(809, 'TRX Mountain Climber', 'Primary Abdominals', '', 'None', 'Pull', 'Start in a plank position with the TRX straps adjusted to mid-calf length. Place your feet in the TRX stirrups., Keep your abs engaged, and bring one knee towards your chest while keeping the other leg extended behind you., Switch legs, bringing the opposite knee towards your chest while keeping your abs engaged and your back straight.', 'TRX', 'Beginner'),
(810, 'TRX Pike', 'Primary Abdominals', '', 'Overhand', 'Pull', 'Place your feet in the stirrups and get in to a plank position., Pull your hips straight up toward the ceiling., \"Lower your hips until youre back in a plank position.\"\"\"', 'TRX', 'Intermediate'),
(811, 'Kettlebell Russian Twist', 'Primary Obliques Secondary Abdominals', '', '', '', 'Sit on the floor with your legs bent, knees together and feet raised slightly off the floor., Starting on one side and sitting up straight with your core muscles engaged, lift the kettlebell off the floor with both hands and bring it across your body to the other side, rotating your chest towards the floor as you do so., Tap the kettlebell lightly on the floor and bring the kettlebell back across your body to the starting position, rotating your chest towards the floor., Repeat, keeping the legs bent and feet raised throughout the exercise.', 'Kettlebells', 'Beginner'),
(812, 'Kettlebell Windmill', 'Primary Obliques Secondary Abdominals', '', 'Overhand Neutral', 'Push', 'Stand with feet slightly wider than shoulder width apart. Hold the kettlebell in one hand and extend your arm above your head., Keeping your legs straight and the kettlebell above your head, lower the relaxed arm towards the ground, twisting your body towards the side of the raised arm., Return to the starting potion, tilt your pelvis forward and repeat.', 'Kettlebells', 'Advanced'),
(813, 'Dumbbell Russian Twist', 'Primary Obliques Secondary Abdominals', '', 'Neutral', 'Pull', 'Sit on the floor and flex your knees and hips to a 90 degree angle., \"Your feet should be hovering off the ground. (If thats too hard start with heels on the floor)\"\"', 'Dumbbells', 'Intermediate'),
(814, 'Bodyweight Russian Twist', 'Primary Obliques Secondary Abdominals', '', 'None', 'Pull', 'Hover your heels just off the ground., Stick your chest out and maintain a flat back. Make all the movement happen at your mid and upper spine., Point your chest to the left, then rotate at the spine until your chest is pointed right.', 'Bodyweight', 'Intermediate'),
(815, 'Barbell Larsen Bench Press', 'Primary Chest Secondary Triceps Tertiary Abdominals', '', 'Overhand', 'Push', 'Start in a bench press position with your legs elevated. Try to keep your knees with a slight bend in them., Unrack the bar then initiate the rep., Bring the bar to your torso and tap your ribcage before pressing back to the ceiling.', 'Barbell', 'Advanced'),
(816, 'Dumbbell Half Kneeling Wood Chopper', 'Primary Obliques Secondary Abdominals', '', 'Neutral', 'Pull', 'Take a half kneeling position. Start with the dumbbell by your knee on the side of the kneeling leg., Rotate at the upper spine, holding the dumbbell away from your body with nearly extended elbows.', 'Dumbbells', 'Intermediate'),
(817, 'Dumbbell Renegade Row', 'Primary Lats Secondary Abdominals', '', 'Neutral', 'Pull', 'Start by getting into a plank position with a dumbbell in each hand. Your feet should be shoulder-width apart and your hands should be placed on the dumbbells, with your palms facing each other., From this starting position, row one dumbbell up towards your chest, keeping your elbow close to your body and your core engaged., Slowly lower the dumbbell back down to the starting position, and repeat on the other side., Make sure to keep your body straight and your hips level throughout the movement.', 'Dumbbells', 'Intermediate'),
(818, 'Burpee', 'Primary Chest Abdominals Secondary Quads Tertiary Glutes', '', 'Overhand', 'Push', 'Start standing with your feet hip-width apart. Lower your body into a squat position, placing your hands on the ground in front of you., Quickly jump your feet back into a plank position, keeping your body in a straight line from your head to your toes., Perform a push-up, keeping your core tight and your elbows close to your body., Jump your feet back towards your hands, and then jump up into the air, reaching your arms overhead., Land softly and immediately lower your body back into the squat position to repeat the movement.', 'Bodyweight', 'Intermediate'),
(819, 'Plate Overhead Carry', 'Primary Traps Abdominals Secondary Shoulders', '', 'Neutral', 'Hold', 'Start by standing upright with your feet shoulder-width apart. Hold a single weight plate overhead with both hands, keeping your arms extended straight above your head., Begin walking forward, taking slow and steady steps while maintaining a straight line. Your focus should be on maintaining balance and control of the weight plate overhead., As you walk, continue to keep your arms extended and the weight plate overhead. Try to maintain good posture and avoid leaning forward or backward, as this could result in losing control of the weight plate., Continue walking for a set distance or for a certain amount of time, keeping your focus on proper form and technique. When finished, return to the starting position and repeat as desired.', 'Plate', 'Beginner'),
(820, 'Plate Russian Twist', 'Primary Obliques Secondary Abdominals', '', 'Neutral', 'Pull', 'Start by sitting on the floor with your knees bent and feet flat on the ground, Engage your core muscles by pulling your belly button toward your spine., Slowly twist your upper body to the right, keeping your feet flat on the ground and your back straight., Hold for a second and then twist to the left, holding for another second.', 'Plate', 'Intermediate'),
(821, 'Pushup to Renegade Row', 'Primary Chest Secondary Lats Tertiary Abdominals', '', 'Neutral', 'Push', 'Start in a high plank position with a pair of dumbbells positioned under your shoulders. Your hands should grip the dumbbells, palms facing each other., Keep your core engaged and your back straight as you row one dumbbell up towards your hip, keeping your elbow close to your body., Lower the dumbbell back down to the starting position and repeat on the other side.', 'Dumbbells', 'Intermediate'),
(822, 'Cobra Pose', 'Primary Abdominals Secondary Quads', '', '', 'Hold', 'Lie on your stomach with your legs extended behind you, feet hip-width apart., Place your palms on the ground next to your shoulders, with your elbows close to your sides., Inhale and press down through your palms, lifting your chest and shoulders off the ground while keeping your elbows close to your sides., Hold the pose for a few breaths, then release and repeat as desired.', 'Yoga', 'Intermediate'),
(823, 'Urdhva Dhanurasana Wheel Pose', 'Primary Abdominals', '', '', 'Hold', 'Lie down on your back with your feet hip-distance apart and your knees bent., Place your hands on the floor beside your head, fingertips pointing towards your shoulders., Push your feet and hands into the floor, lift your hips up and straighten your arms., Lift your chest towards the ceiling and hold the pose for a few breaths before lowering back down.', 'Yoga', 'Beginner'),
(824, 'Machine 45 Degree Back Extension', 'Primary Lower back', '', 'None', 'Pull', 'Position your thighs on the padding and hook your feet on the platform supports., Keeping your back straight, slowly bend at your waist until your legs and back are at a 45Â° angle., Slowly raise your body to the starting position.', 'Machine', 'Intermediate'),
(825, 'Lower back Stretch Variation Four', 'Primary Lower back', '', 'None', '', 'Lay flat against the ground with your knees at an angle and your feet flat against the floor., Grab your legs just below the knees, and pull your legs towards your chest., Pause at the apex of the stretch, then return to the starting position.', 'Stretches', 'Beginner'),
(826, 'Lower back Stretch Variation Three', 'Primary Lower back', '', 'None', '', 'Lay on your back with your arms at either side., Raise your knees so that they are pointing straight towards the ceiling. Keeping your legs pressed together, rotate your hips to either side., Repeat this motion as necessary.', 'Stretches', 'Beginner'),
(827, 'Lower back Stretch Variation Two', 'Primary Lower back', '', 'None', '', 'Lay flat on the floor with your arms at either side., With one leg, hook one foot under the joint of your other leg., Rotate your hips so that your knees are pointing sideways, keeping your hooked leg flat against the floor., Pause at the apex of the stretch and repeat with your other leg.', 'Stretches', 'Beginner'),
(828, 'Lower back Stretch Variation One', 'Primary Lower back', '', 'None', '', 'Sit on the floor with the tops of your feet pointed and flat on the ground. Move your arms out in front of you in the diving position, placing your palms flat against the ground., With your arms fully extended, rest your buttocks on the heels of your feet, you can crawl your hands forward to extend the stretch if need be., Hold the extended position for a few seconds, and return to starting position.', 'Stretches', 'Beginner'),
(829, 'Supermans', 'Primary Lower back', '', 'None', 'Pull', 'Lie face down on the floor with your arms fully extended in front of you., Simultaneously raise your arms, legs and chest off of the floor and hold the position., Slowly lower your arms, legs and chest back to the starting position. Repeat.', 'Bodyweight', 'Beginner'),
(830, 'Plate Deadlift', 'Primary Lower back', '', 'Neutral', 'Pull', 'Stand with feet hip-width apart, the plate in front of the feet, and the knees slightly bent., Bend the hips and knees to grip the plate with palms facing the body., Keeping the back straight, stand up while lifting the plate off the ground., Return the plate back to the starting position by bending the hips and knees, keeping control of the plate.', 'Plate', 'Intermediate'),
(831, 'Dumbbell Superman', 'Primary Lower back', '', 'Neutral', 'Pull', 'Lay flat on the ground on your stomach with your arms extended overhead. Hold a dumbbell with a neutral grip., Lift your legs and chest off the ground simultaneously and hold for a 1 count at the top., Relax your lower back and lower your legs and chest back to the floor.', 'Dumbbells', 'Intermediate'),
(832, 'Barbell Deadlift', 'Primary Lower back Mid back Secondary Glutes', 'https://www.youtube.com/embed/C6aXGfIilEE', 'Overhand Mixed', 'Pull', 'Stand with your mid-foot under the bar and grip the bar with your hands, about a shoulder width apart., Bend your knees, then lift the bar by straightening your back. It is important to keep your back straight., Stand to your full height and hold., Lower the bar to the floor by bending your knees and keeping your back straight.', 'Barbell', 'Advanced'),
(833, 'Dumbbell Row Bilateral', 'Primary Lats Mid back Secondary Biceps Tertiary Lower back', '', '', '', 'Grab both dumbbells and hinge forward at the hips. Make sure you keep a flat back., \"The closer your torso is to parallel with the ground the longer the range of motion will be at your shoulder. The better the results youll get from the exercise.\"\"', 'Dumbbells', 'Intermediate'),
(834, 'Kettlebell Deadlift', 'Primary Hamstrings Mid back Secondary Glutes Tertiary Lower back', '', '', '', 'Stand with your feet shoulder width apart holding the kettlebell with both hands in front of your thighs., Bend forward at the hips bringing the kettlebell to the floor while you slightly bend your knees, keeping your back straight., Return to standing position and repeat.', 'Kettlebells', 'Beginner'),
(835, 'Kettlebell Swing', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Overhand', 'Pull', 'Stand with your feet slightly wider than shoulder width apart holding the kettlebell between your legs, with your knees slightly bent., Keeping your back straight, swing the kettlebell upwards until the kettlebell is level with your chest and carefully lower., Repeat', 'Kettlebells', 'Intermediate'),
(836, 'Kettlebell Staggered Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Stand with your feet shoulder width apart, shifting one foot behind you. Hold the kettlebell in both hands in front of your thighs., Bend forward at the hips bringing the kettlebell to the floor while you slightly bend your knees and keep your back straight., Return to the upright position and repeat.', 'Kettlebells', 'Beginner'),
(837, 'Kettlebell Goblet Good Morning', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Stand with your feet shoulder width apart holding the kettlebell with both hands in front of your chest., Bend forward at the hips keeping your back and knees straight and the kettlebell close to your chest., Return to standing position and thrust your pelvis forward.', 'Kettlebells', 'Beginner'),
(838, 'Good Mornings', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Stand with your feet a little past shoulder width apart with a slight bend at your knee. Place your hands behind the back of your head., Keeping your back straight, rotate your hips to bring your shoulders down towards the floor. Stop when your chest is parallel with the floor.', 'Bodyweight', 'Beginner');
INSERT INTO `workout` (`ID`, `exercise_name`, `target`, `youtubeURL`, `Grips`, `Force`, `steps`, `Category`, `Difficulty`) VALUES
(839, 'Cable Goblet Squat', 'Primary Quads Secondary Glutes Tertiary Lower back', '', 'Neutral', '', 'Start with a straight bar or easy bar attachment ., Get your elbows directly underneath the bar, squat straight up and then walk back a few steps., Break at your knees and hips to initiate the squat. Make sure you maintain a flat back., Squat all the way back to a standing position.', 'Cables', 'Beginner'),
(840, 'Cable Romanian Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', 'https://www.youtube.com/embed/qWb5Zr98B-M', 'Overhand', '', 'Start with a straight bar or ez bar attachment set to the bottom of the machine. Squat the weight up into a standing position and walk a few steps back, \"Push your butt back to initiate the movement and then push your butt forward until youre back in a standing position.\"\"', 'Cables', 'Intermediate'),
(841, 'Cable Single Leg Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Overhand', '', 'Start with a straight bar or easy bar attachment. Squat the weight up into a standing position and walk a few steps back., \"Push your butt back to initiate the movement while lifting one of your legs.\\nThen push your butt forward until youre back in a standing position.\"\"', 'Cables', 'Advanced'),
(842, 'Cable Pull Through', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Neutral', 'Pull', 'This exercise works best with a rope attachment but can be done with anything. The attachment should be set to the bottom of the machine., Face away from the cable machine, straddle the cable itself, and grab the attachment.\\nWalk a few steps away., Break at the hips while maintaining a flat back and mostly extended knees., Push hips forward until you are back in a standing position.', 'Cables', 'Beginner'),
(843, 'Cable Glute Kickback', 'Primary Glutes Lower back', '', '', '', 'Use an ankle attachment. The cable should be set to the bottom of the crossover machine., Push your ankle straight back, extending at the hip. Hold for a one count when you feel a contraction in your glutes., Then flex at the hips until you are back into your original position.', 'Cables', 'Beginner'),
(844, 'Band Good Morning', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Anchor the band at hip level. Take a few steps away until the band is taut., Push your hips back while keeping your back flat until you feel a stretch in your hamstrings.', 'Band', 'Beginner'),
(845, 'Band Pullthrough', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Anchor the band to the lowest point possible. Straddle the band and then grab with both hands., Take a few steps away until the band is taut., Push your butt back until you feel a stretch in your hamstrings. Then extend the hips back into the starting position.', 'Band', 'Beginner'),
(846, 'Band Romanian Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', 'https://www.youtube.com/embed/qWb5Zr98B-M', '', '', 'Stand on your band with both feet., Hinge forward at the hips while keeping a flat back., Hinge until you feel a stretch in your hamstrings., Push your hips back forward until you are in a standing position.', 'Band', 'Intermediate'),
(847, 'Dumbbell Goblet Good Morning', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Neutral', 'Pull', 'Hold the dumbbell in a goblet position. With your elbows directly under your fists., Push your hips back while leaving your knees MOSTLY extended. You should feel a stretch in your hamstrings., \"When you feel the stretch push your hips forward until youre back in a standing position.\"\"\"', 'Dumbbells', 'Advanced'),
(848, 'Dumbbell Romanian Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', 'https://www.youtube.com/embed/qWb5Zr98B-M', 'Overhand Neutral', 'Pull', 'Stand with a shoulder width stance. Push your butt back while leaving your knees MOSTLY extended., You should feel a stretch in your hamstrings. When you feel the stretch, push your hips forward to complete the rep., Do not push your hips all the way forward. This will hyperextend your spine. Just go to a normal standing position.', 'Dumbbells', 'Intermediate'),
(849, 'Barbell High Bar Good Morning', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', '\"Place the bar on your traps. You may need a pad so the bar doesnt dig into your cervical spine.\"\"', 'Barbell', 'Advanced'),
(850, 'Barbell Low Bar Good Morning', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Overhand', 'Pull', 'Place the bar between your traps and rear delts. Pull your shoulder blades back to give the bar a shelf to sit on., \"Push your hips back until you feel a stretch in your hamstrings. This means youre nearing the end of your Range of Motion with out rounding your spine.\"\"', 'Barbell', 'Advanced'),
(851, 'Barbell Sumo Deadlift', 'Primary Glutes Secondary Quads Tertiary Lower back', 'https://www.youtube.com/embed/C6aXGfIilEE', '', '', 'Stance width can vary with a sumo DL. Some prefer toes near the plates. Others prefer a more narrow stance to get more drive off the floor., Make sure your arms are inside your legs. The bar should be just over your mid-foot. Flatten your back and push your heels into the floor., Try to extend your knees and hips simultaneously.', 'Barbell', 'Intermediate'),
(852, 'Barbell Staggered Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Place one foot behind. You should only be on your toes on that back foot. Your front foot should be planted flat the entire time., Bend your back knee and push your hips back. Look for a stretch in your front hamstring., \"When you feel the stretch', 'Barbell', 'Advanced'),
(853, 'Barbell Single Leg Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Start by pushing your hips straight back and extending your non-working leg behind you. It is ok to touch your other foot back to the ground each rep., \"Leave your knee mostly extended. Look for a stretch in your hamstring. When you feel the stretch', 'Barbell', 'Advanced'),
(854, 'Barbell Romanian Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', 'https://www.youtube.com/embed/qWb5Zr98B-M', '', '', 'Take a shoulder width, double overhand or mixed grip., Push your hips back while leaving your knees mostly extended. Look for a stretch in your hamstrings., \"When you feel the stretch', 'Barbell', 'Intermediate'),
(855, 'Barbell Coan Deadlift', 'Primary Quads Secondary Glutes Tertiary Lower back', 'https://www.youtube.com/embed/C6aXGfIilEE', 'Overhand Mixed', '', 'Stand with your feet hip-width apart and place a barbell on the floor in front of you., Bend at the hips and knees to grasp the barbell with an overhand grip, positioning your hands just outside of your hips., Keeping your back straight and your core engaged, lift the barbell off the floor by extending your hips and knees., As you lift the barbell, pull your shoulders back and squeeze your glutes to fully extend your hips at the top of the movement.', 'Barbell', 'Intermediate'),
(856, 'Barbell Pause Box Squat', 'Primary Quads Secondary Glutes Tertiary Lower back', '', 'Overhand', 'Push', 'Stand in front of a box or bench with your feet shoulder-width apart and a barbell resting on your upper back., Lower your body into a squat position, sitting back onto the box and pausing for a moment before standing back up., Push through your heels to return to the standing position.', 'Barbell', 'Advanced'),
(857, 'Barbell Pause Squat', 'Primary Quads Secondary Glutes Tertiary Lower back', '', 'Overhand', 'Push', 'Stand with your feet shoulder-width apart and a barbell resting on your upper back., Lower your body into a squat position, pausing for a moment at the bottom of the movement before standing back up., Push through your heels to return to the standing position.', 'Barbell', 'Intermediate'),
(858, 'Barbell Pronated Pendlay Row', 'Primary Lats Secondary Biceps Tertiary Lower back', '', 'Overhand', 'Pull', 'Start hinged forward with a flat back and the plates resting on the floor. Make sure your elbows are fully extended., Pull the bar to your sternum., Return the plates back to the floor and let the bar come to a full dead stop., Then initiate the next rep.', 'Barbell', 'Advanced'),
(859, 'Barbell Reverse Deadlift', 'Primary Quads Secondary Glutes Tertiary Lower back', '', 'Overhand Underhand', 'Push', 'Start by standing with your feet about hip-width apart, with your toes pointed slightly outward., Place a barbell on the floor behind you, with the weight plates on either side., Bend down and grab the barbell with an overhand grip, your hands slightly wider than shoulder-width apart. Keep your back straight and your shoulders down as you grip the bar., Stand up straight, lifting the barbell off the ground and pulling it toward your hips. Keep your elbows close to your body and your shoulders down as you do this.', 'Barbell', 'Advanced'),
(860, 'Barbell Snatch Grip Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', '', 'Pull', 'Start by standing with your feet about hip-width apart, with your toes pointed slightly outward., Place a barbell on the floor in front of you, with the weight plates on either side. Make sure that the bar is directly in line with your feet., Bend down and grab the barbell with an overhand grip, your hands much wider than shoulder-width apart. Keep your back straight and your shoulders down as you grip the bar., Stand up straight, lifting the barbell off the ground and pulling it toward your hips. Keep your elbows close to your body and your shoulders down as you do this., Slowly lower the barbell back to the ground, making sure to keep your back straight and your shoulders down throughout the movement.', 'Barbell', 'Advanced'),
(861, 'Barbell Snatch Grip Romanian Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', 'https://www.youtube.com/embed/qWb5Zr98B-M', 'Overhand Mixed', 'Pull', '\"Start by standing with your feet about hip-width apart. The bar should be in a rack or box so its lifted off the ground.\"\"', 'Barbell', 'Advanced'),
(862, 'Barbell Suitcase Deadlift', 'Primary Hamstrings Glutes Obliques Secondary Lower back Tertiary Forearms', '', '', 'Pull', 'Begin by standing with your feet hip-width apart and holding a barbell at one side of your body, with your palms facing your thighs., Keeping your core engaged and your back straight, bend at the hips and knees to lower the barbell down towards the ground., As the barbell reaches the ground, use your legs and glutes to push through your heels and extend your hips and knees to raise your body back to the starting position., Repeat the movement for the desired number of reps, then switch sides and repeat the exercise with the barbell on the opposite side of your body.', 'Barbell', 'Advanced'),
(863, 'Barbell Supinated Row', 'Primary Lats Lower back Secondary Biceps', '', 'Underhand', 'Pull', 'Begin by standing in front of a barbell with your feet about hip-width apart., Bend at the hips and knees to grab the barbell with a supinated (underhand) grip, positioning the barbell about an inch away from your shins., Keeping your back straight and your core engaged, use your back muscles to lift the barbell straight up towards your chest., As the barbell reaches your chest, pause briefly, and then lower the barbell back to the starting position.', 'Barbell', 'Advanced'),
(864, 'Barbell Zercher Good Morning', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'None', 'Pull', 'Begin by standing with your feet about hip-width apart and holding a barbell in the crook of your elbows, with your palms facing towards your body., Keeping your core engaged and your back straight, bend at the hips and lower your torso forward until it is nearly parallel to the ground., Push through your heels to extend your hips and raise your body back to the starting position.', 'Barbell', 'Advanced'),
(865, 'Barbell Zercher Squat', 'Primary Quads Secondary Glutes Tertiary Lower back', '', '', 'Push', 'Begin by standing with your feet about hip-width apart and holding a barbell in the crook of your arms, near your belly. Your palms should be facing towards your body., Sit back and lower your body down until your thighs are parallel to the ground, keeping your back straight and your core engaged., Push through your heels to extend your hips and stand back up to the starting position.', 'Barbell', 'Advanced'),
(866, 'Dumbbell Front Rack Pause Squat', 'Primary Quads Lower back Secondary Glutes', '', 'Neutral', 'Push', 'Place two dumbbells on your shoulders and make sure you force your elbows upward., Push your hips back and break at the knees., When you reach parallel (hamstrings parallel with the ground) pause for 1-3 seconds. Then push through your heels back to the starting position.', 'Dumbbells', 'Advanced'),
(867, 'Dumbbell Single Leg Single Arm Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Overhand Neutral', 'Pull', 'Start by standing on one leg with a dumbbell in one hand., Slowly bend at the hips and lower the dumbbell towards the ground while extending your free leg behind you., Keep your core engaged and your back straight as you lower the weight., Once the dumbbell is close to the ground, push through your heel to return to the starting position.', 'Dumbbells', 'Advanced'),
(868, 'Dumbbell Single Leg Stiff Leg Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Overhand Neutral', 'Pull', 'Start by standing on one leg with your feet shoulder-width apart and holding two dumbbells., From this starting position, hinge at the hips to lower the dumbbell towards the ground, keeping your back straight and your core engaged., Once you feel a stretch in your hamstring, slowly return to the starting position., Make sure to keep your standing leg slightly bent and avoid rounding your back as you lower the dumbbell.', 'Dumbbells', 'Intermediate'),
(869, 'Dumbbell Staggered Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Overhand Neutral', 'Pull', 'Start by standing with your feet shoulder-width apart and holding a dumbbell in each hand., Place one foot slightly behind the other, with the heel of your back foot elevated on a step or weight plate., From this starting position, bend at the hips and knees to lower the dumbbells towards the ground, keeping your back straight and your core engaged., Once the dumbbells are below your knees, drive through your heels to stand back up to the starting position.', 'Dumbbells', 'Intermediate'),
(870, 'Dumbbell Sumo Squat', 'Primary Quads Secondary Glutes Tertiary Lower back', '', 'Neutral', 'Push', 'Start by standing with your feet wider than shoulder-width apart and holding a dumbbell in each hand., Place the dumbbells on the ground between your feet., From this starting position, bend your knees and lower your body down into a squat., Make sure to keep your core engaged and your back straight throughout the movement.', 'Dumbbells', 'Beginner'),
(871, 'Dumbbell Swing', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Overhand Neutral', 'Push', 'Start by standing with your feet shoulder-width apart and holding a dumbbell in both hands., Bend your knees slightly and hinge at the hips to swing the dumbbell between your legs., From this starting position, explosively extend your hips and swing the dumbbell up to chest height., Allow the momentum of the swing to carry the dumbbell back down between your legs, and then repeat the movement for the desired number of repetitions.', 'Dumbbells', 'Beginner'),
(872, 'Plate Single Leg Pinch Grip Deadlift', 'Primary Hamstrings Forearms Secondary Glutes Tertiary Lower back', '', 'Pinch Grip', 'Pull', 'Stand with your feet shoulder-width apart and facing forwards. Grip the sides of a weight plate with your palms facing down., Balance on one foot, as you raise the weight keep your arms and back straight, while you bring your other foot back towards the ground, Keep four raised leg straight and in alignment with your back as you lower the plate back down to the starting position.', 'Plate', 'Intermediate'),
(873, 'Plate Good Morning Anterior', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Neutral', '', 'Stand with feet hip-width apart, the plate held in front of you, and the knees slightly bent., Bend forward at the hips, keeping the back straight, until the torso is parallel to the floor., Push through the hips to return to the starting position.', 'Plate', 'Intermediate'),
(874, 'Plate Good Morning', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Neutral', '', 'Stand with feet hip-width apart, the plate held behind the neck and the knees slightly bent., Bend forward at the hips, keeping the back straight, until the torso is parallel to the floor., Push through the hips to return to the starting position.', 'Plate', 'Intermediate'),
(875, 'Plate Single Leg Deadlift', 'Primary Quads Hamstrings Secondary Glutes Tertiary Lower back', '', '', '', 'Engage your core and straighten your legs to lift the plate off the ground., Stand on one leg, keeping the other leg off the ground, while holding the plate in front of your body., Keep your back straight and maintain a neutral head position while looking straight ahead., Lower the plate back down to the ground while keeping your balance on one leg.', 'Plate', 'Advanced'),
(876, 'Plate Pinch Grip Deadlift', 'Primary Lats Forearms Secondary Hamstrings Glutes Tertiary Lower back', '', 'Pinch Grip', '', 'Stand facing the plate with your feet shoulder-width apart and the plate positioned between your feet., Bend down and grasp the plate from the top edges with both hands, making sure your thumbs and fingers are evenly spaced., Keep your back straight, chest up and core engaged, and stand up with the plate, keeping it close to your body., Lower the plate back down to the ground, keeping control and maintaining good posture.', 'Plate', ''),
(877, 'Plate Staggered Deadlift', 'Primary Hamstrings Secondary Glutes Tertiary Lower back', '', 'Neutral', 'Pull', 'Start by standing with your feet shoulder-width apart and holding a weight plate., Place one foot slightly behind the other, with the heel of your back foot elevated., From this starting position, bend at the hips and knees to lower the plate towards the ground, keeping your back straight and your core engaged., Once the plate is below your knees, drive through your heels to stand back up to the starting position.', 'Plate', 'Advanced'),
(878, 'Plate Staggered Pinch Grip Deadlift', 'Primary Hamstrings Forearms Secondary Glutes Tertiary Lower back', '', 'Pinch Grip', 'Pull', 'Start by standing with your feet shoulder-width apart and holding two plates with a pinch grip., Place one foot slightly behind the other, with the heel of your back foot elevated., From this starting position, bend at the hips and knees to lower the plates towards the ground, keeping your back straight and your core engaged.', 'Plate', 'Advanced'),
(879, 'TRX Ab Rollout', 'Primary Abdominals Secondary Lats Tertiary Lower back', '', 'Overhand', 'Pull', 'Grasp the TRX handles with both hands, keeping your arms extended and your shoulders away from your ears., Keep your abs engaged, roll forward onto your toes, and extend your arms in front of you., Slowly lower your body down to the ground, keeping your arms straight and your abs engaged., When your head gets between your arms, pause for a moment and then use your abs to pull your body back up to the starting position.', 'TRX', 'Advanced'),
(880, 'Happy Baby', 'Primary Lower back', '', '', 'Hold', 'Lie down on your back with your knees bent and feet flat on the floor., Lift your feet off the floor and bring your knees towards your chest., Reach inside your knees and hold onto the outside edges of your feet., Gently pull your knees towards your armpits and rock side to side for a few breaths.', 'Yoga', 'Beginner'),
(881, 'Sphinx Pose', 'Primary Lower back Secondary Chest', '', '', 'Hold', 'Start by lying down on your stomach on a yoga mat or a comfortable surface., Place your elbows underneath your shoulders, keeping your forearms parallel to each other., Press your palms and forearms firmly into the ground to lift your chest up, while keeping your hips and legs relaxed., Make sure your shoulders are relaxed and away from your ears. Gently roll your shoulder blades down and back to open up your chest., Lengthen your spine by lifting the crown of your head towards the ceiling, while keeping your chin tucked in slightly.', 'Yoga', 'Beginner'),
(882, 'Kettlebell Incline Shrug', 'Primary Mid back Secondary Traps', '', 'Overhand', 'Pull', 'Leaning across the back section of the bench, with feet firmly on the floor and with arms hanging to the sides., Holding the kettlebell in both hands engage your shoulder blades, as if you are trying to touch them together. Release the shrug.', 'Kettlebells', 'Intermediate'),
(883, 'Traps mid back Stretch Variation Two', 'Primary Mid back', '', 'None', '', 'Sit on the ground and lay one leg flat and the other over the top., Hold your leg with the same side arm and slowly rotate your hips and back., Hold at the peak of the stretch, then slowly return to starting position., Switch sides and repeat.', 'Stretches', 'Beginner'),
(884, 'Traps mid back Stretch Variation One', 'Primary Mid back', '', 'None', '', 'Repeat with your other arm., Stand up with your feet shoulder width apart. Place one arm across the front of your chest at shoulder height, press the forearm of your other arm above the elbow, hooking the stretching arm with your hand., Press the arm until it is straight and rotate your upper torso to engage the stretch even deeper.', 'Stretches', 'Beginner'),
(885, 'Cable Silverback Shrug', 'Primary Mid back Secondary Traps', '', 'Overhand', '', 'Use a bar attachment. Cable should be set all the way to the bottom., Stand up with the attachment in hand, walk a couple of steps back make sure you are standing upright and not leaning backwards., Retract your shoulder blades and then protract to return to the starting position.', 'Cables', 'Beginner'),
(886, 'Dumbbell Silverback Shrug', 'Primary Mid back', '', '', '', 'Hinge forward slightly at the hips. Make sure to keep a flat back. Let your arms hang in front of you., Let your scapula protract, and then retract them. Imagine you have a pencil in your mid-back and squeeze it between your shoulder blades.', 'Dumbbells', 'Intermediate'),
(887, 'Dumbbell Laying Silverback Shrug', 'Primary Mid back Secondary Traps', '', 'Overhand Neutral', 'Pull', 'Lay on an incline bench with your arms hanging freely., Pull your shoulder blades back toward the ceiling.', 'Dumbbells', 'Intermediate'),
(888, 'Dumbbell Silverback Shrug', 'Primary Mid back', '', '', '', 'Hinge forward slightly at the hips. Make sure to keep a flat back. Let your arms hang in front of you., Let your scapula protract, and then retract them. Imagine you have a pencil in your mid-back and squeeze it between your shoulder blades.', 'Dumbbells', 'Intermediate'),
(889, 'Band Horizontal Shrug', 'Primary Traps Shoulders Mid back', '', '', '', 'Anchor the band at shoulder height. Take a few steps away until the band is taut., Retract your shoulder blades straight back in schools. Then protract to the starting position.', 'Band', 'Advanced'),
(890, 'Barbell Landmine Row', 'Primary Lats Mid back Secondary Biceps Tertiary Forearms', '', 'Overhand', 'Pull', 'Position a barbell in a landmine or corner of a room and stand facing it with your feet shoulder-width apart., Grasp the end of the barbell with a neutral grip, keeping your arms straight and your shoulders relaxed., Bend at the hips and knees to lower your torso until it is almost parallel to the ground., Pull the barbell towards your chest, squeezing your shoulder blades together as you row the weight up.', 'Barbell', 'Intermediate'),
(891, 'Barbell Deadlift', 'Primary Lower back Mid back Secondary Glutes', 'https://www.youtube.com/embed/C6aXGfIilEE', 'Overhand Mixed', 'Pull', 'Stand with your mid-foot under the bar and grip the bar with your hands, about a shoulder width apart., Bend your knees, then lift the bar by straightening your back. It is important to keep your back straight., Stand to your full height and hold., Lower the bar to the floor by bending your knees and keeping your back straight.', 'Barbell', 'Advanced'),
(892, 'Machine Seated Cable Row', 'Primary Lats Secondary Biceps Tertiary Mid back', '', 'Overhand Underhand Neutral', 'Pull', 'Sit with your back straight on the machine and grip the handles., Pull the handles back using your arms. Your legs and torso should be at a 90Â° angle. Push out your chest., Pull the handles towards your body until your hands are beside your abdomen.', 'Machine', 'Beginner'),
(893, 'Pull Ups', 'Primary Lats Secondary Mid back Tertiary Biceps', 'https://www.youtube.com/embed/y6x0AIhgjsM', 'Overhand', 'Pull', 'Grasp the bar with an overhand grip, arms and shoulders fully extended., Pull your body up until your chin is above the bar., Lower your body back to starting position.', 'Bodyweight', 'Intermediate'),
(894, 'Dumbbell Row Bilateral', 'Primary Lats Mid back Secondary Biceps Tertiary Lower back', '', '', '', 'Grab both dumbbells and hinge forward at the hips. Make sure you keep a flat back., \"The closer your torso is to parallel with the ground the longer the range of motion will be at your shoulder. The better the results youll get from the exercise.\"\"', 'Dumbbells', 'Intermediate'),
(895, 'Kettlebell Deadlift', 'Primary Hamstrings Mid back Secondary Glutes Tertiary Lower back', '', '', '', 'Stand with your feet shoulder width apart holding the kettlebell with both hands in front of your thighs., Bend forward at the hips bringing the kettlebell to the floor while you slightly bend your knees, keeping your back straight., Return to standing position and repeat.', 'Kettlebells', 'Beginner'),
(896, 'Kettlebell Row', 'Primary Lats Mid back Secondary Biceps Tertiary Forearms', '', '', '', 'Stand with your feet shoulder width apart holding the kettlebell with both hands in front of your thighs., Bend forward at the hips bringing the kettlebell to the floor while you slightly bend your knees, keeping your back straight., Lift the kettlebell upwards towards your chest and lower - repeat.', 'Kettlebells', 'Beginner'),
(897, 'Kettlebell Silverback Shrug', 'Primary Traps Secondary Mid back', '', '', '', 'Stand with your feet shoulder width apart holding the kettlebell with both hands in front of your thighs., Bend forward at the hips bringing the kettlebell to the floor while you slightly bend your knees, keeping your back straight., Holding the kettlebell in both hands engage your shoulder blades, as if you are trying to touch them together. Release the shrug.', 'Kettlebells', 'Intermediate'),
(898, 'Inverted Row', 'Primary Lats Mid back Secondary Biceps Tertiary Forearms', '', 'Overhand Underhand', 'Pull', 'Lay underneath a fixed horizontal bar and grab the bar with a wide overhand grip., Pull your body straight up to the bar., Return to the starting position, your arms fully extended. Repeat.', 'Bodyweight', 'Beginner'),
(899, 'Elevated Pike Press', 'Primary Shoulders Mid back Secondary Traps Tertiary Triceps', '', 'Overhand', 'Push', 'Use a bench or an object to elevate your feet., Lower your head towards the floor by bending your elbows, Push through your hands and return to the starting pike position., Repeat', 'Bodyweight', 'Intermediate'),
(900, 'Band Pull Apart', 'Primary Shoulders Secondary Mid back', '', 'Overhand', 'Pull', 'Grab the band with a shoulder width grip. Point your arms straight in front of you., Retract your shoulder blades and shoulder joint until the band taps your chest.', 'Band', 'Beginner'),
(901, 'Dumbbell Row Unilateral', 'Primary Lats Mid back Secondary Biceps Tertiary Forearms', 'https://www.youtube.com/embed/r4-3p0KgEA8', 'Neutral', 'Pull', 'Brace your off arm against something stable (bench, box). Stagger your stance so your leg on the side of your working arm is back., Try to get your torso to parallel with the ground. That will extend your range of motion., \"Let your arm hang freely and then pull your elbow back. Imagine youve got a tennis ball in your armpit and squeeze it each rep.\"\"\"', 'Dumbbells', 'Beginner'),
(902, 'Dumbbell Rear Delt Row', 'Primary Shoulders Secondary Mid back', '', '', '', 'Hinge forward at the hips while maintaining a flat back. Try to get your torso as close to parallel with the ground as your mobility will allow for., Let your arms hang in front of you. Pull your elbows back towards the ceiling while flaring your elbows outward.', 'Dumbbells', 'Advanced'),
(903, 'Band High Face Pull', 'Primary Shoulders Secondary Mid back', '', 'Overhand', 'Pull', 'Start with a band anchored to a high point. Stagger your stance., Pull the band to the bridge of your nose.', 'Band', 'Beginner'),
(904, 'Band Rear Delt Fly', 'Primary Shoulders Secondary Mid back', '', 'Neutral', 'Pull', 'Start by standing between the two anchored bands with your feet shoulder-width apart and the bands positioned at a high point., Grasp the ends of each band with your hands, keeping your palms facing each other., Keeping your arms slightly bent and your back straight, slowly pull the bands away from your body until your arms are extended straight out to the sides. Hold for a second, then slowly release back to the starting position. Repeat for desired reps.', 'Band', 'Intermediate'),
(905, 'Plate Internally Rotated Rear Delt Fly', 'Primary Shoulders Mid back Secondary Traps', '', 'Overhand', 'Pull', 'With plates in either hand, bend your knees with your feet slightly bowed out. Hinge forward with a flat back., Maintain an overhand grip and internally rotated shoulders., Raise your arms backward toward the ceiling while maintaining a light band in your elbows.', 'Plate', 'Intermediate'),
(906, 'Plate Rear Delt Fly', 'Primary Shoulders Secondary Mid back', '', 'Neutral', 'Pull', 'Keep your back straight and bend forward slightly at the waist., With arms extended, raise the weights out to your sides until they are in line with your shoulders., Pause briefly and then lower the weights back to the starting position.', 'Plate', 'Beginner'),
(907, 'TRX Rear Delt Fly', 'Primary Shoulders Secondary Mid back', '', 'Overhand Neutral', 'Pull', 'Stand facing away from the TRX straps and grab the handles with both hands., Step forward so that your body is in a plank position, with your arms straight and your body in a straight line from your head to your heels., Keeping your arms straight, slowly lift your arms out to the side, keeping them at the same height as your shoulders. Your body should form a \"T\"\" shape.\"', 'TRX', 'Intermediate'),
(908, 'TRX Rear Delt Row', 'Primary Shoulders Secondary Mid back', '', 'Overhand', 'Pull', 'Stand facing away from the TRX straps and grab the handles with both hands., Step away so your body is leaning away from the anchor point., Flex your elbows and pull your elbows straight back behind your body., Make sure to keep your elbows flared outward.', 'TRX', 'Beginner'),
(909, 'TRX W Raise', 'Primary Shoulders Secondary Mid back', '', 'Overhand', 'Pull', 'Hold the TRX straps and lean away from the anchor point., Pull your elbows back while externally rotating your shoulder joint. Your arms should make a W shape at the back portion of the exercise.', 'TRX', 'Beginner'),
(910, 'Cat Pose', 'Primary Lower back Secondary Mid back', '', '', 'Hold', 'Begin on your hands and knees with your wrists directly under your shoulders and your knees directly under your hips., Inhale and arch your back, lifting your tailbone and head towards the ceiling, while lowering your belly towards the floor., Exhale and round your spine, tucking your chin towards your chest, and bringing your tailbone towards your knees., Repeat these movements, flowing smoothly between the two poses, for several breaths.', 'Yoga', 'Beginner'),
(911, 'Easy Seated Twist Pose', 'Primary Obliques Secondary Lower back', '', '', 'Hold', 'Begin seated with your legs extended in front of you., Begin seated with your legs extended in front of you., Bend your right knee and place your foot on the floor next to your left thigh., Bend your right knee and place your foot on the floor next to your left thigh., Inhale and reach your left arm up towards the ceiling., Inhale and reach your left arm up towards the ceiling., Exhale and twist to the right, placing your left elbow on the outside of your right knee. Hold for a few breaths and then repeat on the other side., Exhale and twist to the right, placing your left elbow on the outside of your right knee. Hold for a few breaths and then repeat on the other side.', 'Yoga', 'Beginner'),
(912, 'Revolved Side Angle Pose With Prayer Hands', 'Primary Mid back Secondary Obliques', '', '', 'Hold', 'Begin in Warrior II pose, with your right foot forward and your left foot back., Bring your hands together in a prayer position at your heart., Exhale and twist your torso to the right, placing your left elbow on the outside of your right knee., Hold for several breaths and then repeat on the other side.', 'Yoga', 'Intermediate'),
(913, 'Revolved Side Angle Pose With Prayer Hands Beginner', 'Primary Mid back Secondary Obliques', '', '', 'Hold', 'Begin in a low lunge position with your right foot forward and your left knee on the ground., Bring your hands together in a prayer position at your heart., Exhale and twist your torso to the right, placing your left elbow on the outside of your right knee., Hold for several breaths and then repeat on the other side.', 'Yoga', 'Beginner'),
(914, 'Seated Twist', 'Primary Mid back Secondary Obliques', '', '', 'Hold', 'Begin seated with your legs extended in front of you., Bend your right knee and place your foot on the floor next to your left thigh., Inhale and reach your left arm up towards the ceiling., Exhale and twist to the right, placing your left elbow on the outside of your right knee. Hold for a few breaths and then repeat on the other side.', 'Yoga', 'Beginner'),
(915, 'Barbell Landmine Side Bend', 'Primary Obliques', '', 'Neutral', 'Pull', 'Stand straight and hold the end of the Barbell, Keep your hips and shoulders facing forward, bend to one side slowly, Return to the upright position., Switch hands and repeat.', 'Barbell', 'Intermediate'),
(916, 'Dumbbell Wood Chopper', 'Primary Obliques', '', 'Neutral', 'Pull', 'Stand with a shoulder width stance. Begin with the dumbbell near one of your quads., Rotate at your upper spine to engage the obliques., You can pivot your back foot to keep from generating too much torque at the knee.', 'Dumbbells', 'Advanced'),
(917, 'Kettlebell Wood Chopper', 'Primary Obliques', '', 'Neutral', 'Pull', 'Stand with feet slightly wider than shoulder width apart and hold the kettle bell with both hands against your hip., Keeping your arms straight, swing the kettlebell upwards and across your body towards your shoulder, raising the kettlebell to head height rotating your hips as you do so., Return to the starting position and repeat.', 'Kettlebells', 'Beginner'),
(918, 'Cable Wood Chopper', 'Primary Obliques', '', 'Neutral', 'Pull', 'Set the cable at about nipple level. Use a handle attachment., Face sideways, then rotate your torso with your arms extended against the resistance., After completing your given number of reps, face the opposite direction and repeat.', 'Cables', 'Intermediate'),
(919, 'Band Wood Chopper', 'Primary Obliques', '', '', '', '', 'Band', 'Advanced'),
(920, 'Dumbbell Russian Twist', 'Primary Obliques Secondary Abdominals', '', 'Neutral', 'Pull', 'Sit on the floor and flex your knees and hips to a 90 degree angle., \"Your feet should be hovering off the ground. (If thats too hard start with heels on the floor)\"\"', 'Dumbbells', 'Intermediate'),
(921, 'Kettlebell Russian Twist', 'Primary Obliques Secondary Abdominals', '', '', '', 'Sit on the floor with your legs bent, knees together and feet raised slightly off the floor., Starting on one side and sitting up straight with your core muscles engaged, lift the kettlebell off the floor with both hands and bring it across your body to the other side, rotating your chest towards the floor as you do so., Tap the kettlebell lightly on the floor and bring the kettlebell back across your body to the starting position, rotating your chest towards the floor., Repeat, keeping the legs bent and feet raised throughout the exercise.', 'Kettlebells', 'Beginner'),
(922, 'Band Pallof Press', 'Primary Obliques', 'https://www.youtube.com/embed/Te5VAYXy0wQ', 'Neutral', 'Push', 'Place the band at an anchor point that is shoulder height. Take a few steps away until the band is taut., Start with your fingers interlocked around the band and your fists connected to your chest., Extend at the elbows pressing the band away from your body until your elbows are fully extended.', 'Band', 'Beginner'),
(923, 'Kettlebell Windmill', 'Primary Obliques Secondary Abdominals', '', 'Overhand Neutral', 'Push', 'Stand with feet slightly wider than shoulder width apart. Hold the kettlebell in one hand and extend your arm above your head., Keeping your legs straight and the kettlebell above your head, lower the relaxed arm towards the ground, twisting your body towards the side of the raised arm., Return to the starting potion, tilt your pelvis forward and repeat.', 'Kettlebells', 'Advanced'),
(924, 'Cable Pallof Press', 'Primary Obliques', 'https://www.youtube.com/embed/Te5VAYXy0wQ', 'Neutral', 'Push', 'Use a handle attachment, set the cable at nipple level, walk a few steps away, and face sideways., Start with both hands on the handle and your fists against your chest., Press the handle forward until your elbows are fully extended, then pull the handle back to your chest.', 'Cables', 'Beginner'),
(925, 'Abdominals Stretch Variation Three', 'Primary Abdominals Obliques', '', 'None', '', 'Reach up with your left arm and then lean slowly to the right. Lean until a stretch is felt in the obliques., Stand upright., After completing the desired amount of reps with the left arm, switch to the right arm and lean to the left.', 'Stretches', 'Beginner'),
(926, 'Abdominals Stretch Variation Two', 'Primary Abdominals Obliques', '', 'None', '', 'Stand upright., Reach with both hands upwards until you can interlock your fingers., With your hands above your head, lean back until a stretch is felt in the abdominals.', 'Stretches', 'Beginner'),
(927, 'Bodyweight Russian Twist', 'Primary Obliques Secondary Abdominals', '', 'None', 'Pull', 'Hover your heels just off the ground., Stick your chest out and maintain a flat back. Make all the movement happen at your mid and upper spine., Point your chest to the left, then rotate at the spine until your chest is pointed right.', 'Bodyweight', 'Intermediate'),
(928, 'Side Plank Up Down', 'Primary Obliques', 'https://www.youtube.com/embed/7Zat7RFY52Y', 'None', 'Pull', 'Start with your elbow and hip on the ground. Either neutral fist or flat hand on the ground., Begin in a side plank position, and then drop your hips to the floor., Pull your hips back to the ceiling to finish the rep.', 'Bodyweight', 'Intermediate'),
(929, 'Side Plank Reach Through', 'Primary Obliques', 'https://www.youtube.com/embed/7Zat7RFY52Y', 'None', 'Pull', 'Begin in an elbow side plank position., Reach for the ceiling with your off hand., Rotate at the mid and upper spine and reach through the open space between the floor and your torso., Reach backwards, and then rotate back into the starting position.', 'Bodyweight', 'Advanced'),
(930, 'Elbow Side Plank', 'Primary Obliques', 'https://www.youtube.com/embed/7Zat7RFY52Y', 'None', 'Hold', 'Press your elbow into the floor., Pick your hips up off the ground and maintain this position for the allotted amount of time., \"Make sure you dont let your chest fall forward or backward.\"\"\"', 'Bodyweight', 'Beginner'),
(931, 'Hand Side Plank', 'Primary Obliques', 'https://www.youtube.com/embed/7Zat7RFY52Y', 'None', 'Hold', 'Press your hand into the ground and pick up your hip off the ground., Open your chest and maintain this position for the allotted amount of time.', 'Bodyweight', 'Intermediate'),
(932, 'Barbell Suitcase Deadlift', 'Primary Hamstrings Glutes Obliques Secondary Lower back Tertiary Forearms', '', '', 'Pull', 'Begin by standing with your feet hip-width apart and holding a barbell at one side of your body, with your palms facing your thighs., Keeping your core engaged and your back straight, bend at the hips and knees to lower the barbell down towards the ground., As the barbell reaches the ground, use your legs and glutes to push through your heels and extend your hips and knees to raise your body back to the starting position., Repeat the movement for the desired number of reps, then switch sides and repeat the exercise with the barbell on the opposite side of your body.', 'Barbell', 'Advanced'),
(933, 'Dumbbell Half Kneeling Wood Chopper', 'Primary Obliques Secondary Abdominals', '', 'Neutral', 'Pull', 'Take a half kneeling position. Start with the dumbbell by your knee on the side of the kneeling leg., Rotate at the upper spine, holding the dumbbell away from your body with nearly extended elbows.', 'Dumbbells', 'Intermediate'),
(934, 'Dumbbell Kneeling Wood Chopper', 'Primary Obliques', '', 'Neutral', 'Pull', 'Start in a kneeling position with your shoulder blades pulled back., The dumbbell should begin by one of your hips., Rotate at the spine and bring the dumbbell from your hip, 180 degrees around your body. The dumbbell should land at eye level.', 'Dumbbells', 'Beginner'),
(935, 'Dumbbell Overhead Side Bend', 'Primary Obliques', '', 'Overhand Neutral', 'Pull', 'Start by standing with your feet shoulder-width apart and holding a dumbbell overhead., From this starting position, bend to the side as far as you can while keeping your feet planted and your core engaged.', 'Dumbbells', 'Advanced'),
(936, 'Dumbbell Side Plank Up Down', 'Primary Obliques', 'https://www.youtube.com/embed/7Zat7RFY52Y', 'Neutral', 'Push', 'Start by lying on your side with your feet stacked on top of each other and a dumbbell on your hip., Place your bottom elbow on the ground beneath your shoulder and engage your core to raise your hips off the ground., Your hips will do all the moving. Think, \"hips up', 'Dumbbells', 'Beginner'),
(937, 'Dumbbell Hand Side Plank', 'Primary Obliques', 'https://www.youtube.com/embed/7Zat7RFY52Y', 'Overhand Neutral', 'Hold', 'Start by lying on your side with your feet stacked on top of each other and a dumbbell on your hip., Place your bottom hand on the ground beneath your shoulder and engage your core to raise your hips off the ground., From this starting position, hold the side plank position for the desired amount of time, making sure to keep your body straight and your hips level.', 'Dumbbells', 'Intermediate'),
(938, 'Dumbbell Elbow Side Plank', 'Primary Obliques', '', 'None', 'Hold', '', 'Dumbbells', 'Beginner'),
(939, 'Plate Russian Twist', 'Primary Obliques Secondary Abdominals', '', 'Neutral', 'Pull', 'Start by sitting on the floor with your knees bent and feet flat on the ground, Engage your core muscles by pulling your belly button toward your spine., Slowly twist your upper body to the right, keeping your feet flat on the ground and your back straight., Hold for a second and then twist to the left, holding for another second.', 'Plate', 'Intermediate'),
(940, 'Plate Standing Twist', 'Primary Obliques', '', 'Neutral', 'Pull', 'Stand with a plate extended away from your body. Elbows mostly extended., Rotate at the upper spine while keeping your lumbar spine in a neutral position., Do not let the plate go behind your body.', 'Plate', 'Beginner'),
(941, 'Plate Wood Chopper', 'Primary Obliques', '', 'Neutral', 'Pull', 'Stand with a shoulder width stance. Begin with the plate near one of your quads., Rotate at your upper spine to engage the obliques., You can pivot your back foot to keep from generating too much torque at the knee.', 'Plate', 'Beginner'),
(942, 'Dumbbell Long Lever Russian Twist', 'Primary Obliques', '', 'Neutral', 'Pull', 'Start by sitting on the floor with your knees bent and feet flat on the ground. Hold a dumbbell in both hands with your arms extended straight in front of you., Twist your torso to the right, bringing the dumbbell towards the ground, then return to the center. Repeat on the left side, twisting to the left and bringing the dumbbell towards the ground., Continue this twisting motion, alternating sides, for the desired number of repetitions. Keep your core engaged and avoid swinging your arms to generate momentum.', 'Dumbbells', 'Advanced'),
(943, 'Dumbbell Side Bend', 'Primary Obliques', '', 'Neutral', 'Pull', 'Stand with feet hip-width apart and place your right hand on the right hip., Hold a dumbbell with the left hand. Bend at the hips to the side., \"Make sure you dont start to lean forward to try and force a longer range of motion.\"\"\"', 'Dumbbells', 'Beginner'),
(944, 'TRX Forearm Side Plank', 'Primary Obliques', '', 'Neutral None', 'Hold', 'Lie on your side with your legs straight and your feet resting in the TRX foot cradles., Prop yourself up on your forearm and engage your core to maintain a straight line from your head to your feet.', 'TRX', 'Intermediate'),
(945, 'TRX Oblique Crunch', 'Primary Obliques', '', 'None', 'Pull', 'Place your feet in the TRX stirrups and get in to a hand plank position., Pull both knees to your right elbow., Extend your legs back to the starting position, then pull both knees to the left elbow.', 'TRX', 'Intermediate'),
(946, 'Dumbbell Offset Squat', 'Primary Quads Secondary Glutes Tertiary Obliques', '', 'Neutral', 'Push', 'Hold a dumbbell over one shoulder., Initiate the squat by pushing your hips back., Avoid doing any twisting. Try to keep your shoulders even.', 'Dumbbells', 'Intermediate'),
(947, 'Plate Reverse Lunge Twist', 'Primary Quads Triceps Secondary Glutes Obliques', '', '', '', 'Step back with your right foot into a lunge position, both knees bent at 90 degrees., Twist your torso to the right, keeping the plate close to your chest., Push through your front foot to return to the starting position and repeat on the other side.', 'Plate', 'Intermediate'),
(948, 'Plate Pinch Grip Side Bend', 'Primary Forearms Secondary Obliques', '', 'Pinch Grip', '', 'Stand straight with the plate held at your side, Keep your hips and shoulders facing forward, bend to one side slowly, Return to the upright position., Switch hands and repeat', 'Plate', 'Beginner'),
(949, 'Gate Pose Variation Arm Extended on Side', 'Primary Obliques Secondary Lats', '', '', 'Hold', 'Start on your knees and extend your right leg out to the side., Raise your right arm above your head and reach towards the left side of the room., Gently bend your body to the left side and rest your left hand on your left thigh., Look up towards your right hand and hold the pose for a few breaths before switching sides.', 'Yoga', 'Intermediate'),
(950, 'Revolved Head To Knee', 'Primary Obliques Secondary Hamstrings', '', '', 'Hold', 'Sit on the floor with your legs extended in front of you., Bend your right knee and bring your heel towards your perineum., Place your left hand on the outside of your right knee and twist your body to the right., Reach your right hand towards your left foot and look up towards the ceiling. Hold the pose for a few breaths before switching sides.', 'Yoga', 'Advanced'),
(951, 'Seated Side Bend Pose', 'Primary Obliques', '', '', 'Hold', 'Sit comfortably on the floor with your legs crossed., Inhale and raise your right arm above your head., As you exhale, bend your body to the left side and rest your left hand on the ground., Look up towards the ceiling and hold the pose for a few breaths. Then switch sides and repeat the pose on the other side.', 'Yoga', 'Beginner'),
(952, 'Supine Twist Lying', 'Primary Obliques Secondary Lower back', '', '', 'Hold', 'Lie down on your back with your arms stretched out to your sides., Bend your right knee and bring it towards your chest., Slowly twist your body to the left side, bringing your right knee across your body towards the ground on the left side., Look towards your right hand and hold the pose for a few breaths before switching sides.', 'Yoga', 'Beginner'),
(953, 'Wild Thing Pose', 'Primary Obliques', '', '', 'Hold', 'Start in a downward facing dog position., Shift your weight onto your left hand and foot, and lift your right leg towards the ceiling., Slowly bend your right knee and shift your weight onto your right hand as you open up your chest towards the ceiling., Look towards your right hand and hold the pose for a few breaths before switching sides.', 'Yoga', 'Intermediate');
